INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CB98045361728498205E65294B56D77', X'E2B9E7673E4E0543A3EE4359928F146A', X'E2B9E7673E4E0543A3EE4359928F146A', 2, NULL, NULL, 2, NULL, 0, 'Wash the squash and then trim and discard the ends. Quarter the squash lengthwise and then cut them into 1/2-inch pieces.');
