INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A4D6C09744FDA4CAC741CB039B8874E', X'E2B9E7673E4E0543A3EE4359928F146A', X'E2B9E7673E4E0543A3EE4359928F146A', 5, NULL, NULL, 2, NULL, 0, 'Cut the butter into small pieces. Dot the casserole with the butter and then sprinkle with the breadcrumbs and Parmesan cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD00B69D848C754F9FD0E6B56049D32A', X'E2B9E7673E4E0543A3EE4359928F146A', X'E2B9E7673E4E0543A3EE4359928F146A', 6, NULL, NULL, 2, NULL, 0, 'Cover the baking dish tightly with foil and bake for 35 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60080F3BAE9F734F997C4CF631F56BEC', X'E2B9E7673E4E0543A3EE4359928F146A', X'E2B9E7673E4E0543A3EE4359928F146A', 7, NULL, NULL, 2, NULL, 0, 'Uncover and bake for about 15 to 20 minutes longer, until the topping has browned and the squash is fork-tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD5940D51395B245B150F09BD69F6FBE', X'8CAE1506939E9546B9B26B6C5AA1D116', X'8CAE1506939E9546B9B26B6C5AA1D116', 0, NULL, NULL, 2, NULL, 0, 'Toss prepared vegetables together in a glass or ceramic bowl (non-reactive container).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'620A9166809C3843979AD92A61417EDF', X'8CAE1506939E9546B9B26B6C5AA1D116', X'8CAE1506939E9546B9B26B6C5AA1D116', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl with the lemon juice, drizzle olive oil while whisking, to emulsify the liquids. Add to vegetables; cover and refrigerate for an hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9FCC796DF42EB409732CCE4F4042257', X'8CAE1506939E9546B9B26B6C5AA1D116', X'8CAE1506939E9546B9B26B6C5AA1D116', 2, NULL, NULL, 2, NULL, 0, 'Add cooked, cooled rice; add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'551BE5D07988004E87AE7260037421A4', X'8CAE1506939E9546B9B26B6C5AA1D116', X'8CAE1506939E9546B9B26B6C5AA1D116', 3, NULL, NULL, 2, NULL, 0, 'To toast almonds, put them in a dry skillet over medium heat. Cook, stirring and turning constantly, just until lightly browned and aromatic.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEBB507E2F4C8C47A68C8BE877E536D1', X'8CAE1506939E9546B9B26B6C5AA1D116', X'8CAE1506939E9546B9B26B6C5AA1D116', 4, NULL, NULL, 2, NULL, 0, 'Top with toasted almonds.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDDDE844489DE64CB98AF95326E19948', X'8CAE1506939E9546B9B26B6C5AA1D116', X'8CAE1506939E9546B9B26B6C5AA1D116', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate until serving time.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A286216A8FF4A44EAA3E584A3A89E4E5', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Lightly grease a 1 1/2-quart baking dish or spray it with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16CD13B2F4788341899BD07A3AA82FA0', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 1, NULL, NULL, 2, NULL, 0, 'Trim the green beans; place them in a strainer or colander and rinse with cold water.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7B73DE0339C7F49BC5FCA02788D5F9B', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 2, NULL, NULL, 2, NULL, 0, 'Set the Instant Pot to sauté (normal level). Add the shallots and garlic and cook for about 2 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C68E46E191047469D5B8B701F92DDFE', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 3, NULL, NULL, 2, NULL, 0, 'Add the green beans to the pot along with the chicken stock, salt, and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63A453C031E098428A6AA96D3749BF95', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 4, NULL, NULL, 2, NULL, 0, 'Place the lid on the pot; lock it in place and set the vent to `sealing.` Choose the manual setting, high pressure, and set the time for 4 minutes. When the time is up, do a quick release.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2612CB0E105F874987AAEAB6708452F8', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 5, NULL, NULL, 2, NULL, 0, 'Strain the liquids into a bowl and return the solids to the pot. Add the cream of mushroom soup to the green beans and stir gently to combine. Reserve about 3 to 4 tablespoons of cheese for the topping and add the remaining cheese to the green beans. Stir the mixture, adding some of the reserved liquids to thin the sauce, if desired. Alternatively, for a richer sauce, add some light or heavy cream or a few tablespoons of sour cream. Taste and adjust the seasonings. Transfer the green bean mixture to the prepared baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AA946D1B1FC4C4FABAE3F8DC50DAB57', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 6, NULL, NULL, 2, NULL, 0, 'Combine the French fried onions in a bowl with the panko breadcrumbs; toss with the 2 tablespoons of melted butter. Sprinkle the reserved shredded cheese over the green bean casserole filling and then sprinkle the buttered breadcrumbs and fried onions evenly over the cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9BC8B15B4A5AE449A9995EBE1369E0E', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 7, NULL, NULL, 2, NULL, 0, 'Bake the casserole in the preheated oven for about 15 to 20 minutes, or until the topping is golden brown and the casserole is hot. Transfer the casserole to a serving dish or serve it straight from the baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDC195FBD07E0C47B412E06B0746A240', X'68E954B432BC724384D7F2770F86BF75', X'68E954B432BC724384D7F2770F86BF75', 8, NULL, NULL, 2, NULL, 0, 'Enjoy this green bean casserole with an everyday family meal or holiday feast.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DC2BB106E7AAC44B361D2AB99BECDC4', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E3250D4AAC77B44837144E73ADB88D9', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 1, NULL, NULL, 2, NULL, 0, 'Cook the sliced squash and chopped onion in boiling salt water for 5 to 10 minutes. Drain well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98C78FE68F751747AB22AB28D91DFE5A', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the soup and sour cream. Stir in the shredded carrot and mix thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'512818C4C29591499DA7C5F179322D3A', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 3, NULL, NULL, 2, NULL, 0, 'Fold in the well-drained squash and onion.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19CBD944013E744B717A8BD7378B2BE', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 4, NULL, NULL, 2, NULL, 0, 'In a microwave-safe bowl, melt the butter, remove from microwave, and toss with the stuffing mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E2B1C81CF68048B19A198712D13543', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 5, NULL, NULL, 2, NULL, 0, 'Spread half of the stuffing in a 12 x 8 x 2-inch baking dish (about 1 1/2- to 2-quart size). Spoon vegetable mixture on top. Sprinkle with remaining stuffing mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBA507BBE58EEC44851941D9E4A73003', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 6, NULL, NULL, 2, NULL, 0, 'Bake at 350 F until heated through, about 25 to 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'592BFDFE7D34F84B9A0E671119620645', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 7, NULL, NULL, 2, NULL, 0, 'Remove it from the oven and serve the casserole hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4032113793D4AC4083A9D3F547EA62A9', X'04CEDD15E236674595AF24371455F653', X'04CEDD15E236674595AF24371455F653', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DF3F7FBCAFE9041972812C20CA7E610', X'EBD9487679C02D4E857E1569264838F2', X'EBD9487679C02D4E857E1569264838F2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13DD68986272004298BD15CFDA712733', X'EBD9487679C02D4E857E1569264838F2', X'EBD9487679C02D4E857E1569264838F2', 1, NULL, NULL, 2, NULL, 0, 'Remove the stems from the mushrooms and discard them. Wipe the mushroom caps with damp paper towels and give them a quick rinse under cold running water. Pat dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0E0E985E397F646A9D51EBA5C1DED60', X'EBD9487679C02D4E857E1569264838F2', X'EBD9487679C02D4E857E1569264838F2', 2, NULL, NULL, 2, NULL, 0, 'Heat a covered grill to medium heat, or about 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D80FB945393FFD428EDAB8DC49CFB12D', X'EBD9487679C02D4E857E1569264838F2', X'EBD9487679C02D4E857E1569264838F2', 3, NULL, NULL, 2, NULL, 0, 'Combine the butter, olive oil, lemon juice, garlic, and freshly ground black pepper in a small saucepan. Place the saucepan over medium-low heaton the stovetop or on the grilland cook until the mixture is simmering and the garlic is aromatic. Remove the sauce from the heat and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D6D51F712A234C9D17DCCBF1F3F7DC', X'EBD9487679C02D4E857E1569264838F2', X'EBD9487679C02D4E857E1569264838F2', 4, NULL, NULL, 2, NULL, 0, 'If some of the mushrooms are very large, cut them in half or slice them into thick strips. Place the mushrooms on a plate or tray and brush them generously with the melted butter and garlic mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0B9781D3150524F88674B0119AD123F', X'EBD9487679C02D4E857E1569264838F2', X'EBD9487679C02D4E857E1569264838F2', 5, NULL, NULL, 2, NULL, 0, 'On a covered grill over medium coals, grill the portobello mushrooms stem-side down for about 5 to 7 minutes, or until they are browned.');
