INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D50299BB93D8604AB7FED3CB78C34851', X'022F3DD44EEC974EA92ACC2A12C33F65', X'022F3DD44EEC974EA92ACC2A12C33F65', 8, NULL, NULL, 2, NULL, 0, 'Pour the milk over the squash and then sprinkle with the cheese and chopped pecans or bread crumbs.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'995E45FDCB24CD42B0AC364D47C6A404', X'022F3DD44EEC974EA92ACC2A12C33F65', X'022F3DD44EEC974EA92ACC2A12C33F65', 9, NULL, NULL, 2, NULL, 0, 'Bake uncovered in the preheated oven for 20 minutes, until the milk is absorbed and the squash casserole is bubbly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBD5C36B8041C64895799C91DF3DFE5D', X'022F3DD44EEC974EA92ACC2A12C33F65', X'022F3DD44EEC974EA92ACC2A12C33F65', 10, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B6CA915649C8A43891D3E2F4C0FF620', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE4B2C6AAD2EB54F8777052F2476109B', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F/180 C/Gas Mark 4. Grease and flour two 8 1/2-by-4 1/2-inch loaf pans.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283A86B77528CD4FA589A735802E75BD', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 2, NULL, NULL, 2, NULL, 0, 'Wash, dry, and, shred the zucchini by hand using a box or Microplane grater or use the shredding disk of a food processor.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48E8BF25A9748E41A6FE2302383660C0', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 3, NULL, NULL, 2, NULL, 0, 'Put the zucchini in a mesh strainer or colander and press or squeeze it with your hands to get some of the excess liquid out. You should have approximately 2 cups of shredded zucchini.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2D3F7CE8A73D4A8571D8D7FC7EEF6B', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 4, NULL, NULL, 2, NULL, 0, 'In a mixing bowl with an electric mixer, beat the eggs, granulated sugar, and vegetable oil together.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'602D3CCCD804644482BBFA9C7C8A61E4', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 5, NULL, NULL, 2, NULL, 0, 'Stir in the flour, baking powder, baking soda, cinnamon, salt, vanilla, and walnuts, mixing just until all ingredients are combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'802EC168922535449F4C13DF6E8867EC', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 6, NULL, NULL, 2, NULL, 0, 'Add the drained zucchini. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FF0230098B95C47B26AC611C8F146BF', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 7, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared loaf pans.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'686AE05FCB66A8419C2EBB2DE007BBCA', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 8, NULL, NULL, 2, NULL, 0, 'Bake the zucchini bread for 55 to 60 minutes or until a wooden pick inserted in the center of a loaf comes out with little or no crumbs clinging to it.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF6165E37CDFAC4CB16DA92D949D70C3', X'953BC3173CAEC248A89F463CD4A866CD', X'953BC3173CAEC248A89F463CD4A866CD', 9, NULL, NULL, 2, NULL, 0, 'Cool, slice, and serve!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D9E210733CCDE4FB4472CECD1A24DB9', X'7099EB152FC3094D8C0D5140841D2A9D', X'7099EB152FC3094D8C0D5140841D2A9D', 0, NULL, NULL, 2, NULL, 0, 'Place zucchini or summer squash and onions in a large non-reactive pot; add the salt and enough water to cover. Let stand for 2 hours. Pour the mixture into a colander; rinse well with cold water and drain thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7537365381AD644F888F3CD4CD88885E', X'7099EB152FC3094D8C0D5140841D2A9D', X'7099EB152FC3094D8C0D5140841D2A9D', 1, NULL, NULL, 2, NULL, 0, 'In a 2-quart saucepan, bring the vinegar to a boil; pour over the squash and onions. Let stand for 2 hours. Bring to a boil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18221B1478B33041AEB0513CE221E582', X'7099EB152FC3094D8C0D5140841D2A9D', X'7099EB152FC3094D8C0D5140841D2A9D', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the jars and lids. Wash the jars and lids in hot, soapy water. Fill a large canner with water. Place a rack in the pan and add the jars. Bring to a gentle boil. Turn the heat down to low to keep the jars hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBB407D1E9D238429C205D761C3A79A7', X'7099EB152FC3094D8C0D5140841D2A9D', X'7099EB152FC3094D8C0D5140841D2A9D', 3, NULL, NULL, 2, NULL, 0, 'Put the lids in a saucepan and cover with water. Bring to a simmer; do not boil. Keep them hot in the barely simmering water until it''s time to seal the jars.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1076B6B54D045A46966BEB945F237041', X'7099EB152FC3094D8C0D5140841D2A9D', X'7099EB152FC3094D8C0D5140841D2A9D', 4, NULL, NULL, 2, NULL, 0, 'Boil the squash for 5 minutes; pack the squash pickles into the hot sterilized jars leaving 1/2-inch headspace. Wipe the edges of the jar with a clean, damp paper towel and seal with the lid and screw-on bands. Do not overtighten. Process pint or quart jars in the boiling water bath for 10 minutes from 1 to 1,000 feet, 15 minutes from 1,001 to 6,000 feet, or 20 minutes above 6,000 feet in altitude. The water should be at least 1 inch above the tops of the jars. Add extra boiling water, if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'378103626C6E0D4880D130B3204F2E31', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF6DECC0F4BC1244A122D15DAA2859B7', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04B724CF6B965A4DBC8EDFEE9AACE655', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 2, NULL, NULL, 2, NULL, 0, 'Butter a 2-quart casserole or baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C31A5E4BC3921A4ABD041A4F57633A25', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the mashed sweet potatoes with 1 cup of the brown sugar, eggs, vanilla extract, milk, and 1/2 cup of melted butter. Blend thoroughly and spoon the mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67ABF4D06EA8941A128C14C7387264B', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 4, NULL, NULL, 2, NULL, 0, 'In another bowl, combine the remaining 1/2 cup of brown sugar, the flour, the remaining 1/4 cup of melted butter, and the chopped pecans.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E71832FD44D7604AB8278BAE2C6297D8', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the pecan topping mixture over the top of the sweet potato casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2988F86A6C3484592B325450051FDBE', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', X'B1BC64ADC93F0D4FB9FB0C73EBADE800', 6, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 35 to 40 minutes, until the filling is hot and the topping has browned. Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC9D30483C1E74CA08EA46096492A79', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0E433EBCD44EC4E893E0E586F9115DA', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter a 9-by-13-by-2-inch baking pan. Heat the oven to 350 F/180 C/Gas 4).');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51F0AED8192C384782E96A2BF671CA75', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, cook the sausage, breaking it up and stirring frequently, until it is fully cooked and no longer pink. Remove the sausage to paper towels to drain.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B493ED91F8957A409E6782082BDB1AFE', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 3, NULL, NULL, 2, NULL, 0, 'Wipe out the skillet with a paper towel and place it over medium-low heat. Add the butter and heat until it has melted. Add the chopped onion and celery and cook, stirring, until the vegetables have softened, about 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5236ADFA952914C9A6C4057EACCD243', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, toss the bread cubes with the herbs and seasonings.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F7C56444E177E4BBB954EEC51CCA07F', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 5, NULL, NULL, 2, NULL, 0, 'Add the vegetables with the butter and the drained sausage; then add the chicken broth and stir until well moistened, but not mushy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A496BAD7E6BD4CBA4F3BE02C42311F', X'3A0A79DB5FEF814785D8E68012551BDE', X'3A0A79DB5FEF814785D8E68012551BDE', 6, NULL, NULL, 2, NULL, 0, 'Pack gently into the prepared baking dish and cover it tightly with foil.');
