INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'935B2665DFA1CC448F34D40978FBE51E', X'B19617D55FC5064FABD9E32F69F7F106', X'B19617D55FC5064FABD9E32F69F7F106', 2, NULL, NULL, 2, NULL, 0, 'Toss with shredded or chopped cabbage and refrigerate for an hour or two before serving for best flavor.');
