INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E6E40870E4F364EB5660219B0AF7165', X'B19617D55FC5064FABD9E32F69F7F106', X'B19617D55FC5064FABD9E32F69F7F106', 4, NULL, NULL, 2, NULL, 0, 'Serve coleslaw with any summer meal, barbecued ribs, grilled chicken, burgers, or pulled pork, or use it to top shredded beef or chicken sandwiches. It is, of course, an essential side to serve with fried catfish or fish and chips!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCC4755D803BB740B3718877BAB7B7F0', X'31611ABBF9432E4BB8055E198A256CBF', X'31611ABBF9432E4BB8055E198A256CBF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients');
