INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6B99D16BF5BD442B2358128BCCDB901', X'B03A53C96FBC0442AA6DCE5BB1032121', X'B03A53C96FBC0442AA6DCE5BB1032121', 11, NULL, NULL, 2, NULL, 0, 'Set 1/2 cup of the shredded cheese aside and stir the remaining 1 1/2 cups of cheese into the sauce. Continue cooking and stirring until the cheese has melted.');
