INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A3C0496D8254842885776EBE9D3D2F8', X'B03A53C96FBC0442AA6DCE5BB1032121', X'B03A53C96FBC0442AA6DCE5BB1032121', 15, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter. Put the breadcrumbs in a small bowl and drizzle the butter over them. Toss the breadcrumbs lightly to coat with the butter.');
