INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'810105AA7102AF4E9E59D2872C6B21BA', X'B03A53C96FBC0442AA6DCE5BB1032121', X'B03A53C96FBC0442AA6DCE5BB1032121', 17, NULL, NULL, 2, NULL, 0, 'Bake the macaroni and cheese casserole for 25 to 30 minutes, until lightly browned and bubbly.');
