INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11AECFC18F61D745BA8C336250B82E44', X'631490AFF709BD43AB87096E8D21EDD8', X'631490AFF709BD43AB87096E8D21EDD8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
