INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2A1F3DE78F583409E348506D2849136', X'631490AFF709BD43AB87096E8D21EDD8', X'631490AFF709BD43AB87096E8D21EDD8', 2, NULL, NULL, 2, NULL, 0, 'Drain and carefully split the squash lengthwise.');
