INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'339753BAE7D1004D8BA06A8C674B3A19', X'631490AFF709BD43AB87096E8D21EDD8', X'631490AFF709BD43AB87096E8D21EDD8', 6, NULL, NULL, 2, NULL, 0, 'Add squash pulp, bread crumbs, and seasoned salt. Add a little water if the mixture is too dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5353E7EEC43F08479FA450E7F243A1D8', X'631490AFF709BD43AB87096E8D21EDD8', X'631490AFF709BD43AB87096E8D21EDD8', 7, NULL, NULL, 2, NULL, 0, 'Stuff squash shells with the sausage mixture and sprinkle with Parmesan cheese.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11341DBC42E6AB49B74479C2CCC00269', X'631490AFF709BD43AB87096E8D21EDD8', X'631490AFF709BD43AB87096E8D21EDD8', 8, NULL, NULL, 2, NULL, 0, 'Bake at 375 F for 15 to 20 minutes, or until hot and browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECB0A8EC99C2B94E844D80BA1FD996CA', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF5DD3F4813809438AC74033FEE3F855', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 1, NULL, NULL, 2, NULL, 0, 'Remove liver from giblets and refrigerate.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CA4979F6B327744B8B902A8BA76AF34', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 2, NULL, NULL, 2, NULL, 0, 'Place the remaining giblets into a saucepan and cover with 4 cups cold water; bring to a boil. Reduce the heat to low and simmer the giblets for about 1 1/2 to 2 hours. Add the liver to the saucepan and simmer for another 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F33E079E70EE146B420519AC1CFEB31', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 3, NULL, NULL, 2, NULL, 0, 'Place a mesh strainer or colander over a bowl. Drain the giblets and set the liquids aside to use in the gravy, if needed. Let the giblets cool. Remove the meat from the neck and chop with the rest of the meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA013DB404C86A448E0E97AAD1FBC0F7', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 4, NULL, NULL, 2, NULL, 0, 'Melt the butter in a heavy saucepan and stir in the flour. Cook and stir for 3 to 5 minutes, or until the roux just barely begins to turn golden.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'280FFCF8A3190D4281516D837002B053', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 5, NULL, NULL, 2, NULL, 0, 'If you don''t have drippings from a roasted turkey or chicken, or if you only have a small amount, add the giblet broth or chicken or turkey stock to make 2 cups. Slowly stir in the drippings and/or broth into the roux. Add the milk or half-and-half. Continue cooking and stirring until thickened.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5311D09B20A5443B7028BDF97202F7A', X'A61840B7D7758140B6F6E542FE6F79BE', X'A61840B7D7758140B6F6E542FE6F79BE', 6, NULL, NULL, 2, NULL, 0, 'Taste and season the gravy with kosher salt and freshly ground black pepper.');
