INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE614106E3A215469EAAD35D1D7F181D', X'4EFB16519B85234F878FB948D2755036', X'4EFB16519B85234F878FB948D2755036', 4, NULL, NULL, 2, NULL, 0, 'Cook on low 8 to 10 hours or until the meat and vegetables turn tender. Or cook on high for about 3 to 4 hours.');
