INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E46628AD2AE454284703B7B75765173', X'4EFB16519B85234F878FB948D2755036', X'4EFB16519B85234F878FB948D2755036', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF6E62757E302F4CA5B78ADBB4FD8A85', X'DBF1D81E9093BB4694E4FBA87DDC23D8', X'DBF1D81E9093BB4694E4FBA87DDC23D8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5DE0ECEB292714194B1E3CD54DC6461', X'DBF1D81E9093BB4694E4FBA87DDC23D8', X'DBF1D81E9093BB4694E4FBA87DDC23D8', 1, NULL, NULL, 2, NULL, 0, 'Scrub the beets and rinse well. Trim the beets, leaving the rootlets intact and about 1 inch of the tops.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F58FC19B996EB48A3FE9D5A54E55BA6', X'DBF1D81E9093BB4694E4FBA87DDC23D8', X'DBF1D81E9093BB4694E4FBA87DDC23D8', 2, NULL, NULL, 2, NULL, 0, 'Place a beet on a square of foil and drizzle with about 1/2 teaspoon of olive oil; rub the oil over the surface of the beet. Bring the corners of the foil up around the beet and twist to seal. Repeat with the remaining beets.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DFAD302E1C31F45A448394325618312', X'DBF1D81E9093BB4694E4FBA87DDC23D8', X'DBF1D81E9093BB4694E4FBA87DDC23D8', 3, NULL, NULL, 2, NULL, 0, 'Put the wrapped beets in the slow cooker. Cover, and cook on HIGH for 3 to 4 hours, or until the beets are tender. Remove from the slow cooker with tongs and place on a heatproof platter with a lip to catch any juices.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6FA7ED15EE3CA42863FEE77D8857D96', X'DBF1D81E9093BB4694E4FBA87DDC23D8', X'DBF1D81E9093BB4694E4FBA87DDC23D8', 4, NULL, NULL, 2, NULL, 0, 'When they''re cool enough to handle, remove the foil. Trim top and roots and slip the skins off. Use in a recipe or slice and eat!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAAA43D2274F03499658D59C71B76157', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FBC31F431DADE4C9CB3213BB231E432', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F. Prepare a 9 x 9-inch baking dish or pan with cooking spray or butter and flour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA804B6223F6DB468B96BFE6DA7C978A', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, sift together the flour, cornmeal, baking powder, and salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AC8914C95BAC94EA1A3F36C178DB8F6', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl or measuring cup, combine the half and half, eggs, melted butter, and honey and sugar if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADFC724AC89A2045BFFEB71982F0FFC3', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 4, NULL, NULL, 2, NULL, 0, 'Add the liquid ingredients to the dry and mix just until the flour is moistened, no more than 10 to 15 seconds. The batter should be visibly lumpy; do not overmix.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CB467871BDCF3468DEDF6F94ADC4F0F', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 5, NULL, NULL, 2, NULL, 0, 'Pour the batter into your prepared pan and bake the cornbread immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D942E20D451764498FDECC4D22A5ACE', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 6, NULL, NULL, 2, NULL, 0, 'Bake 25 to 30 minutes, or until a toothpick inserted into the center comes out clean and the edge of the bread starts to pull away from the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C8B5986C333E844AA278B5E62A47CF7', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 7, NULL, NULL, 2, NULL, 0, 'Cool for about 10 minutes, then remove ?it from the pan. Let it cool for 5 more minutes on a rack, and serve as soon as possible.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBB8DE93B70D924F8149FBC4917D8AA5', X'B98320AB2AE19B4689A0595660A7A280', X'B98320AB2AE19B4689A0595660A7A280', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEDE741C58B08C4CA6837EF3D478B491', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 0, NULL, NULL, 2, NULL, 0, 'Rinse the squash well and then pat dry with paper towels. Cut off and discard the ends of the squash. Slice the squash into 1/2-inch rounds.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C7CD450737BF4F8210E1AAC332EEFE', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 1, NULL, NULL, 2, NULL, 0, 'Peel the onion. Cut off and discard the root end. Cut the onion in half and chop it finely');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7740102E684DF49B5662A88A40C2970', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 2, NULL, NULL, 2, NULL, 0, 'Combine the sliced squash and chopped onion in a large saucepan. Cover the vegetables with water and add 1 teaspoon of kosher salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A106040079EA742B7A099E9E0C6ED73', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 3, NULL, NULL, 2, NULL, 0, 'Place the saucepan over high heat and bring to a boil. Reduce the heat to medium-low, cover the pan, and cook for about 15 minutes, or until the squash is very tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A893BD227B04544818A0A7CBB33F307', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE839B99C4DA804EB0C7353573869AD9', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 5, NULL, NULL, 2, NULL, 0, 'Butter a 2-quart baking dish or casserole.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A890AE58823B4DA087EFF077D45E59', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 6, NULL, NULL, 2, NULL, 0, 'Drain the squash and onions thoroughly and transfer them to a bowl; mash well. Taste and add seasoned salt and pepper, as desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A505B7A58A6BF4BBCFC61AB35C7E715', X'0FF819B6C214F54CB48C79927F6960B1', X'0FF819B6C214F54CB48C79927F6960B1', 7, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the eggs with the milk, flour, and 3 tablespoons of melted butter. Add the cheese and stir until well blended.');
