INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1C7F6E50E9B354D9DB389F191801EC4', X'32439C2FD9986E428732451811BEA70A', X'32439C2FD9986E428732451811BEA70A', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA899EEB6399F14799519BBC0963C89F', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68D7859318D0BB419C9A2A2B906B33DD', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 1, NULL, NULL, 2, NULL, 0, 'Peel the peaches, remove the stone, and roughly chop the fruits into small chunks. You will need a minimum of two cups for this recipe. Ripe peaches will be easy to peel with a sharp knife. If you are struggling to remove the skin, cut a small cross in the base of the peach, pop the peach into the microwave, and heat on high for 30 seconds.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E8B042410B6CF4AB1A6963564D3B7F6', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 2, NULL, NULL, 2, NULL, 0, 'Place the chopped peaches into a large glass bowl and press lightly with a potato masher to release some of the juice and squish the peaches pieces a little; be careful not to over press, there should still be some lovely chunks of fruit. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'105441AE58C8D044A120CC77F48421C9', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 3, NULL, NULL, 2, NULL, 0, 'Place the sugar into a microwavable bowl and heat on high for 1 1/2 minute. The sugar will become hot so be careful when handling. Heating the sugar this way helps it to dissolve in the jam more quickly and prevent the jam from being grainy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD6732416A079B46A327E0DC9D9B78D1', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 4, NULL, NULL, 2, NULL, 0, 'Add the warmed sugar to the peaches and stir through and continue to stir until the sugar dissolves. Taste, and if the jam is still grainy, then pop the jam into the microwave and heat on high for one minute at a time, and taste again. You should not need to do this more than three times if you have heated the sugar beforehand.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20C314F3B24BD74D97AE30C1A8814D82', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 5, NULL, NULL, 2, NULL, 0, 'Add the liquid pectin to the jam and stir through, finally add the lemon juice, stir again then cover the bowl with a tea cloth, leave to go completely cold then place into the refrigerator for 24 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A271EBD1B541BA4A8AD417B2022FA844', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 6, NULL, NULL, 2, NULL, 0, 'The next day, wash three one-pound glass jars in hot soapy water and dry them carefully if you are canning your jam. Otherwise, use freezer bags.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8C92AE40BE1E444B9E317017171B905', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 7, NULL, NULL, 2, NULL, 0, 'Fill the jars leaving a one-inch gap at the top, clean the rim with a damp cloth before covering with a lid. You should fill two comfortably and any leftover can go into the third jar that can be kept in the fridge and used within three weeks. If using freezer bags, portion the jam into the bags, do not overfill, seal and freeze.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2BFBBAE5273C444B90E357BC7CFC868', X'2A6D5C97D30E7D44825C8E0C6918A4E3', X'2A6D5C97D30E7D44825C8E0C6918A4E3', 8, NULL, NULL, 2, NULL, 0, 'To defrost jars of freezer jam, do not rush this process, remove from the freezer and leave in the refrigerator until defrosted, this will not take more than 24 hours (freezer bag swill defrost much faster). Never put the frozen jars in the microwave to speed up the process.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98FD2EE0ADA4C8498D8A52D36F77A124', X'CCA0C144EEC10A43B71DCE6088F1F9C0', X'CCA0C144EEC10A43B71DCE6088F1F9C0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
