INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C5D43F8C7456D4EBEDD9A228CB7359B', X'CCA0C144EEC10A43B71DCE6088F1F9C0', X'CCA0C144EEC10A43B71DCE6088F1F9C0', 2, NULL, NULL, 2, NULL, 0, 'Pour it into a small canning jar with a screw top, another small lidded container, or a zip-close food storage bag. Use as desired. Enjoy.');
