INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9116932B76BED4797E5769C3795B735', X'F03CC7E8A0B7B548AEC25C4E4EEE73A6', X'F03CC7E8A0B7B548AEC25C4E4EEE73A6', 4, NULL, NULL, 2, NULL, 0, 'Cook for 15 to 20 minutes, stirring frequently, or until the apples are tender and the sugar mixture has become syrupy. Some varieties of apples hold their shape better than others. Add more water to the pan if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'654BFDB14E16FA4CB940BD52B05C8280', X'F03CC7E8A0B7B548AEC25C4E4EEE73A6', X'F03CC7E8A0B7B548AEC25C4E4EEE73A6', 5, NULL, NULL, 2, NULL, 0, 'Serve over pancakes, ice cream, or inside of crepes, and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13B1C75D7C672B44A9BCE0DF256A2324', X'1CAE0C5299E08643821E08EE26E0E908', X'1CAE0C5299E08643821E08EE26E0E908', 0, NULL, NULL, 2, NULL, 0, 'Put potatoes in a medium saucepan and cover with water. Add about 1 teaspoon of kosher salt for each quart of water. Bring the potatoes to a boil, reduce heat to low, and cover the pan. Simmer the potatoes for about 15 minutes, or just until the chunks are fork-tender. Drain and set them aside to cool.');
