INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F90A486C9AFBE24694224E0D43568D5B', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Grease and flour a 13 x 9-inch baking pan and set aside. Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A612E63E77E19543AA808F664C49C21A', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat the eggs until thick. Gradually beat in the sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E822FE9377324941BA98E1BC1CD99D4B', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 2, NULL, NULL, 2, NULL, 0, 'In another bowl, combine the melted butter with flour, cocoa, vanilla, sweetened flaked coconut, and pecans.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57109FCAB51E444797C04060A6B3ACA6', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 3, NULL, NULL, 2, NULL, 0, 'Add the chocolate mixture to egg and sugar mixture. Stir well to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550CA5D84ABF8940A72BE2F05DAF5BFF', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 4, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared pan. Bake for 30 minutes or until a skewer inserted in the center of the cake comes out clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFFC505EF2AE7408DCFB2EBA231CE56', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 5, NULL, NULL, 2, NULL, 0, 'Remove the cake from the oven. Immediately spread marshmallow fluff gently on the surface of the warm cake.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EACE50CF0A7A434693FF05A19FD432DF', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 6, NULL, NULL, 2, NULL, 0, 'Prepare the frosting. In a medium bowl, combine the melted butter, cocoa powder, vanilla extract, and milk. Stir in the powdered sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'859192164E9F5546B85D05EA90665A00', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 7, NULL, NULL, 2, NULL, 0, 'Spread the frosting gently over the warm marshmallow crème, swirling to give a marbled effect.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8430D494EA90B74FAFDC429A15A363D1', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle the chopped pecans over the top.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91936CF0953D304D9A34802EF6B073CC', X'5981207621238A4289318E2B750E12A7', X'5981207621238A4289318E2B750E12A7', 9, NULL, NULL, 2, NULL, 0, 'Cut into squares to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40E973C06C1BCD4AA819CE57AF5FCF2D', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C35873A43DC020479E099628931295F4', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 1, NULL, NULL, 2, NULL, 0, 'Peel the ripe and sweet peaches and then slice them thinly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB29B065AE3EE4FB4B238D4268C815C', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 2, NULL, NULL, 2, NULL, 0, 'Place them in a plastic ziplock bag and freeze the peaches for 3 to 4 hours or until completely frozen. Try to place the peaches in an even, flat layer in the bag and place them flat on a freezer shelf so they can be broken up and put in the blender easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67A305B6758D834C8E39FBCB3017F92E', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 3, NULL, NULL, 2, NULL, 0, 'Pour the lemonade (water can also be substituted) into a measuring cup. Add the 1 cup of sugar and 1 tablespoon of freshly squeezed lemon juice. Stir the mixture until the sugar dissolves somewhat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15645C5C99E3BD4B85B19108DE8074F3', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 4, NULL, NULL, 2, NULL, 0, 'Pour the lemonade mixture into a blender and then add the frozen peach pieces. If the frozen peaches formed a kind of block, break it up into smaller pieces before adding it to the blender. Blend on high until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21CFC24BA04BBB419885361308E70902', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 5, NULL, NULL, 2, NULL, 0, 'You can eat it immediately as a a kind of soft serve sorbet or place it in a loaf pan, cover in plastic wrap, and freeze. Freeze the peach sorbet for at least 5 hours or until completely frozen. You can even freeze it overnight if you want to be sure it is completely frozen.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48CD41D917978540A5FFE3C60D8D5541', X'A4D56FC163F95448874BC48C07DBE4CA', X'A4D56FC163F95448874BC48C07DBE4CA', 6, NULL, NULL, 2, NULL, 0, 'Scoop and serve the sorbet when you are ready to eat!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C973C36B8F1CC84EAD06B7A09F717E23', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC54D44774CF874B90E9B056917EC665', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 degrees F. In the bowl of a food processor fitted with the S-shaped (sabatier) blade, combine 3/4 cup of the chilled butter, powdered sugar, and salt. Process until moist clumps begin to form.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBCB01B11E6D14428284FB0BA4AB8066', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and egg, and pulse just until a dough forms');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F93ECE2507AD34DAEB8EE4A76CE5E0C', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 3, NULL, NULL, 2, NULL, 0, 'Press the dough onto bottom and 3/4 inch up the sides of a 13x9x2-inch baking pan. Bake until the crust is golden brown, about 22 minutes. Allow the crust to cool slightly on a wire rack. Maintain the oven temperature as you proceed through the next steps.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EFB47BB5A8BE64F81AE417E8E70B953', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 4, NULL, NULL, 2, NULL, 0, 'In a large saucepan, bring the brown sugar, honey, and remaining 3/4 cup of butter to a boil, stirring frequently. Boil for 2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EB62A437AED1F419A49E23D76DA38CA', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 5, NULL, NULL, 2, NULL, 0, 'Remove the saucepan from the heat and add the vanilla extract. (The mixture will bubbly slightly when the vanilla is added.)');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB4C328A998B34A91F8D19303F9CF66', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 6, NULL, NULL, 2, NULL, 0, 'Add the pecans and stir to coat completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2EB4B52CD3B6F4DB8C6B918B64612DD', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 7, NULL, NULL, 2, NULL, 0, 'Pour the pecan filling onto prepared crust, spreading it out evenly. Return the pan to the 375 degree oven. Bake until filling thickens and bubbles all over, about 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B86AAB33CCA2A47B4FACFD7486700BA', X'6842CF1625C889419453AA59C7D1F6C6', X'6842CF1625C889419453AA59C7D1F6C6', 8, NULL, NULL, 2, NULL, 0, 'Allow to cool completely on a wire rack. Cut into 2-inch squares.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADDAEEE8BBD4B949B5ADDB905AF7A642', X'881AEC1A70ADBE45B2D41DDC51F9418C', X'881AEC1A70ADBE45B2D41DDC51F9418C', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D69B7A806A68E4E839DB2F06A726614', X'881AEC1A70ADBE45B2D41DDC51F9418C', X'881AEC1A70ADBE45B2D41DDC51F9418C', 1, NULL, NULL, 2, NULL, 0, 'Butter an 11 by 7-inch baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5989EAA4D7080D4F847A8A92BB36484F', X'881AEC1A70ADBE45B2D41DDC51F9418C', X'881AEC1A70ADBE45B2D41DDC51F9418C', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the sliced apples, raspberries, blueberries, and granulated sugar. Toss to coat. Transfer the apple and berry mixture to the prepared baking dish. Dot with small pieces of cold butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13A8D3CA631E9949AD0CA9DE742DDB8F', X'881AEC1A70ADBE45B2D41DDC51F9418C', X'881AEC1A70ADBE45B2D41DDC51F9418C', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl combine the melted butter with the brown sugar, flour, salt, baking soda, and rolled oats. Sprinkle the crumb mixture evenly over fruit.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C9025D88788714986217456067E9BAD', X'881AEC1A70ADBE45B2D41DDC51F9418C', X'881AEC1A70ADBE45B2D41DDC51F9418C', 4, NULL, NULL, 2, NULL, 0, 'Bake this fruit and berry crisp in the preheated oven for 45 to 55 minutes, or until fruit is tender and topping is browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84716303E3C0CD4FB377C6341E6EF643', X'881AEC1A70ADBE45B2D41DDC51F9418C', X'881AEC1A70ADBE45B2D41DDC51F9418C', 5, NULL, NULL, 2, NULL, 0, 'Serve the apple berry crisp warm with a scoop of vanilla ice cream or drizzle the crisp with heavy cream, or serve it cold or at room temperature with whipped cream or whipped topping. Store leftovers in the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9E4FDE0C368ED4CADA7FBB4AD50109D', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 0, NULL, NULL, 2, NULL, 0, 'Cut the challah, or other egg bread such as brioche, into 1-inch cubes. This is a great use of stale bread. Although you can use fresh bread as is, stale bread will absorb the liquid better. If your bread is fresh, lightly toast the cubes by spreading them out on a baking sheet and placing them in a 350 F oven for 10 minutes. You can also leave them on the counter overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50617BCE63DF194793CA4E878229D092', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, whisk together the sugar, unsweetened cocoa powder, ground cinnamon, salt, whole milk, heavy cream, eggs, and vanilla.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEAE6264E2CD9B41B1F3B4ECD267F599', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 2, NULL, NULL, 2, NULL, 0, 'Fold in the bread cubes and chocolate chips and allow the mixture to sit for about half an hour for the bread to absorb some of the liquid.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'199878DA99EDEE4C9AA819F1F09FD8B4', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95ED3363F9B3774F9B1BCF711588F272', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 4, NULL, NULL, 2, NULL, 0, 'Butter a medium sized baking dish (approximately 10` x 7`) and pour in the bread mixture and all the unabsorbed liquid. Note that it will puff as it bakes and then slightly deflate when it cools down after baking.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1950250D4CA48D4F93EA84EBD78CDE7E', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 5, NULL, NULL, 2, NULL, 0, 'Place on a baking sheet to catch any drips and bake for 30 to 40 minutes, or until the mixture is set.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF0B5AF2814EC443963727C56B8B0CD4', X'56B12DA5FEF56F4F9F3D0B458F84D540', X'56B12DA5FEF56F4F9F3D0B458F84D540', 6, NULL, NULL, 2, NULL, 0, 'Make the topping by melting the chocolate chips in the microwave for a few seconds and stirring in the heavy cream until smooth. Pour over the top of the bread pudding and serve warm or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'807289FAC26CAE40B28A34872B02F82B', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C9D87384B398245959FF0015F434251', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 1, NULL, NULL, 2, NULL, 0, 'Pour the apple cider into a saucepan and bring it to a boil over high heat. Reduce the heat to medium and boil until it is reduced to 1/4 cup, about 15 minutes. Set the concentrated boiled cider aside to cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'266D6133C00ECB4BBE114959379C6002', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the flour, sugar, baking powder, baking soda, cinnamon, nutmeg, and salt. Stir or whisk to blend thoroughly and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A88DF80AE4E8544799C6F5C48BAFD762', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 3, NULL, NULL, 2, NULL, 0, 'In another bowl, whisk the buttermilk and eggs together until well blended. Whisk in the melted butter and vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62408AB35D911B4EABF1BAF768383187', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 4, NULL, NULL, 2, NULL, 0, 'Add the wet mixture to the dry ingredients and mix to form a soft, well-blended dough. You can use a stand mixer with paddle attachment for this step or mix by hand.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90B20B43C866AF4EA49758153B48062A', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 5, NULL, NULL, 2, NULL, 0, 'Line a large baking sheet with parchment paper; sprinkle liberally with flour. With floured hands, pat the dough out onto the parchment paper to a thickness of about 3/8-inch to 1/2-inch. Transfer the baking sheet to the freezer for about 15 minutes, until firm and well chilled. Alternatively, refrigerate the dough for about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35F85FC62C215046930BC20383D86526', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, pour about 2 to 3 inches of vegetable oil into a Dutch oven or large, heavy saucepan. Attach a deep-fry thermometer to the pan and heat the oil to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'866FD8D6DE7C6440B6F46C89307BDC37', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 7, NULL, NULL, 2, NULL, 0, 'Remove the dough from the refrigerator or freezer and transfer it to a floured work surface. Cut the donuts out with a 3-inch doughnut cutter. Dip it into flour between cutting out the dough to keep the dough from sticking. Separate the donuts from the doughnut holes. Re-roll the dough as necessary and continue to cut out donuts until all of the dough is used.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFDF893C3AFF2444BADB84CD82173D6F', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 8, NULL, NULL, 2, NULL, 0, 'For the cinnamon sugar, combine the 1 cup of granulated sugar and 1 tablespoon of cinnamon in a bowl and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFFB55689ECD564FA544BFA67CF5A4C5', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 9, NULL, NULL, 2, NULL, 0, 'Place paper towels on a baking sheet and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76CBF5F55B287546BCD58C252B8D931C', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 10, NULL, NULL, 2, NULL, 0, 'Using a metal spatula or skimmer, lower the about 3 to 4 donuts into the hot oil. Fry the donuts on one side for 1 minute and then turn them over and fry for about 1 minute longer, or until golden brown. Carefully remove the donuts to the paper towel-lined baking sheet to drain. Repeat with the remaining donuts; fry the donuts holes last; they will take a bit less time.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F53171DF529B740B044FE636E0EE777', X'9B68DECA37221C429FAA5A588DFFA0D1', X'9B68DECA37221C429FAA5A588DFFA0D1', 11, NULL, NULL, 2, NULL, 0, 'Dip the donuts into the cinnamon-sugar mixture while they are still warm, coating them completely or just the tops.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561BC00537284D4BA67D018277E06E44', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BF64388E1098246960B3D52D26573C5', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 1, NULL, NULL, 2, NULL, 0, 'Line baking sheets with wax paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBB4806707F6A049AE426875E147DDDF', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 2, NULL, NULL, 2, NULL, 0, 'Spread a thick layer of peanut butter on a cracker. Gently press another cracker onto the peanut butter layer to make a sandwich cookie. Repeat with the remaining crackers.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7EA7F9665AA4E44A5BCCC9F490F65BD', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 3, NULL, NULL, 2, NULL, 0, 'Melt the almond bark or candy melts in a microwave oven or in the top of a double boiler over simmering water as directed on the package.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30C06A487E2C9941AAAB11ECBB6DABD2', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 4, NULL, NULL, 2, NULL, 0, 'Dip each sandwich cookie in the melted chocolate or almond bark. With a fork, lift the cookie out and shake gently to let the excess drip off.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03DF23AF2A63884F9838DFB4B208138E', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 5, NULL, NULL, 2, NULL, 0, 'Place the cookies on the wax paper-lined pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97A650212ED7F64CB0165D39F35B0035', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 6, NULL, NULL, 2, NULL, 0, 'Before the chocolate sets, sprinkle the treats with colorful decorating sugar or candy sprinkles. Refrigerate until firm.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC8CBFB4598D546BF319C52A9BA5061', X'19411195DC73F44C82E3697423BC085F', X'19411195DC73F44C82E3697423BC085F', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D6E4AFAE6DF947872149DC64A25FC8', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B889C42D26E8348A2BF5E6191B65B45', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 1, NULL, NULL, 2, NULL, 0, 'Peel the banana and slice lengthwise.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34D22C302A15F040881BF1FB92AC6922', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 2, NULL, NULL, 2, NULL, 0, 'Melt brown sugar and butter in a small saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F39F2202B2D71849BFCCF09DA0612923', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 3, NULL, NULL, 2, NULL, 0, 'Add the banana and sauté until tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8505DB562EE29444A298F60151480CC6', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle with the ground cinnamon. Add the banana liqueur and rum. Gently warm the alcohol and coat the bananas in the sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41267EBF3FD33440B09EB1A914217E6C', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 5, NULL, NULL, 2, NULL, 0, 'Ignite the sauce with a flame.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A96EA028FD40744494C8516015C62828', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 6, NULL, NULL, 2, NULL, 0, 'Baste the bananas in the liquid until the flame burns out.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FE9DDB074617C4E81D9F4CE9F74AED0', X'FF22AEDE33BF0A44B3C89595E4937A03', X'FF22AEDE33BF0A44B3C89595E4937A03', 7, NULL, NULL, 2, NULL, 0, 'Serve the bananas foster immediately with a scoop of ice cream, and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F101BB6A1653247979207344DC3B2E6', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 0, NULL, NULL, 2, NULL, 0, 'Grease your bundt pan with cooking spray. Pour the 1/2 cup of melted butter into the bottom of the pan and sprinkle the 1/2 cup of brown sugar evenly over the butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C90E3EDF3FCDFB49ACDFC0525FA82B38', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 1, NULL, NULL, 2, NULL, 0, 'Prepare the pineapple by cutting the peeled fruit into rings and coring each ring. Cut each ring in half. Lay the curved edge of each half ring into the indentations of the bundt pan. Place the maraschino cherries in between each piece of pineapple. You may need more or fewer maraschino cherries and pineapple rings depending on the shape of your bundt pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9989D85E47C49E44A8191B1E1307606E', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Place the oven rack on the lower middle part of the oven. Cream together the 1/2 cup of butter and 1 1/2 cups of sugar in the bowl of a stand mixer with the whisk attachment until creamy, about 10 minutes on medium-high speed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9D87F6AE959114D95BDFB71FBF39D49', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 3, NULL, NULL, 2, NULL, 0, 'Beat in the 2 eggs and 1 teaspoon vanilla until fluffy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21F4BCE094E58043A72E2900518280A2', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 4, NULL, NULL, 2, NULL, 0, 'Sift together the dry ingredients in a separate bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15082A3E8865F84C8F929C3162DB9382', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients alternately with the 1 1/4 cup of milk to the creamed mixture in your stand mixer. Beat after each addition and scrape down the sides of the bowl as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'074A6BC8D6797D4FB53302E7546BFA44', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 6, NULL, NULL, 2, NULL, 0, 'Pour the cake batter over the prepared bundt pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'901D323E2CDEE543A53AFD0A414295DB', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 7, NULL, NULL, 2, NULL, 0, 'Place the pan in the preheated oven and bake for about 40 minutes, or until edges are bubbly and a toothpick comes out of the cake cleanly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58FDAFE42A5AD34A935F90B3DB2B1CE2', X'E4806CC4917A084BAF1883A1F280874E', X'E4806CC4917A084BAF1883A1F280874E', 8, NULL, NULL, 2, NULL, 0, 'Remove the cake from the oven and allow it to cool for 15 minutes in the cake pan. Then flip the cake onto your chosen serving platter and serve while it is still warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EBFEBEFB7599140AC08CD7C46C7B77C', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF0574C21224AB48BCA869494F571400', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2F891D5C9D28D4BABBD4D6FB15D7DEF', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 2, NULL, NULL, 2, NULL, 0, 'Spray doughnut pans with nonstick cooking or baking spray.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E495A3249DFF504989C93AC367D21875', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the flour, granulated sugar, baking powder and salt. Blend thoroughly with a whisk or spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D53B97E52CF75341B2B3501C667256A9', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, whisk the eggs with the milk, melted butter, 2 teaspoons of orange zest and 1 1/2 teaspoons of vanilla extract.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28BDFC65B3E6444F8D8265BE61E3E6F4', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 5, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet mixture along with the finely chopped cranberries. Mix just until the ingredients are moistened.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DBBB44817938A4DA327AFA247B5049B', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 6, NULL, NULL, 2, NULL, 0, 'Spoon the batter into the cavities of the doughnut pans, filling about half full. Alternatively, spoon the batter into a plastic food storage bag, cut off a small piece of the corner and pipe the batter into the pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'093E27325CDCBC4A8605BA8EEDCF6473', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 7, NULL, NULL, 2, NULL, 0, 'Bake the doughnuts for about 10 to 12 minutes (about 7 to 9 minutes for mini doughnuts), or until a toothpick comes out clean when inserted into a doughnut.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B90C4F6F73B19149B7CF482F9DCCA3FD', X'7D966F19F5C33B41870C6326F04EE0D7', X'7D966F19F5C33B41870C6326F04EE0D7', 8, NULL, NULL, 2, NULL, 0, 'Let the doughnuts cool for about 5 minutes and then carefully remove them to a rack to cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9E0946F90C99D4C8A17C80CA3E9BACA', X'6A5A6FA807D8EB44AE31434D01A192D7', X'6A5A6FA807D8EB44AE31434D01A192D7', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350° F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9E89200F334C46978D887C57B87179', X'6A5A6FA807D8EB44AE31434D01A192D7', X'6A5A6FA807D8EB44AE31434D01A192D7', 1, NULL, NULL, 2, NULL, 0, 'Combine flour and confectioners'' sugar in mixing bowl. Add melted butter and mix to blend well. Pat the dough out into a 12-inch or 14-inch pizza pan. Bake the crust for 10 minutes, or until lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3DFD4588D157E47BF898FC0A9B4D27A', X'6A5A6FA807D8EB44AE31434D01A192D7', X'6A5A6FA807D8EB44AE31434D01A192D7', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl with an electric mixer, beat the cream cheese with sweetened condensed milk, lemon juice, and vanilla. Spread over the cooled pizza crust.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7978BAF9FB054944B88D083CFB066CE7', X'6A5A6FA807D8EB44AE31434D01A192D7', X'6A5A6FA807D8EB44AE31434D01A192D7', 3, NULL, NULL, 2, NULL, 0, 'In a large saucepan, combine sugar and cornstarch; stir in water. Cook, constantly stirring, over medium heat until thickened. Add blueberries and cool completely.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D49C14CF8899D418E56407ABC16EED0', X'6A5A6FA807D8EB44AE31434D01A192D7', X'6A5A6FA807D8EB44AE31434D01A192D7', 4, NULL, NULL, 2, NULL, 0, 'Spread cooled blueberry mixture over cream cheese layer. Chill thoroughly.');
