INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'467CEE4B0D33184084124DB2EFA3C2B4', X'5F6683F0D4D8BB4E9490E0C97FF1800C', X'5F6683F0D4D8BB4E9490E0C97FF1800C', 3, NULL, NULL, 2, NULL, 0, 'In a mixing bowl with an electric mixer, combine the cake mix, butter, and the beaten egg. Beat on low speed until the butter is blended into the dry cake mix and the egg mixture and the crumbs begin to clump together. Stop mixing and pat the crumbs firmly into the bottom of the prepared baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1ACF86D790020747AD13EAA2EE04BB5C', X'5F6683F0D4D8BB4E9490E0C97FF1800C', X'5F6683F0D4D8BB4E9490E0C97FF1800C', 4, NULL, NULL, 2, NULL, 0, 'In a mixing bowl with an electric mixer, beat the softened cream cheese until smooth. Add the confectioners'' sugar and beat until the mixture is light and well blended. Beat in the remaining 3 large eggs. Continue beating until very smooth. Beat in the vanilla, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74523AF24CC70246A5BFBB3FDB480CC3', X'5F6683F0D4D8BB4E9490E0C97FF1800C', X'5F6683F0D4D8BB4E9490E0C97FF1800C', 5, NULL, NULL, 2, NULL, 0, 'Pour the cream cheese batter over the first layer and spread to cover evenly.');
