INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CD7D4AAF27BD141B783D31F12B6ADEB', X'5F6683F0D4D8BB4E9490E0C97FF1800C', X'5F6683F0D4D8BB4E9490E0C97FF1800C', 7, NULL, NULL, 2, NULL, 0, 'Cook on a rack and then cover the pan with foil or plastic wrap. Transfer the pan to the refrigerator to chill thoroughly before cutting. They are a little gooier if you cut them while they are still warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37CCA56C44349C468975B0296F474822', X'5F6683F0D4D8BB4E9490E0C97FF1800C', X'5F6683F0D4D8BB4E9490E0C97FF1800C', 8, NULL, NULL, 2, NULL, 0, 'This recipe makes about 24 (2-inch) bars. Store the bars in the refrigerator until ready to serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD726656AC86904E80A725F24836940C', X'0D35F0F95266824C888B05CC710B0F28', X'0D35F0F95266824C888B05CC710B0F28', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
