INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B56265AAE6BE4C82F344273154C57E', X'0D35F0F95266824C888B05CC710B0F28', X'0D35F0F95266824C888B05CC710B0F28', 2, NULL, NULL, 2, NULL, 0, 'Whisk in the remaining milk and bring the mixture to a boil over medium-high heat, stirring occasionally. This can take up to 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E8EA3B67F68B949868387D185E98BA5', X'0D35F0F95266824C888B05CC710B0F28', X'0D35F0F95266824C888B05CC710B0F28', 3, NULL, NULL, 2, NULL, 0, 'When the sauce comes to a boil, reduce the heat to low and simmer very gently for 5 minutes, whisking often. Remove from heat, add the vanilla extract, and let the sauce cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40152905AA5ED54DB6635873921C8420', X'0D35F0F95266824C888B05CC710B0F28', X'0D35F0F95266824C888B05CC710B0F28', 4, NULL, NULL, 2, NULL, 0, 'Store the sauce in the refrigerator for up to 2 weeks, covered.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AEC73DDDC1472408126583845814282', X'0D35F0F95266824C888B05CC710B0F28', X'0D35F0F95266824C888B05CC710B0F28', 5, NULL, NULL, 2, NULL, 0, 'Warm the sauce in the microwave or on the stovetop, if desired, and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BFD0261AFF31A43B60CDEDB7E35BD22', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 0, NULL, NULL, 2, NULL, 0, 'Cut the butternut squash in half lengthwise; remove stem and scoop out the seeds.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7D4750D1ADA1640A0F45D867F9A2019', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 1, NULL, NULL, 2, NULL, 0, 'Place the squash, cut side down, on a foil-lined oiled baking pan; add about 1/2 cup of water to the pan. Cover loosely with foil and bake at 400 F for 45 to 55 minutes, or until the squash is tender and can be easily pierced with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94CBB2D7AC087042AED18920685247AE', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 2, NULL, NULL, 2, NULL, 0, 'Let the squash cool completely; scoop out the flesh and then peel and mash or puree the squash or put it through a food mill. Measure 1 1/2 cups of the squash and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E2ACA71A69D3848B82B69D855630800', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 3, NULL, NULL, 2, NULL, 0, 'Reduce oven temperature to 350 F and position an oven rack in the center of the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97A581799D38EB42B92792B0F4A3DFEB', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 4, NULL, NULL, 2, NULL, 0, 'In a mixing bowl with an electric mixer, beat the squash with the brown sugar. Add the eggs, evaporated milk, spices salt, flour, butter, and vanilla. Beat until well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFDEF24C6E2314F8C9F29616E03103E', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 5, NULL, NULL, 2, NULL, 0, 'Pour the filling into the chilled pie crust and place on the center oven rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'836EA54CA4927649982F9AD5414D50DC', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 6, NULL, NULL, 2, NULL, 0, 'Bake for 45 to 55 minutes, or until set. Check after about 35 minutes and loosely set a ring of foil or a pie crust protector over the browned crust, so it won''t get too dark.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF461D3D94109747AD02A968124102CB', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 7, NULL, NULL, 2, NULL, 0, 'When the filling is set, transfer the pie to a rack to cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A06E8F81FA90E44FB68AD9ECE9B58762', X'5E51BE9C6DC1694C9192F4717FE3CC18', X'5E51BE9C6DC1694C9192F4717FE3CC18', 8, NULL, NULL, 2, NULL, 0, 'Serve just warm or at room temperature with a dollop of whipped topping or whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F97567F6593EE40A3BD1B8A46E7F6B0', X'15D31CAC67C1D24AAFA872B6CAC979C9', X'15D31CAC67C1D24AAFA872B6CAC979C9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51F42B015BD9014091C1194CDF4A33F8', X'15D31CAC67C1D24AAFA872B6CAC979C9', X'15D31CAC67C1D24AAFA872B6CAC979C9', 1, NULL, NULL, 2, NULL, 0, 'Butter a 1 1/2-quart baking dish or 9-inch pie plate. Heat the oven to 425 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E4ED0839DD2CD4EB5FDAFF1D8DA119C', X'15D31CAC67C1D24AAFA872B6CAC979C9', X'15D31CAC67C1D24AAFA872B6CAC979C9', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine 2/3 cup of granulated sugar and cornstarch, stir in water. Bring to a boil; stirring constantly, boil for 1 minute. Add blueberries and blend well.');
