INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76DE99283DEF834A8D4E1C3F0584CCBC', X'15D31CAC67C1D24AAFA872B6CAC979C9', X'15D31CAC67C1D24AAFA872B6CAC979C9', 5, NULL, NULL, 2, NULL, 0, 'Pour the milk and 3 tablespoons melted butter into a measuring cup; add to flour mixture all at once. Stir just until mixture is moistened and forms a soft dough.');
