INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06AA1A048184D545BFA768558AF52AF8', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EA13BBCB9FF8148838DA8DD7664BFFE', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the oats, chocolate chips, vanilla, and peanut butter, if using; stir to blend the ingredients. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'715B1F96A0F00646B07E038D5E9C0D4A', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine the butter with the milk and granulated sugar. Place the pan over medium-high heat and bring to a full rolling boil. Boil the mixture for 1 full minute.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E329913A25E50E4EA20DA5FE70A99865', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 3, NULL, NULL, 2, NULL, 0, 'Combine the hot butter and sugar mixture with the oatmeal and chocolate chip mixture. Stir until the chocolate has melted and the mixture is well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9B54AACA17D534FBB2309E349D7B96E', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 4, NULL, NULL, 2, NULL, 0, 'Working quickly, drop by spoonfuls or cookie scoop onto waxed paper or parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9122D8AA7AEDB74696AA8730C7528EA0', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 5, NULL, NULL, 2, NULL, 0, 'Allow to cool and firm up.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C1A7F4E70303459F30205B28B725DB', X'30AF99B1FC26A34F8BFB98F76F13267B', X'30AF99B1FC26A34F8BFB98F76F13267B', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05D1EAD683423940AE99A6AE1C437822', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 0, NULL, NULL, 2, NULL, 0, 'Gather the cake ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B23B5D410A2FD4EA2E6E4A6871B6C5D', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 1, NULL, NULL, 2, NULL, 0, 'Pre heat the oven to 325 F (165 C/Gas 3). Generously grease and flour a 12-cup Bundt pan or 10-inch tube pan. Make sure you get all the nooks and crannies of the Bundt cake pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04411DF40A611047BBD81FE305371E11', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl or the bowl of a stand mixer fitted with a paddle attachment, combine the flour, 2 cups of sugar, salt, baking powder and soda, the buttermilk, 1 cup of butter, 2 teaspoons of vanilla, and the eggs; beat on low speed until moistened. Increase the mixer speed to medium and beat for about 3 minutes longer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AC61CC14796C24289A87A01B98A33AA', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 3, NULL, NULL, 2, NULL, 0, 'Spoon the batter into the prepared cake pan and spread evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4C3611A436E524F8CE7170EABA63027', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 4, NULL, NULL, 2, NULL, 0, 'Bake the cake in the preheated oven for 55 to 65 minutes, or until a toothpick inserted into the center comes out clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D17261C8B2586409EAF8CB2B6B510EF', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, gather the glaze ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEFEA58A4F09084CB166D63896066C96', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 6, NULL, NULL, 2, NULL, 0, 'In a small saucepan over low heat, combine the 3/4 cup of sugar, 1/3 cup of butter, 3 tablespoons of water, and 2 teaspoons of vanilla; cook, stirring, just until butter melts. Do not bring the mixture to a boil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4F6BFFC13A48641A6D9F5B928B966FC', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 7, NULL, NULL, 2, NULL, 0, 'Remove the cake to a rack and (while it is still in the pan) pierce it all over with a fork or skewer.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65600EF3618B9442A66F4C41606F2FAC', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 8, NULL, NULL, 2, NULL, 0, 'Pour the hot butter glaze over the warm cake.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3BC1EA905141FE4B8BC4C7AED6944AAB', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 9, NULL, NULL, 2, NULL, 0, 'Let the cake stand 5 to 10 minutes, or until glaze is absorbed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12F68944884E5243A01C50130C447489', X'E6B838F49224E344A7EA313C0DB28FA2', X'E6B838F49224E344A7EA313C0DB28FA2', 10, NULL, NULL, 2, NULL, 0, 'Then carefully turn it out onto a cake plate and sprinkle with some confectioners'' sugar, if you''d like.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F36E463B8B146A49991E8646DDAC2544', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1396B686DEB77D44AAD8D813535B568F', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 1, NULL, NULL, 2, NULL, 0, 'Thoroughly combine the vanilla wafer crumbs, chopped pecans, 1 cup confectioners'' sugar, and the cocoa powder.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5767D42B18FA0409D5DCA276C2B9D3E', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, blend the bourbon and corn syrup.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E476C51331EDF2468F67F8626959BF27', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 3, NULL, NULL, 2, NULL, 0, 'Stir the bourbon mixture into the dry mixture. Stir and mix well with the help of a wooden or stainless steel spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'668F6353707D6641A289BA895E4C567D', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 4, NULL, NULL, 2, NULL, 0, 'Cover the mixture and chill in the refrigerator for at least 2 to 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E46E222C7FD2E49BE0EFEA82CAF7313', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 5, NULL, NULL, 2, NULL, 0, 'Once the mixture is chilled and ready to be shaped, sift about 1/2 to 1 cup of confectioners'' sugar into a wide, shallow bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E70766B9B2DC384FB5EE8C075593A3C4', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 6, NULL, NULL, 2, NULL, 0, 'Place a bowl of cold water next to your working area. Wet your hands with cold water and shape small bits of the dough into balls. Roll them in the confectioners'' sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'787967E3AD11FA428E09F9D6A7697655', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 7, NULL, NULL, 2, NULL, 0, 'Serve right away, or store in the refrigerator in tightly covered containers for up to 2 weeks.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDF0883494D23F4F99D2FAEF4CD8463D', X'7703DB2CAEDF3D4D94154812FCE9058D', X'7703DB2CAEDF3D4D94154812FCE9058D', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51495BAE226963409F621447CD915E4B', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8F75EBF6D1E564690785F82457B1556', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE81AA3C066E594097103A4DFFBEA0B3', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 2, NULL, NULL, 2, NULL, 0, 'Combine the butter and sugar in the bowl of a stand mixer. Alternatively, use a hand mixer in a sturdy bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'145A4673081C2946B5C1DF6B1668A8FE', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 3, NULL, NULL, 2, NULL, 0, 'Cream the butter and sugar until light and fluffy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BEF95A48DD63B4EBC3096C801613374', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 4, NULL, NULL, 2, NULL, 0, 'Gradually stir flour into the creamed mixture until well blended. Use a paddle attachment for the stand mixer, or mix by hand with the help of a wooden spoon.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D6E746C65C70F439E51507F24A4D9F9', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 5, NULL, NULL, 2, NULL, 0, 'Once the mixture is well incorporated, use a small offset spatula to spread or pat it into the bottom of an ungreased 13 x 9 x 2-inch baking pan. If you''d like a thicker shortbread, use a 9-inch square pan. Prick the dough all over with a fork. Alternatively, roll the dough out on a lightly floured surface and cut out with round or square cookie cutters.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B83BEDCED5FC1A4890371DD150845C75', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 6, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 40 to 45 minutes until just lightly browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2429AA887A2880478D297C8C43794D1A', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 7, NULL, NULL, 2, NULL, 0, 'Remove from the oven and immediately cut into squares or rectangles. Cool completely before removing from pan. If using, dust with powdered sugar.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255DDFB5FFE20A4997EF6D7EAA49049F', X'620C9547937FD145BFCEF93A3D489F2E', X'620C9547937FD145BFCEF93A3D489F2E', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC279F62D2A2543A020F4EB0C6BA19F', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67AC44C919C0BA4CA8EDDE2D68E9E629', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F/165 C/Gas Mark 3. Mix the pears with 1/2 cup of the sugar and let stand.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'079970467A137C488F2AB77BF460B1AF', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 2, NULL, NULL, 2, NULL, 0, 'Put butter in a 2-quart baking dish and place in the oven until melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A047A31DB796F4E85AA43556360203E', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 3, NULL, NULL, 2, NULL, 0, 'Combine the remaining sugar, flour, baking powder, cinnamon, and salt in a mixing bowl. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AC9D8699D6588449CDB92E3DE004DC3', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the milk and egg in a separate small mixing bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4339B0F9DDA9F24AB59065BF2C9CEFC1', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 5, NULL, NULL, 2, NULL, 0, 'Slowly combine the milk and egg mixture with the dry ingredients to make a batter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A1D7DF68E4A6E4F8C54A72BB9663059', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 6, NULL, NULL, 2, NULL, 0, 'Pour the batter over the melted butter. Do not stir.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6554056150A5E4B9C6C75592362AD6E', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 7, NULL, NULL, 2, NULL, 0, 'Spoon the sliced pears over the batter. Do not stir.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF8C49FCBA919B4D9F2D51F1A5E6FDE3', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 8, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 50 to 60 minutes, or until nicely browned and the pears are tender. If the topping appears to be over-browning, cover the dish with foil and continue baking until the pears are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA2E15AEA55E424CBDB63D763521A0E9', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 9, NULL, NULL, 2, NULL, 0, 'Remove the cobbler from the oven and set it on a rack to cool slightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'695863E7C9EDE842920934680B12D243', X'612B795E0F14E74185509B13AFD48095', X'612B795E0F14E74185509B13AFD48095', 10, NULL, NULL, 2, NULL, 0, 'Enjoy the cobbler hot or cold with a scoop of ice cream or whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF38360B2BCFF840AB4AB8D9162DF27F', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 0, NULL, NULL, 2, NULL, 0, 'With a wire whisk, combine flour, salt and baking powder.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65C52A20BE5A5E419B43AD0D0B5688C4', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 1, NULL, NULL, 2, NULL, 0, 'Whisk in eggs, milk and butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8269F316C027184CBEF3003A17C95686', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 2, NULL, NULL, 2, NULL, 0, 'Fold in peaches.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0849B65D34D8BA4CBB06252ED98C6BB7', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 3, NULL, NULL, 2, NULL, 0, 'Heat oil in fryer or pan to 375 degrees F.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE78B466850FB748B1A98C7399EDC35E', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 4, NULL, NULL, 2, NULL, 0, 'With a long-handled ladle, ladle about 1/4 -1/2 cup batter into hot oil. The batter will sink at first and then rise. Cook for about 2 - 3 minutes, depending on the size of fritters.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01C8401A44FA114E8E19A1305B993992', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 5, NULL, NULL, 2, NULL, 0, 'Remove fritters from oil with long-handled tongs. Quickly drain on paper towels or newspaper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D71C1AED81D8FB469796B1805D7CE102', X'70DE1D6E8E182041994166D911764C29', X'70DE1D6E8E182041994166D911764C29', 6, NULL, NULL, 2, NULL, 0, 'One by one, place each fritter in ?a bowl of sugar. Coat both sides. Place on serving plate. Serve warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED25B7C22407642BBB768688A5AB719', X'D108FA1EAE4F8843A0ED1D7FC541219B', X'D108FA1EAE4F8843A0ED1D7FC541219B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1E37B80896FBB4283EB210D3B705C7C', X'D108FA1EAE4F8843A0ED1D7FC541219B', X'D108FA1EAE4F8843A0ED1D7FC541219B', 1, NULL, NULL, 2, NULL, 0, 'Peel the apples, if desired. Core them and then dice them or slice them into thin, uniform wedges. Alternatively, slice the cored apples crosswise to make thin circles.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'486FAD419619DF42A0244000E8E89FA3', X'D108FA1EAE4F8843A0ED1D7FC541219B', X'D108FA1EAE4F8843A0ED1D7FC541219B', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter in a heavy skillet or sauté pan over medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3138906A1267D84583B0F547D1194770', X'D108FA1EAE4F8843A0ED1D7FC541219B', X'D108FA1EAE4F8843A0ED1D7FC541219B', 3, NULL, NULL, 2, NULL, 0, 'When the butter has melted and begins to foam, add the chopped or sliced apples, brown sugar, cinnamon, nutmeg, and 2 tablespoons of water.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3CC36449809E84CBE796A5FB6AAF6B8', X'D108FA1EAE4F8843A0ED1D7FC541219B', X'D108FA1EAE4F8843A0ED1D7FC541219B', 4, NULL, NULL, 2, NULL, 0, 'Cook for 15 to 20 minutes, stirring frequently, or until the apples are tender and the sugar mixture has become syrupy. Some varieties of apples hold their shape better than others. Add more water to the pan if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC8EBC3ACCF5C647ABABF3670133F6E5', X'D108FA1EAE4F8843A0ED1D7FC541219B', X'D108FA1EAE4F8843A0ED1D7FC541219B', 5, NULL, NULL, 2, NULL, 0, 'Serve over pancakes, ice cream, or inside of crepes, and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E284DFC9D8A1864BAC8A52171F2547BE', X'0F76DD0384634E4284ACD70C2D7431EE', X'0F76DD0384634E4284ACD70C2D7431EE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABD95063B6CB9740B186A16FB7E48372', X'0F76DD0384634E4284ACD70C2D7431EE', X'0F76DD0384634E4284ACD70C2D7431EE', 1, NULL, NULL, 2, NULL, 0, 'Place a mesh sieve over a bowl and, from the canned pineapple, strain the pineapple juice into the bowl. Set the juice aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69F2ABE2B09CB7449DFA60688DE0D1F7', X'0F76DD0384634E4284ACD70C2D7431EE', X'0F76DD0384634E4284ACD70C2D7431EE', 2, NULL, NULL, 2, NULL, 0, 'Measure 6 tablespoons (about 1/3 cup) of crushed pineapple into a small bowl and set aside. Refrigerate or freeze any remaining crushed pineapple and juice for another use, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE637718A5B94A4ABF57D8D77F8E1A4B', X'0F76DD0384634E4284ACD70C2D7431EE', X'0F76DD0384634E4284ACD70C2D7431EE', 3, NULL, NULL, 2, NULL, 0, 'Add butter to a large bowl. Using an electric mixer, cream the butter until smooth and pale. Gradually beat in the confectioners'' sugar. Continue beating until light and fluffy.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AB695B93F31224E808AB4205DA26F07', X'0F76DD0384634E4284ACD70C2D7431EE', X'0F76DD0384634E4284ACD70C2D7431EE', 4, NULL, NULL, 2, NULL, 0, 'Stir in the 6 tablespoons of crushed pineapple and just enough of the reserved pineapple juice to make it spreadable. Beat to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAA0F222858D56499B61D983DE2DC505', X'0F76DD0384634E4284ACD70C2D7431EE', X'0F76DD0384634E4284ACD70C2D7431EE', 5, NULL, NULL, 2, NULL, 0, 'Spread over cake layers or cupcakes. If desired, add more crushed pineapple and spoon the mixture over a Bundt cake.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAEF3219EDAE0C459E4678840C498AB4', X'74D1F0353C4124449EA6DFD509FE0B3F', X'74D1F0353C4124449EA6DFD509FE0B3F', 0, NULL, NULL, 2, NULL, 0, 'Lightly butter the slow cooker and then add bread cubes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72C430EDB8756B4C9E8434A03FDEED29', X'74D1F0353C4124449EA6DFD509FE0B3F', X'74D1F0353C4124449EA6DFD509FE0B3F', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the scalded milk, eggs, sugar, cinnamon, nutmeg, salt, vanilla, and melted butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50056BB302A5654AA7315E1E4DC9A062', X'74D1F0353C4124449EA6DFD509FE0B3F', X'74D1F0353C4124449EA6DFD509FE0B3F', 2, NULL, NULL, 2, NULL, 0, 'Pour milk mixture over the bread cubes then add raisins, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'390D3579C093FC4EB55B3D6D0716D407', X'74D1F0353C4124449EA6DFD509FE0B3F', X'74D1F0353C4124449EA6DFD509FE0B3F', 3, NULL, NULL, 2, NULL, 0, 'Using a large spoon or spatula, gently press bread cubes down into the mixture so all of the bread pieces are soaked with the milk and egg mixture. Do not stir.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C1C5EBBF5E98A46A3B3F8B8D8235396', X'74D1F0353C4124449EA6DFD509FE0B3F', X'74D1F0353C4124449EA6DFD509FE0B3F', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on the low setting for 5 to 6 hours or until the bread pudding is set.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13349F564D20BD44ACDB6C5744BED2CA', X'74D1F0353C4124449EA6DFD509FE0B3F', X'74D1F0353C4124449EA6DFD509FE0B3F', 5, NULL, NULL, 2, NULL, 0, 'Serve with a dessert sauce or whipped cream.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99BB4630F5C673428301150A28904701', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 300 F/150 C/Gas 2.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF05F5CB0BAD046820E3FBAB1D869C2', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter a shallow 2-quart baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F37DE92D4C3C34CA212AB73E3B764C2', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 2, NULL, NULL, 2, NULL, 0, 'Trim the crusts from the bread; butter them lightly with about 2 teaspoons of softened butter and arrange them in the baking dish. If the slices are smaller than a standard loaf size, trim extra slices so they will make a full layer in the bottom of the baking dish. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB225F66C95FCD4492C6D481146DB996', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 3, NULL, NULL, 2, NULL, 0, 'Heat the milk in a saucepan over low heat until hot, but not boiling.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2612019E56018047A9A7D446E4E35069', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, combine eggs, sugar, and salt; stir well.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECDC77AD1CEF044698B3DC11A3B2BFF4', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 5, NULL, NULL, 2, NULL, 0, 'While whisking briskly, gradually stir about one-quarter of the hot milk into the egg mixture. Add the remaining hot milk, stirring constantly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4425B5576EDE3149B047E590B9E23C61', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 6, NULL, NULL, 2, NULL, 0, 'Whisk the cinnamon and vanilla extract into the egg and milk mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D0858BF1E03FD46B3275D1CC78646CA', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the raisins over bread, if you are using them.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EC55BE8B9317A4EAE1C14E2E17E0BF9', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 8, NULL, NULL, 2, NULL, 0, 'Pour the milk mixture over bread.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D5A182CB7609140B99926D164502BE2', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle ground nutmeg lightly over the pudding mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFE1B49E0859A84EB6650484B7DCE170', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 10, NULL, NULL, 2, NULL, 0, 'Bake the bread pudding uncovered in the preheated oven for about 50 minutes, or until a knife inserted near the center comes out clean.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'661BF60743E3BA4EAA9F04492179174E', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 11, NULL, NULL, 2, NULL, 0, 'To brown the top slightly, turn the broiler on and continue to cook for a minute or two, or just until the top has browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5E72A6C7CA47C479A0050224907DA32', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', X'0E1C889B9EF8FE49B0DAFF0CD4DF4E04', 12, NULL, NULL, 2, NULL, 0, 'Serve warm with vanilla sauce, a caramel sauce, or purchased dessert sauce, as desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8662819CCB9B24884B94B5E10A29E3B', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 0, NULL, NULL, 2, NULL, 0, 'Generously grease and flour a 10-inch, one-piece tube cake pan or Bundt pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F24A2AE429C6C4B803DD04B81060FDA', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 1, NULL, NULL, 2, NULL, 0, 'Put 3/4 cup of undrained pineapple with its juice in a small bowl; set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2B3067A7D8F764096C68D789C6A36EE', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 2, NULL, NULL, 2, NULL, 0, 'Drain remaining pineapple and measure 1 cup of drained crushed pineapple; set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2277025F4BA854FBE7F757856839072', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 3, NULL, NULL, 2, NULL, 0, 'In a mixing bowl with an electric mixer, beat the shortening and 1 cup of butter until smooth, about 2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A74313BFB422E24AA1F1E1755C9C8C17', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 4, NULL, NULL, 2, NULL, 0, 'Gradually beat in the sugar and continue beating until the mixture is light and fluffy, or about 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F1D7FCC804FB5438DE9403CE8406F6C', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 5, NULL, NULL, 2, NULL, 0, 'Add the eggs, one at a time, beating after each addition. Do not overbeat.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD93ACAF1E65F648BF7F2F8D08DA0E2A', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 6, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine the flour and baking powder and blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EED7B06218177E4C9A4A16A065D43130', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 7, NULL, NULL, 2, NULL, 0, 'Add one-third of the dry mixture to the creamed mixture and about half of the milk; blend well. Repeat with another third of the flour mixture and the remaining milk; blend well. Beat in the remaining flour mixture just until well blended.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B090C5E853B1754A87E7DA3ABDF4BDE0', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 8, NULL, NULL, 2, NULL, 0, 'Add the vanilla extract and the reserved 3/4 cup of undrained pineapple with juice; stir to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D66BCDD2E2A9EE48B116E0A3570C999F', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 9, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared tube cake pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BF2545D65089141AE21D0DA92EEAADE', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 10, NULL, NULL, 2, NULL, 0, 'Place the cake in the unheated oven. Turn on the oven to 325 F and bake for 1 1/2 hours (do not preheat), or until a long wooden pick comes out clean when inserted into the center.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AAD66162C59174D962B1EA787E36B76', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 11, NULL, NULL, 2, NULL, 0, 'Let the cake stand for about 5 minutes in the pan on a rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2946BBD6819FA4CA737004C7D0187E6', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 12, NULL, NULL, 2, NULL, 0, 'Run a knife around the edge of the pan to loosen and then carefully remove the cake to a rack.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BE2D1A60961244487888C1980E1C410', X'17E72F71134EBD41B9734F421E11DF65', X'17E72F71134EBD41B9734F421E11DF65', 13, NULL, NULL, 2, NULL, 0, 'Combine 1/4 cup of butter, the confectioners'' sugar, and the remaining 1 cup drained pineapple. Pour the pineapple sauce over the cake while it is still hot.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE175C2A3A419A44BA5EA0BAA1CE5FFA', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA3FB8FA79D6644A13748C202DBB348', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 1, NULL, NULL, 2, NULL, 0, 'Combine the 2 1/2 cups of flour, 2 teaspoons of sugar, and 3/4 teaspoon of salt in a food processor. Pulse to blend thoroughly. Add half of the butter pieces and pulse 6 times. Add the remaining butter and pulse 6 to 8 more times. The mixture should look crumbly with larger pea-size pieces of butter visible.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1ADF46C1EA4BB4BB09EE8F7EE485901', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 2, NULL, NULL, 2, NULL, 0, 'Add about 7 tablespoons of ice water and pulse a few times. Add more water, about 1 teaspoon at a time, until the dough is moist enough to hold together when squeezed.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'119A03BA4050C541AA2543EEDF10BDB9', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 3, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a floured surface. Press the dough together and shape it into 2 flattened discs. Do not knead more than necessary to bring it together into a dough. Wrap the discs in plastic wrap and refrigerate for about 30 to 45 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C903CB7D7DF8A4B87469711F29739B6', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 4, NULL, NULL, 2, NULL, 0, 'Take one disc of dough out of the refrigerator. On a lightly floured surface, roll the dough into a circle about 2 inches larger than the upside-down pie plate. Fit the dough into the pie plate and leave the overhang.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072510344BDCFF4DA1420C8B28EBD348', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 5, NULL, NULL, 2, NULL, 0, 'Peel the pears and cut them in half lengthwise. Remove the cores and fibers and slice them thinly. Toss them in a bowl with the lemon zest and juice.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7BFB49A1348484498AE98F03845AC3E', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 6, NULL, NULL, 2, NULL, 0, 'Turn the sliced pears into the pie crust. Combine 1 cup of sugar, 1/4 cup of flour, cinnamon, and 1/8 teaspoon of salt; sprinkle over the pears. Dot with the cut-up 2 tablespoons of butter.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BD9925B8152F24FBC51068BD9461289', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 7, NULL, NULL, 2, NULL, 0, 'Remove the remaining disc of dough from the refrigerator. On the lightly floured surface, roll it out and cut it into strips about 3/4-inch in width. Arrange five dough strips across the top of the filling. Weave the strips in a criss-cross pattern over the top of the pie. Fold every other strip back and place a new strip at a right angle to the first set.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2A4C344BB85504EB0E805D7742C9D3F', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 8, NULL, NULL, 2, NULL, 0, 'To create the latticework, unfold the strips you folded and fold the alternate strips. Place another new strip over the strips and unfold the folded strips again. Repeat until you have covered the pie with `woven` strips of dough. Cut any long ends and wrap the overhang over the ends of the strips. Flute as desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38D62CA6C155604CA8F921626DB28EFA', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 9, NULL, NULL, 2, NULL, 0, 'Combine the egg and 1 tablespoon of milk and whisk until well blended. Brush the egg wash lightly over the lattice strips. Avoid the edge for now. Set the egg wash aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B182D7B52D3DD4CAC6A42557BF7463A', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 10, NULL, NULL, 2, NULL, 0, 'Cover the fluted edge of pie with a strip of foil or pie shield.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C90BE261281E6C4D9C4CBC19578D78AA', X'C5649F7B64483543AEE20E519E26A07F', X'C5649F7B64483543AEE20E519E26A07F', 11, NULL, NULL, 2, NULL, 0, 'Bake the pie at 425 F for 15 minutes. Remove pie shield and brush the egg wash over the crust edge. Reduce the oven temperature to 375 and bake the pie for about 20 to 25 minutes longer, or until the pears are tender and the crust is golden brown.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D2C84640F70A140AA0737FF81B99F66', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91D1630778BDF142BB17CBC9D7EC1814', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13BB26636E7CC0409B6F931F24B81E6C', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 2, NULL, NULL, 2, NULL, 0, 'Grease and flour an 8-inch square baking pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46834FEA7439914EB9DA83EAAA4F7A4E', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 3, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the flour, sugar, cocoa, soda, and salt. Whisk or stir to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98B7AA6E0A2F0E4EB84E2BE03A93657E', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 4, NULL, NULL, 2, NULL, 0, 'Make three wells in the flour mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22E7125C8F03C14FADDE66CF9C058CCA', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 5, NULL, NULL, 2, NULL, 0, 'In one put the vanilla; in another the vinegar, and in the third the vegetable oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04660962495655409E181A59213A91B1', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 6, NULL, NULL, 2, NULL, 0, 'Pour the cold water over the mixture and stir until moistened.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF08AF46633BD940941C1346DC505196', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 7, NULL, NULL, 2, NULL, 0, 'Pour the batter into the prepared pan and bake for 25 to 30 minutes, or until it springs back when touched lightly.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20EDE52E5A401841B3E2A15A9753308C', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 8, NULL, NULL, 2, NULL, 0, 'Frost with chocolate frosting or your favorite icing or glaze. Or sift powdered sugar over the cake.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9065C3FD99FF42BE11F81ECCB76E64', X'4F9F0C0B1FFB8145944166442229A905', X'4F9F0C0B1FFB8145944166442229A905', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'354EBFF109A1A948817095C4EB97CD6F', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C60A475BFC73345A8EF60654DBF51DA', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 1, NULL, NULL, 2, NULL, 0, 'Line a baking pan or tray with wax paper or parchment paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'011691165F138E40B25108C3E4C71A7B', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 2, NULL, NULL, 2, NULL, 0, 'Sift the confectioners'' sugar into a large bowl and add the sweetened condensed milk, coconut, melted butter, and pecans. Mix well with the help of a wooden spoon. Refrigerate for 30 minutes, covered.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC432D5CF1FE8E4DB4608DBCD59CF1A0', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 3, NULL, NULL, 2, NULL, 0, 'With your hands, shape the mixture into balls and place them on the pan or tray. Refrigerate the balls until the balls are very firm, or about 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F906EB18CED83F40A2EC8A44636E9471', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 4, NULL, NULL, 2, NULL, 0, 'Once the candy is hard, melt the wax and milk chocolate chips together in a double boiler. Make sure the water at the bottom is simmering and not boiling.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03B201E7AA9C504A8481260DB23A3E45', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 5, NULL, NULL, 2, NULL, 0, 'Dip the chilled balls in the melted chocolate and let cool on top of wax paper.');
INSERT INTO `VWR INTERNAL AmericanRecipes2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2C5C46FCA4F2F4885FA97D6E3EBCECF', X'1F037AA2AA9789488A08175E20D6C873', X'1F037AA2AA9789488A08175E20D6C873', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
