INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0011CA6139C2FF4CB96F889982C683C8', X'602A7DAE97858A41AA6CBD46197E4F3B', X'602A7DAE97858A41AA6CBD46197E4F3B', 3, NULL, NULL, 2, NULL, 0, 'Rub the chicken with the rub mixture. Make sure to get the rub under the skin, on top of the skin and on the underside of the chicken. Cover the chicken with plastic wrap and refrigerate for at least 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43856872FFB91E4382DE73137216B49C', X'602A7DAE97858A41AA6CBD46197E4F3B', X'602A7DAE97858A41AA6CBD46197E4F3B', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill or smoker to 180 F. Prepare your wood chips. If you are using a regular grill, place a pan of damp wood chips in a disposable pan under one side of the grill rack. Place the chicken, skin side up on the opposite side of the grill. This will “smoke” the meat for you without having a smoker.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42A36C6D3293B249AA575A7B35C3BCD4', X'602A7DAE97858A41AA6CBD46197E4F3B', X'602A7DAE97858A41AA6CBD46197E4F3B', 5, NULL, NULL, 2, NULL, 0, 'While the chicken is cooking for the first 15 minutes, prepare the glaze. Add the jam, ketchup, peaches, brown sugar, molasses, whiskey, apple cider vinegar, garlic powder, and hoison sauce into a small saucepan. Stir to combine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D91DA62C68810E4D8AE178D56F8A2208', X'602A7DAE97858A41AA6CBD46197E4F3B', X'602A7DAE97858A41AA6CBD46197E4F3B', 6, NULL, NULL, 2, NULL, 0, 'Cook on medium-low heat for 8 to 10 minutes, stirring frequently until the sauce is thickened. It should be quite thick and sticky.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0201E2424AE11944BDC6C206EB5728D1', X'602A7DAE97858A41AA6CBD46197E4F3B', X'602A7DAE97858A41AA6CBD46197E4F3B', 7, NULL, NULL, 2, NULL, 0, 'Increase the grill or smoker temperature to 375 F. If you are using a grill or pellet grill, you can flip the chicken to sear the skin as well. This will make it extra crispy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'217245C1ECBFEF418B4EBC018A198A92', X'602A7DAE97858A41AA6CBD46197E4F3B', X'602A7DAE97858A41AA6CBD46197E4F3B', 8, NULL, NULL, 2, NULL, 0, 'When the chicken is at about 160 F internally, you can brush the glaze over the top of the chicken skin. Finish cooking until the chicken reaches the internal temperature of 165 F, about 10 to 15 minutes. Serve immediately or enjoy cold!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66066BD4EB413F4797E215ACCE9936BD', X'72D1E4E39EE2D44E977FB224022B5B97', X'72D1E4E39EE2D44E977FB224022B5B97', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4440A828DC9932459A5B40B142EC67FD', X'72D1E4E39EE2D44E977FB224022B5B97', X'72D1E4E39EE2D44E977FB224022B5B97', 1, NULL, NULL, 2, NULL, 0, 'Heat up a medium saucepan on medium-low. Add all of the ingredients, but just 1/4 teaspoon of cayenne powder. Allow the mixture to simmer for 5 to 8 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F65B68460996A5418EAD214F5CBB1CCD', X'72D1E4E39EE2D44E977FB224022B5B97', X'72D1E4E39EE2D44E977FB224022B5B97', 2, NULL, NULL, 2, NULL, 0, 'Taste for flavor and add the remaining 1/4 teaspoon of cayenne if you''d like a spicier sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43401970A9DE00429CF844C4BE484CDE', X'72D1E4E39EE2D44E977FB224022B5B97', X'72D1E4E39EE2D44E977FB224022B5B97', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to cool completely before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D59687B88970B4F801F5E5251F99BB1', X'72D1E4E39EE2D44E977FB224022B5B97', X'72D1E4E39EE2D44E977FB224022B5B97', 4, NULL, NULL, 2, NULL, 0, 'Store covered in the refrigerator for up to a week.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98AF97F566A2324DB48F2E86F2EC7C1F', X'72D1E4E39EE2D44E977FB224022B5B97', X'72D1E4E39EE2D44E977FB224022B5B97', 5, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3D58E4CBA2814998922AED063113AE', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D275CBD5BFA64C44A81F5470763CF4CB', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 1, NULL, NULL, 2, NULL, 0, 'Rinse the lemons clean. If you have any plans to use or otherwise eat the peel, you may even want to go so far as to scrub them with soap before rinsing them. Pat them dry. To release extra juice, roll them on a hard surface.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2430B65A61D0A24091594313AB5507E5', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 2, NULL, NULL, 2, NULL, 0, 'Cut the lemons in half crosswise.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B96102BAEE84AB40BDEE6CAB44E34673', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 3, NULL, NULL, 2, NULL, 0, 'Optional: Pop out and discard any visible seeds. Use the tip of a paring knife to urge any deeper-set seeds to the surface. This is a kind service to your fellow diners since it minimizes the chance that they''ll bite into a bitter seed while enjoying their lovely grilled lemons.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FE981F37DCA7347A19E32887783A562', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 4, NULL, NULL, 2, NULL, 0, 'Use a pastry brush, silicone grilling brush, or a paper towel to brush the cut sides with a bit of oil. You can sprinkle the cut sides with fine sea salt, too, if you want to add a more intense savory effect to the grilled lemons.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'070F3CD2DA629744B30F26812BB23320', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 5, NULL, NULL, 2, NULL, 0, 'Set the lemons cut-side-down on the hot grill or in the frying pan. Cook until the lemons are heated through and charred on the cut side, about 3 minutes. Ideally, you''ll cook them without moving them so they char nicely, but adjust them if the heat seems uneven so they char evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B3453F1871285498B99A394A5BB24D9', X'8FC777C35D8C6540BAB0FA56E95FB2B3', X'8FC777C35D8C6540BAB0FA56E95FB2B3', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94AFDDCB8311214780D6A9A69FE1B429', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B64705292BD60D4F9494C68FCFB5B918', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 1, NULL, NULL, 2, NULL, 0, 'If you choose to brine your chicken, combine the salt, sugar, and water in a plastic bowl and stir until the salt and sugar are completely dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DB689DDE39FCE48A86A8CA9E5EA2D40', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 2, NULL, NULL, 2, NULL, 0, 'Apply a spice rub under the skin of the breast and in the chicken cavity. The rub can be almost any combination of herbs and spices, but if you have brined your chicken, it already has all the salt it needs, so avoid a rub with extra salt. If you have skipped the brining step, use a rub that contains salt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A26E1B944FBDCA4DA39528BCE77F17DD', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 3, NULL, NULL, 2, NULL, 0, 'Set up your full-size charcoal grill for smoking. The trick is to separate the fire from the food. To do this, light enough charcoal to cover half the coal grate with two layers of burning coals. It is best to light the charcoal with a charcoal chimney; this allows you to light more charcoal outside the grill if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4766719F259804390F6EF438D90F32F', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 4, NULL, NULL, 2, NULL, 0, 'Place one of the aluminum pans on the coal grate next to the burning coals, but not on the coals. This is the drip pan that helps to keep the grill clean and prevents any dripping fat from reaching the fire which can cause flare-ups.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC9EA79C59AC2D49859DDD9509DEE5D5', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 5, NULL, NULL, 2, NULL, 0, 'Heat the grill to 250 F to 275 F/120 C to 135 C and then adjust the vents on the grill to hold that temperature. While the grill is heating up, take the chicken out of the refrigerator to come to room temperature. Once the grill is ready, place the chicken, breast-side up, on the grill grates directly over the drip pan on the coals and next to the pan of water on the grate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C37A2ED21E2B1648AD87DB56BB8D59CB', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 6, NULL, NULL, 2, NULL, 0, 'Replace the grill lid and let the chicken cook for about 1 hour to 90 minutes. It is important to keep an eye on the grill to ensure that the fire is still burning and that the temperature is being maintained.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C629771BAB1AAB4AA5C15D99AF18C183', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 7, NULL, NULL, 2, NULL, 0, 'After about 90 minutes, it is time to flip and turn the chicken. Since our makeshift smoker won''t cook evenly, it is important to do this halfway through the cooking time. Flip the chicken upside down, placing the side that was away from the fire close to the fire. This should make for an evenly cooked chicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE704981FA0E6349BFCC3ABA7731684F', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 8, NULL, NULL, 2, NULL, 0, 'The total cooking time is about 3 hours depending on the size of the chicken. As the chicken gets close to being done, it is time to start checking the temperature. Make sure to use a reliable meat thermometer; a high-quality digital thermometer is recommended. Check the center of each breast as well as the thickest part of each thigh. If the part away from the fire is lower in temperature, you might want to rotate the chicken again. Look for a temperature around 185 F/85 C. Chicken is cooked at 165 F/75 C, but it is best to overcook the chicken to make it easier to shred. Generally, any type of barbecue is overcooked to maximize tenderness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B20023827F10384B8CEA784AF0815428', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 9, NULL, NULL, 2, NULL, 0, 'After the chicken has rested for 15 minutes, it''s time to start shredding the meat. This is best done with a pair of insulated food gloves so the hot chicken can be easily handled. It is something of a messy job, so a large, clean workspace is needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E159974E8EF3E64AA07BCE4A54DBBDEE', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 10, NULL, NULL, 2, NULL, 0, 'There is no trick to shredding the meat, so you may need to try out a few techniques before you find what works for you. Start by pulling off the skin and breaking down the larger pieces of meat, removing bones and fat as you go. Once you have the pile of meat, taking a portion at a time, use two forks to shred. Either pull each fork away from each other or use one to hold the meat and the other to pull away, shredding the chicken meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'163194E125B3274998F13AA3466BADF4', X'7FAFB4EF4AACA9499752637AF70B64A2', X'7FAFB4EF4AACA9499752637AF70B64A2', 11, NULL, NULL, 2, NULL, 0, 'To keep the meat warm, place it in a large pot over a low flame, tossing occasionally, or if you are going to be eating later, a slow cooker on the warm setting works well. Once the shredded chicken is warmed, add your desired barbecue sauce, tossing to evenly coat. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBD2A7A1137C3B49919D01A41F3020F7', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E8636AE847CD34C94CAF89773D68B1B', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas or charcoal grill to medium-high heat (you should be able to hold your hand about 1 inch above the cooking grate for 3 to 4 seconds before pulling it away from the intensity of the heat).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9EE3F635BEE474E918D824767458B8A', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, trim off and discard any wilted greens at the top of the spring onions. Cut off and discard the root end of the onions as well. Be careful to cut right at the bottom of the onion and the top of the roots so the bulb stays together and you can cook and serve the onions intact.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE39C78320CB204AB77059F3EB41BA81', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 3, NULL, NULL, 2, NULL, 0, 'Brush the spring onions with olive oil or vegetable oil. Be liberal with the application of the oil — it prevents the onions from sticking to the hot grill and adds tons of flavor as it mixes with the juices from the onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'869FC9549FAD62408AA2E5ADEBEFEF21', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 4, NULL, NULL, 2, NULL, 0, 'Put the spring onions on the preheated grill and cook them, turning once, until grill marks appear on both sides and the spring onions feel fork tender all the way through, about 15 minutes total cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3BF161BE82A5946B7A96E1FD4F35CDE', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the grilled spring onions with salt and freshly ground black pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB125EC6DE509F48B8361C3616EC72F6', X'36C93996864EC4408DAA324C175337B7', X'36C93996864EC4408DAA324C175337B7', 6, NULL, NULL, 2, NULL, 0, 'Spritz the onions with fresh lemon juice or serve the lemon wedges on the side so everyone can dress their own plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFDD89AE14CAA8418FDE12C27F526147', X'F8C709D662764241A5B16B63C560E2FA', X'F8C709D662764241A5B16B63C560E2FA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8BC3188261E9B4DAAD6A5A3C96DF08F', X'F8C709D662764241A5B16B63C560E2FA', X'F8C709D662764241A5B16B63C560E2FA', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'793CB6355FA2DF499B760C2D87EEA171', X'F8C709D662764241A5B16B63C560E2FA', X'F8C709D662764241A5B16B63C560E2FA', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the chicken pieces liberally with salt, pepper, and garlic powder, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CC0814B002F01479A5B98B2103ADF24', X'F8C709D662764241A5B16B63C560E2FA', X'F8C709D662764241A5B16B63C560E2FA', 3, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine barbecue sauce ingredients, blending well.');
