INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B363E6BE427B9D41A2AA226DEBF3591F', X'F8C709D662764241A5B16B63C560E2FA', X'F8C709D662764241A5B16B63C560E2FA', 10, NULL, NULL, 2, NULL, 0, 'Broil briefly for a few minutes in the end, if you''d like crispier skin.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07C8CDB15AD31C43909150C4E9EADA46', X'F8C709D662764241A5B16B63C560E2FA', X'F8C709D662764241A5B16B63C560E2FA', 11, NULL, NULL, 2, NULL, 0, 'Serve with your choice of sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'370DBC60D36E4D4189EA71534134A29F', X'C25A6BBFBCD2AE40986605A66D59AD8D', X'C25A6BBFBCD2AE40986605A66D59AD8D', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the marinade ingredients in a large bowl or a plastic bag. Add the chicken to the marinade, cover and let sit in the refrigerator for at least 20 minutes and no more than 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1840F752C3BC744D9C760F176916E3D3', X'C25A6BBFBCD2AE40986605A66D59AD8D', X'C25A6BBFBCD2AE40986605A66D59AD8D', 1, NULL, NULL, 2, NULL, 0, 'While the chicken is marinating, prepare the pickled vegetables. Add the salt and sugar to the warm water and stir until everything has dissolved. Add in the vinegar and stir. Place the carrots and radishes in a large bowl and pour the vinegar mixture over the top Cover and place in the refrigerator for at least 20 minutes and up to 1 week.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEC3F2BA16AB9F4BACE1160339519DF8', X'C25A6BBFBCD2AE40986605A66D59AD8D', X'C25A6BBFBCD2AE40986605A66D59AD8D', 2, NULL, NULL, 2, NULL, 0, 'Heat your grill or grill pan on medium-high heat. Add the marinated chicken and cook for about 4 minutes per side or until completely cooked through. Remove the chicken from the heat and set aside until you''re ready to assemble the sandwiches.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E70D567971B9944AB8138027447DC5B', X'C25A6BBFBCD2AE40986605A66D59AD8D', X'C25A6BBFBCD2AE40986605A66D59AD8D', 3, NULL, NULL, 2, NULL, 0, 'Split the bread, but don''t cut it all the way through, leaving a hinge. Toast the baguettes if you wish, then slather one side with mayo and the other with the pate. Add the chicken as the first layer, then top with the pickled vegetables, jalapenos, cilantro, and cucumber. Cut in half and serve with more mayo, Sriracha, and extra toppings! Dig in immediately for best results.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCB9AD217F3B2547A17B927DB21F6094', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', 0, NULL, NULL, 2, NULL, 0, 'Place chicken in the slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F273B1C5FAF1144B8177E370F2A19C20', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', 1, NULL, NULL, 2, NULL, 0, 'Add the sliced onion, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2945F9647F4B964EBE7612D575A20F37', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', 2, NULL, NULL, 2, NULL, 0, 'Pour barbecue sauce over the chicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7948A696B2905E4E979F61378463AFD5', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', X'6A8CD4A8CBC9844A89A74679EF4B2DF0', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 6 to 8 hours or until the chicken is fully cooked');
