INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01E75A288A43234DABF136579BECBE97', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B93E127B78EF324ABA43458789553CC4', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 1, NULL, NULL, 2, NULL, 0, 'To make the brine, combine salt, white sugar, and hot water in a large plastic or glass container that you can cover. Stir to mix. Add cold water and cayenne, if using. Continue stirring to dissolve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCEAC5B4A9F14548B9F95A3EAAD6E217', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 2, NULL, NULL, 2, NULL, 0, 'Add chicken drumsticks, cover, and refrigerate for 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ACBEE05B507F8459B28EADE01E34DCA', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, gather the ingredients for the sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85009188EA848C4889CD8CC3443A0899', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 4, NULL, NULL, 2, NULL, 0, 'Make barbecue sauce by combining ingredients in a medium saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'078586CAA806AE4381A66B0F2C6DE5AB', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 5, NULL, NULL, 2, NULL, 0, 'Simmer over low heat for 15 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64CB245742F7A54491BD82FDCD03C932', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and allow to cool for 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D69BBF58C97BA43878E0EDEA8F0B995', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 7, NULL, NULL, 2, NULL, 0, 'Preheat grill to a medium-low heat (around 275 F/135 C).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA89E5CD603FF548A5DA837036DF75F5', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 8, NULL, NULL, 2, NULL, 0, 'Remove the chicken legs from the brine and discard brine. Rinse chicken thoroughly to remove all traces of the brine from the surface. Pat dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36DC4E02ED718A4C9CCC0F424011557B', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 9, NULL, NULL, 2, NULL, 0, 'Grill chicken legs for about 30 to 35 minutes, turning often (about every 5 minutes) to make sure all sides are cooked evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D591985DB7DD1340948C42BA1DF417A0', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 10, NULL, NULL, 2, NULL, 0, 'During the last 15 to 20 minutes, start brushing sauce over the surface every 5 minutes, rotating the chicken to coat evenly, until the chicken is done (an internal temperature of should be at least 165 F/75 C, but you may want to get closer to 180 F/82 C), and the sauce has cooked down to a sticky consistency.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAB84FB580632C4493C60D9F94D97926', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 11, NULL, NULL, 2, NULL, 0, 'Remove chicken from heat, place on a large serving platter, and tent loosely with aluminum foil. Let drumsticks rest for 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA15F51550015449B5BDC767DF0EAF66', X'A3DEF36150EE4E4D80F1D85697AF3C72', X'A3DEF36150EE4E4D80F1D85697AF3C72', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE84537BF369564F9A49726C77C5AD62', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 0, NULL, NULL, 2, NULL, 0, 'Split each chicken breast so that you have four halves. Place the chicken breasts in a large bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F2D064AF55AB640AB6561D7306E7EFC', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the lime juice, seasoned salt, pepper, and olive oil. Pour it over the chicken. Cover the chicken and let marinade for at least 20 minutes or up to 2 hours in the refrigerator. Any longer will cause the lime juice to `cook` the chicken (i.e. ceviche) so don''t let it marinade for too long!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31274AA69B765E42803127F5CE96A063', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 2, NULL, NULL, 2, NULL, 0, 'Heat up your grill or grill pan to medium-high heat so that you will get nice grill marks. Grill the chicken for about 5 minutes on each side or until cooked through. Remove from the heat and allow to rest for about 5 minutes. Then slice into narrow strips.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C035110D238764389BBEC1500C2BA62', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 3, NULL, NULL, 2, NULL, 0, 'Toss the strips with the barbecue sauce in a medium-sized bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D30C0EDD0BD3C4AB9D5955D4E898A34', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 4, NULL, NULL, 2, NULL, 0, 'Heat up a cast iron skillet or griddle to medium-low heat. Quesadillas can burn easily, so monitor the temperature closely. Place a tortilla on the pan, top with about 1/4 of the cheese, some of the red onion and about 1/4 of the chicken. Place another tortilla on top. Cover with a lid to help allow the cheese to melt.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21FF1EAEBD04184A8A3BFC2D89DE06CE', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 5, NULL, NULL, 2, NULL, 0, 'Flip the quesadilla and finish cooking for a minute or two on the other side. Repeat with the rest of the quesadillas. You can keep them warm in a 200 F oven while you are cooking the rest of the quesadillas.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2421C4680F8CAB4D816D09ABECF7C48A', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', X'AC17B9A7A343AA45BE2C8DF7CF58A50B', 6, NULL, NULL, 2, NULL, 0, 'Cut each quesadilla into quarters. Garnish with cilantro leaves, more barbecue sauce, and sour cream. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'829AAF0E469D0E43AB75AB530BEA198B', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3CB5E18784C1C4DA7E73DDD3D952E1F', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 1, NULL, NULL, 2, NULL, 0, 'In a large resealable food storage bag (plastic), combine the oil, vinegar, sugar, ketchup, onion, Worcestershire sauce, salt, garlic, and Tabasco. Add the chicken and turn to coat thoroughly. Seal the bag and refrigerate for at least 4 hours or overnight, turning occasionally.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2029E02F7736748A44B4C04A22200E6', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 2, NULL, NULL, 2, NULL, 0, 'Pour marinade into a saucepan and bring to a full rolling boil over high heat. Remove from the heat and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'056259756D56CF42AEE28238964FFDDE', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 3, NULL, NULL, 2, NULL, 0, 'Heat the grill to moderate or medium-high heat, about 350 F to 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'377420EBD9FA9E40B7EF58225D4B1EB0', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 4, NULL, NULL, 2, NULL, 0, 'First, sear the chicken over direct heat for about 2 to 3 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E47F0BB5F71C3498E4FB576C65B178F', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 5, NULL, NULL, 2, NULL, 0, 'Continue grilling the chicken leg quarters over indirect heat -- not directly over coals or gas burners -- turning frequently and basting with the reserved boiled marinade, for about 35 to 55 minutes, depending on size.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1226FE3EE3992E4FBDB6015676D499C9', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 6, NULL, NULL, 2, NULL, 0, 'The chicken should be fork tender and juices will run clear. The temperature should register at least 165 F on an instant-read thermometer inserted into the thickest part of the meat but not touching bone.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBF8DE8BD6908C4B84F71D22D297508D', X'8E54413700D2AA49BC267EFD42E29709', X'8E54413700D2AA49BC267EFD42E29709', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF78950B3F4140448E669D417889BF76', X'169A101C56AC064F8E8A6DA6036DA397', X'169A101C56AC064F8E8A6DA6036DA397', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71141D61C49AE145926AEA366E839270', X'169A101C56AC064F8E8A6DA6036DA397', X'169A101C56AC064F8E8A6DA6036DA397', 1, NULL, NULL, 2, NULL, 0, 'If necessary, cut whole wings into two pieces, discarding the tips. Cover two baking sheets with heavy-duty foil and spray lightly with non-stick oil. Season the wings with salt and spread out evenly on baking pans. Bake for about 20 minutes, remove and turn the wings over; cook for another 15 to 20 minutes until the wings are cooked through and well-browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A23B015E64FBBE4EA5283C447F345E89', X'169A101C56AC064F8E8A6DA6036DA397', X'169A101C56AC064F8E8A6DA6036DA397', 2, NULL, NULL, 2, NULL, 0, 'While the chicken wings are baking mix the rest of the ingredients in a large mixing bowl. Taste sauce and add more spice if so desired. After the wings are cooked, transfer them to the bowl (use tongs or slotted spatula, so the grease stays in the pan). Toss with a spatula to completely coat. Let sit for 3 minutes and toss again, repeat several times. The chicken wings will soak in the sauce as they rest, and the glaze will slightly thicken as it cools.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED6CFF1043608A46B687182AB3C141F3', X'169A101C56AC064F8E8A6DA6036DA397', X'169A101C56AC064F8E8A6DA6036DA397', 3, NULL, NULL, 2, NULL, 0, 'Toss well one last time and transfer to a serving platter. The wings should be well-coated with sauce, but not drippy. If you want, you can keep the wings in a warm oven at 200 F for 15 minutes for a `drier` chicken wing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC434576A50F844DA9D361A8FB10FF9A', X'169A101C56AC064F8E8A6DA6036DA397', X'169A101C56AC064F8E8A6DA6036DA397', 4, NULL, NULL, 2, NULL, 0, 'Can be served warm or room temp.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19EB286AA3570E478991337FBF346878', X'19C0A71B6A10B540ADB570FB26664BD7', X'19C0A71B6A10B540ADB570FB26664BD7', 0, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together in a saucepan and let simmer for 10 minutes. Remove from the heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'306CAA5A9F74A24B9FA71A74D3B63E91', X'19C0A71B6A10B540ADB570FB26664BD7', X'19C0A71B6A10B540ADB570FB26664BD7', 1, NULL, NULL, 2, NULL, 0, 'Once the chicken is cooked, shred the chicken and add the sauce to coat it. You can vary the amount of barbecue sauce added to your taste.');
