INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCF854EE99841B4FBB1E7DD64700ECAE', X'DE3DB612C6F46846800F97ED359844E3', X'DE3DB612C6F46846800F97ED359844E3', 4, NULL, NULL, 2, NULL, 0, 'Pour remaining half of mixture into the bag with wings, coat well, seal bag, and allow it to marinate in the refrigerator for 2 to 4 hours.');
