INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'819F36C49CE53341A565C6BD6FB65447', X'DE3DB612C6F46846800F97ED359844E3', X'DE3DB612C6F46846800F97ED359844E3', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and let cool to room temperature. If the mixture becomes too thick, add a small bit of water (1 to 2 tablespoons).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4091AACA791304BACFFF1F45C795EB3', X'DE3DB612C6F46846800F97ED359844E3', X'DE3DB612C6F46846800F97ED359844E3', 7, NULL, NULL, 2, NULL, 0, 'Preheat grill for direct medium heat.');
