INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E920F66108F564482AFA2B62340C4B3', X'DE3DB612C6F46846800F97ED359844E3', X'DE3DB612C6F46846800F97ED359844E3', 10, NULL, NULL, 2, NULL, 0, 'Make sure to watch for flare-ups.');
