INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2077EAE4C000B043A1ACB6C642F21342', X'457392831B5CEB42B49067782F7AE598', X'457392831B5CEB42B49067782F7AE598', 0, NULL, NULL, 2, NULL, 0, 'Place skinless quartered chicken in a shallow glass dish.');
