INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4279AB6F9BD374B9FCC7B82EC42F00B', X'457392831B5CEB42B49067782F7AE598', X'457392831B5CEB42B49067782F7AE598', 2, NULL, NULL, 2, NULL, 0, 'Pour mixture onto chicken. Cover dish and allow to marinate in the refrigerator for 8 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A061EAE755A8B94CAE6F7958D09A8E89', X'457392831B5CEB42B49067782F7AE598', X'457392831B5CEB42B49067782F7AE598', 3, NULL, NULL, 2, NULL, 0, 'Remove chicken from refrigerator and allow to stand for 20 to 30 minutes.');
