INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12C795C4F6DE2843BB5A75DBCF14F48D', X'457392831B5CEB42B49067782F7AE598', X'457392831B5CEB42B49067782F7AE598', 5, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88E368CE60C8FC42B8CA047D8D265CC5', X'457392831B5CEB42B49067782F7AE598', X'457392831B5CEB42B49067782F7AE598', 6, NULL, NULL, 2, NULL, 0, 'Place chicken on lightly oiled grill rack and cook for 35 to 45 minutes, turning chicken and basting with melted butter often.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E8B7594B1E46C488122859B511CC6C7', X'457392831B5CEB42B49067782F7AE598', X'457392831B5CEB42B49067782F7AE598', 7, NULL, NULL, 2, NULL, 0, 'When chicken is fully cooked (or reaches an internal temperature of 165 F in multiple spots), remove from grill and serve with remaining melted butter on top.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7914B501F2B9B4698A1984C52D0B1B0', X'7CF73C9647B6DC458B61C211EB4598D3', X'7CF73C9647B6DC458B61C211EB4598D3', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill. Combine butter, onion, garlic and curry powder. Mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F113F613F9EE441B7E7E971BB6416BC', X'7CF73C9647B6DC458B61C211EB4598D3', X'7CF73C9647B6DC458B61C211EB4598D3', 1, NULL, NULL, 2, NULL, 0, 'Coat chicken thighs with vegetable oil and season with salt and pepper. Apply about 1 tablespoon of curry butter mixture under the skin of each chicken thigh.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA5E8F4EE5460D40BA7B9B29AA77FF98', X'7CF73C9647B6DC458B61C211EB4598D3', X'7CF73C9647B6DC458B61C211EB4598D3', 2, NULL, NULL, 2, NULL, 0, 'Place onto hot grill, seasoning side up. Grill over medium heat, turning occasionally until internal temperature reaches 165 F, about 35 minutes depending on how large the thighs are.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CCECE4268ADD24A9684BCB15DBA7930', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 0, NULL, NULL, 2, NULL, 0, 'Combine lemonade concentrates with soy sauce, salt, garlic powder, marjoram, paprika, and black pepper in blender and mix until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60AE61AAA9202249A4BCB0CFB664384A', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 1, NULL, NULL, 2, NULL, 0, 'Place chicken in a large resealable plastic bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0F1221CA8ED2443A67AAD5EBF9D6C8E', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 2, NULL, NULL, 2, NULL, 0, 'Pour in half of the lemonade mixture into the plastic bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6C3E097A856F54CBA314D7478774F8C', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 3, NULL, NULL, 2, NULL, 0, 'Turn to coat and let sit for 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE1AA2574542B8499F0B136A591358D4', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D9AF85A5B90C448AD121BA691BF8C4', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken from the marinade and place on hot grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'944CD1B5A7B3D14EABB4D851A8D539D1', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 6, NULL, NULL, 2, NULL, 0, 'Grill chicken for about 6 to 7 minutes per side basting with reserved marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5C5ABA9990FC94F84120A372C2669CA', X'C530F508ED900442B0DBC951F44BE913', X'C530F508ED900442B0DBC951F44BE913', 7, NULL, NULL, 2, NULL, 0, 'Continue grilling chicken until done, about 12 to 15 minutes or until it reaches an internal temperature of 165 F/75 C, and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22A0F04BD770C348B5B871EAF8773102', X'57A7EB464C8D5247860D7A297633D777', X'57A7EB464C8D5247860D7A297633D777', 0, NULL, NULL, 2, NULL, 0, 'Tear a piece of aluminum foil paper about 12 inches long. Fold in half and put on the grill top, folding edges under the grill to secure it in place.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25F163323048DC458A79788FA32B23A2', X'57A7EB464C8D5247860D7A297633D777', X'57A7EB464C8D5247860D7A297633D777', 1, NULL, NULL, 2, NULL, 0, 'Remove any excess fat or skin hanging from chicken, and rinse with cool water. Pat dry with paper towels. Season chicken well inside and out with Cajun seasoning. Make sure to get under the skin of the breast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0120FC6105522D4E9D3FDE2578FB4BAC', X'57A7EB464C8D5247860D7A297633D777', X'57A7EB464C8D5247860D7A297633D777', 2, NULL, NULL, 2, NULL, 0, 'Open can of beer and pour out 1 ounce of liquid—you can either drink it or discard it. Add liquid crab boil to beer. Separate chicken skin from the neck area by pushing a finger between the skin and meat. While holding pocket open, pour beer into the pocket. Do this in several areas. A large injection needle also works well for doing this.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'669602F53254DC41A71938F1A5F6D971', X'57A7EB464C8D5247860D7A297633D777', X'57A7EB464C8D5247860D7A297633D777', 3, NULL, NULL, 2, NULL, 0, 'Next, slide the bottom-end of the chicken over the can of beer. While holding the beer can and the chicken, gently place on the foil-covered grill. Close grill cover and cook for about 45 to 60 minutes or until the juices run clear and the chicken is done. The internal temperature must be 165 F in the breast meat and 175 F in the thigh area.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE50FDDADEC3274DABB40D8108129F26', X'57A7EB464C8D5247860D7A297633D777', X'57A7EB464C8D5247860D7A297633D777', 4, NULL, NULL, 2, NULL, 0, 'Once cooked, using heat-resistant gloves, carefully remove the chicken from the grill (still with the beer can inserted) and place onto a large cutting board with a lip to collect any juices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D1594F25092B34BA22DA5C1E7BD3467', X'57A7EB464C8D5247860D7A297633D777', X'57A7EB464C8D5247860D7A297633D777', 5, NULL, NULL, 2, NULL, 0, 'Remove beer can and loosely tent the bird with aluminum foil. Let it rest for 8 to 10 minutes before carving. Reserve any juices from the beer can and cutting board to make a pan sauce or gravy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'789C54C27FEA8D4680D18DFDE7A7A2FC', X'773BEF8B7486AC4CA7BDB92B68DC762A', X'773BEF8B7486AC4CA7BDB92B68DC762A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E95E217B5152BA4E84E002B66A343561', X'773BEF8B7486AC4CA7BDB92B68DC762A', X'773BEF8B7486AC4CA7BDB92B68DC762A', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the dressing/marinade ingredients in a bowl or measuring cup until the ingredients are emulsified and completely combined.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B316EC8A3A64144912FECD8ED065003', X'773BEF8B7486AC4CA7BDB92B68DC762A', X'773BEF8B7486AC4CA7BDB92B68DC762A', 2, NULL, NULL, 2, NULL, 0, 'Pour half of the mixture into a large bowl and reserve the other half for the dressing. Add the chicken breasts, toss to coat, and cover. Store in the refrigerator for at least 30 minutes or up to 2 hours. Any longer and the lemon juice will start to break down the chicken and cause it to become mushy when cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3FC43292DE74043BE99BC8F3F9F1256', X'773BEF8B7486AC4CA7BDB92B68DC762A', X'773BEF8B7486AC4CA7BDB92B68DC762A', 3, NULL, NULL, 2, NULL, 0, 'Heat a grill pan or your grill on high heat. Add the chicken and cook for about 5 minutes per side. Don’t move the chicken too soon or the grill marks will not form. Cook until the internal temperature reaches 165 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85C7F0BD2543E3498D8517C3893FF758', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 0, NULL, NULL, 2, NULL, 0, 'Combine marinade ingredients in a large mixing bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0623C0F4121BF7438EDC10FC222C0B63', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 1, NULL, NULL, 2, NULL, 0, 'Add the wings to the mixing bowl and toss them to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3F77260B0EC044C994318E21310A24D', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 2, NULL, NULL, 2, NULL, 0, 'Cover the wings with plastic wrap and allow them to marinate in a refrigerator for 4 to 8 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9784A2C2163EBA4981672FA83BFD4ECD', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill for high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60E0928A159D134DBA754A5553E0FB71', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 4, NULL, NULL, 2, NULL, 0, 'Place wings on the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77D6E6A77E73E145880AC03D485D8E4D', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 5, NULL, NULL, 2, NULL, 0, 'Reserve the remaining marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A113D7326CAE84429C0BD8C1EEFB2789', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 6, NULL, NULL, 2, NULL, 0, 'Cook the wings for 5 minutes per side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31009672B4BB4B4B924F7D73D3338B1E', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 7, NULL, NULL, 2, NULL, 0, 'Baste wings liberally with remaining marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8900F6065633AD4D98BFA1F8DC8D7128', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 8, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium and continue cooking the wings for an additional 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13166792D1C081418A2AF11C598F2A17', X'11C7F9AB352166428ED86C6007CB99A7', X'11C7F9AB352166428ED86C6007CB99A7', 9, NULL, NULL, 2, NULL, 0, 'When done, remove from grill and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EAAF3FAE192CF4B9AB88131EF411D9F', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 0, NULL, NULL, 2, NULL, 0, 'Make a dry rub by sifting the pepper, onion powder, chili powder, garlic powder, and seasoned salt into a bowl to blend.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF24C71FF6E4174BA4689AA91D3D8D23', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 1, NULL, NULL, 2, NULL, 0, 'Place the chicken wings in a large resealable plastic bag. Pour in the dry rub and shake to coat the wings well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75181B380D03BF4A8A77A7536CA98E04', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 2, NULL, NULL, 2, NULL, 0, 'Marinate for at least 30 minutes (at room temperature) or as long as 24 hours (in the refrigerator).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BE303BD3E81F34495F84F56E5373CA0', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 3, NULL, NULL, 2, NULL, 0, 'Prepare coals using three pounds of charcoal. Make sure the coals are in a pile to one side of your grill or smoker.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4659D1FE94D5E54DA68217297D7A1754', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 4, NULL, NULL, 2, NULL, 0, 'After the coals have turned white, place 2 cups of soaked and drained apple wood chips on the pile of coals.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526C4B92F73292408E9B7A3EC078A6A3', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 5, NULL, NULL, 2, NULL, 0, 'Set the wings on the grate so they will cook by indirect heat—in other words, not over the coals—and smoke for 25 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7C44591B81DF943A235E37D806C542E', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 6, NULL, NULL, 2, NULL, 0, 'Turn the wings and smoke for another 20 to 25 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80E92F0F2E8D0A4A9A4C2DC534D0F1A1', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 7, NULL, NULL, 2, NULL, 0, 'Mix the honey, barbecue sauce, and apple juice together in a small saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAAB26F540C25747BD94331292B912B1', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 8, NULL, NULL, 2, NULL, 0, 'Cook over medium heat until warmed through, about two to three minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'938C530E80259A43ADFC482C7E0ECBD6', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 9, NULL, NULL, 2, NULL, 0, 'Place the wings in a disposable foil pan and pour the warm sauce over the wings.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66985A405847DC4E8EF36C7D215E2B97', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 10, NULL, NULL, 2, NULL, 0, 'Toss to coat evenly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC411D9CBC7B944E9FCC50393E91A828', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 11, NULL, NULL, 2, NULL, 0, 'Cover and smoke for another 20 to 30 minutes, stirring occasionally, until the glaze is finished the way you like it.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E087663E54BE574AA2BCB516B8E9B3A6', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 12, NULL, NULL, 2, NULL, 0, 'If you''re using a grill, you can move the aluminum pan directly over the coals while you''re glazing the wings. Chicken wings are fully cooked once the internal temperature reaches 175 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4500DC9BEA7A4B8A501A8901B4A396', X'CC88FF68BB896A48B65DF5CFBA3AEF15', X'CC88FF68BB896A48B65DF5CFBA3AEF15', 13, NULL, NULL, 2, NULL, 0, 'Remove from heat and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2420C741CBD8B499CA87426738C6D42', X'D79FD3D61D05244D932D3E045BFF99F7', X'D79FD3D61D05244D932D3E045BFF99F7', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in a saucepan and allow to simmer for 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B66248B7BFD2146AD8B70772A8863C9', X'D79FD3D61D05244D932D3E045BFF99F7', X'D79FD3D61D05244D932D3E045BFF99F7', 1, NULL, NULL, 2, NULL, 0, 'Once the sauce has cooked through, remove from heat and allow to cool before using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97E732804753CF44AAF3C2EDEE7D6004', X'D79FD3D61D05244D932D3E045BFF99F7', X'D79FD3D61D05244D932D3E045BFF99F7', 2, NULL, NULL, 2, NULL, 0, 'Store in an airtight container in the refrigerator for up to 5 days after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A270654F82FCAE4AA6AA09CCF0D0B0AD', X'A314A0E8A57F9547AE87D623568A1DAB', X'A314A0E8A57F9547AE87D623568A1DAB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE2CBEB1C78D2F46BCBFF06E602BE862', X'A314A0E8A57F9547AE87D623568A1DAB', X'A314A0E8A57F9547AE87D623568A1DAB', 1, NULL, NULL, 2, NULL, 0, 'Preheat grill. Combine all ingredients (except chicken wings) in a small saucepan and let boil for 2 minutes. Remove from heat, pour into a large bowl and let cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFCA9CC0BACDA84BA222B938E4E0C8CD', X'A314A0E8A57F9547AE87D623568A1DAB', X'A314A0E8A57F9547AE87D623568A1DAB', 2, NULL, NULL, 2, NULL, 0, 'Add chicken wings to the marinade and marinate in the refrigerator for 2 hours or more.');
