INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DA9110333D8804B985D7826C4A2DE70', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 1, NULL, NULL, 2, NULL, 0, 'With a sharp knife, make several deep slashes through the skin and flesh of the chicken: two across the breast, two across the thigh, one across the leg.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04C9288EFC1AEA48BBCEC462A584990E', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 2, NULL, NULL, 2, NULL, 0, 'In a wide, shallow dish, combine the rice vinegar, garlic, and 1/4 cup of the barbecue sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C0269FAC23863449AE1D59C2A9F3A9F', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken and coat with the marinade. Once coated turn the chicken skin-side down in the dish, cover, and refrigerate for 1 to 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05A58F0029B65C4192056F00D331433E', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 4, NULL, NULL, 2, NULL, 0, 'Remove the chicken from the marinade, pat dry with paper towels, and place on a foil-lined baking sheet.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47BFD4BD2902014185FDC0D0FAD2AA88', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the dry rub ingredients and sprinkle over both sides of the chicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D09B6866994C8A42827DF1E19D4DBC48', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 6, NULL, NULL, 2, NULL, 0, 'Build a medium fire in your charcoal grill (you may use a gas grill, but charcoal is recommended for this recipe). When all of the charcoal is covered in gray ash, brush the grates lightly with oil and place the chicken skin-side down on the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8899AC7CD50C5C408BB37653EF2135C1', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 7, NULL, NULL, 2, NULL, 0, 'Cook for just 3 to 4 minutes to lightly char the skin and create some grill marks, then turn over so the skin side is up. Brush the top of the chicken generously with barbecue sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6F004B28D5AB2409474F160C32D9162', X'F5D72B37920EDE47A57B93420741EC08', X'F5D72B37920EDE47A57B93420741EC08', 8, NULL, NULL, 2, NULL, 0, 'Cover the grill and cook, without turning the chicken again, for 35 to 40 minutes, or until the internal temperature is 165 to 170 F. Baste with sauce every 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'304082EB1542F94F8EF374D5C591B964', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'410F68197698DE44A73F0CD82980F5B8', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 1, NULL, NULL, 2, NULL, 0, 'Thoroughly dry the chicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A8BD990BDF89244A9B2E95EB18211DF', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle the chicken pieces with the salt, taking care to work some under the skin.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95AECD92C8277242BE8DEC2F1EFC91D4', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 3, NULL, NULL, 2, NULL, 0, 'Cover and chill at least two hours and up to overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'532022F3B90D6446BE8B61A26056054B', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 4, NULL, NULL, 2, NULL, 0, 'Let the chicken come to room temperature for about 30 minutes before grilling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E5655F4BBDEC444ACA1AE54E5DE249C', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare a gas grill for indirect heat. Turn all burners on high and close the lid. When temperature inside the grill reaches 400 F, turn off one burner. The area over the turned-off burner is the indirect heat section.?');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F7EF9AD2A6715498714CD2DF194CFE1', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 6, NULL, NULL, 2, NULL, 0, 'Brush the cooking grate with vegetable oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8715D4D7D69F32469A7A10772C108BE7', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 7, NULL, NULL, 2, NULL, 0, 'Set the chicken skin-side down on the indirect heat section of the grill. Close the lid on a gas grill and cook for 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59C17180A308E04F8673145C9EBB36B6', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 8, NULL, NULL, 2, NULL, 0, 'Turn the chicken over, close the lid on the gas grill, and cook 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5954AA32B9D1884586120DA30EAB4C6E', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 9, NULL, NULL, 2, NULL, 0, 'Move chicken to the direct heat section and cook, turning once, until skin is brown and crispy–about five minutes. (Pay close attention: any dripping fat or added oils or marinades catch fire easily. You may want to keep a spray bottle of water nearby to douse flames.)');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'691AB751D2C8B3489C16FD609D7EE598', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 10, NULL, NULL, 2, NULL, 0, 'Test the chicken for doneness by cutting into ?its center. It should be slightly pink (it will finish cooking while it rests). If the center is very pink, return the chicken to the indirect heat section and cook another five minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAEC6B6679978D469A3DC6DA61317B0A', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 11, NULL, NULL, 2, NULL, 0, 'Let the chicken rest at least 10 minutes, covered with foil, before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3EC00018F8A834CB5EC413CB86C372E', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 12, NULL, NULL, 2, NULL, 0, 'Serve hot, warm, or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D78BFEBF9F21934BB574DE59696228FE', X'997A6C705563624DBE630233AC9EEFE0', X'997A6C705563624DBE630233AC9EEFE0', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AA315E500CEA4408AC5A643920F95C7', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DFFFC13D8E1964F8BA14CF0CCFBF735', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, bring 2 cups of water to a boil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CFF5FD06DC38E409483E9E601229C3F', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 2, NULL, NULL, 2, NULL, 0, 'Add the salt and sugar, and stir until dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'624514EA4A3B5C4FA3625ADD798A103B', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining 6 cups of cold water and stir in the whole peppercorns and allspice berries. Let the mixture cool completely before using it.');
