INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'013D3DE7D2E70E418C36B25DF1279D4C', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 5, NULL, NULL, 2, NULL, 0, 'Pour the cooled brine mixture over the pork. Cover it with a lid or plastic wrap, and store in the refrigerator. Brine the pork for 8 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E5EC446194D3D4EAB53FD05E103E93B', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 6, NULL, NULL, 2, NULL, 0, 'When ready to cook, remove the meat from the brine. Quickly rinse it under cool water, and pat dry with paper towels. Do not over-rinse as this will remove all the salt and will leave the meat quite bland.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B745F4A3A1DA1640BB717B0FFC9A5099', X'ADE0FFB3DCC42D428899D8BCC41B0724', X'ADE0FFB3DCC42D428899D8BCC41B0724', 7, NULL, NULL, 2, NULL, 0, 'Coat the tenderloin with the salt-free seasoning rub of your choice, and cook as directed per the grilling or oven roasting instructions of your preferred recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'675AF0542B04984EBDECE40D3C1A69B2', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
