INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F161015F46C7BE439650DE4BBA3EEDB6', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 2, NULL, NULL, 2, NULL, 0, 'Combine all rub ingredients in a small mixing bowl. Set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'417D19478624E34CB9492FFF2DEDA756', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 3, NULL, NULL, 2, NULL, 0, 'Remove giblets and the neck from chicken if needed.');
