INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F358C8B6DCDB1489CCA3B05E6ED7F54', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 5, NULL, NULL, 2, NULL, 0, 'Open can of beer and discard (or drink) half of it.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73625771EB12D543AF07F1EC78D4E909', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 6, NULL, NULL, 2, NULL, 0, 'Place minced garlic, rosemary, thyme, lemon juice, and pepper flakes in it.');
