INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB40C07B17076B4F8C8E35A89A3D97E8', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 10, NULL, NULL, 2, NULL, 0, 'Grill over indirect medium heat for 1 1/2 to 2 hours until the internal temperature of the thigh reaches 180 F/82 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'369E4CB1B591B24A883704415A421DE2', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', X'EF353FB6ECD9F1499C6B7EFCBF57F80D', 11, NULL, NULL, 2, NULL, 0, 'Remove chicken when finished cooking and let sit (with beer can still intact), for 10 minutes before carving.');
