INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D0D4795FF45FF49B3A6E2B8D8DD7D4D', X'D2A09B35064C214080DE9270875AD5AC', X'D2A09B35064C214080DE9270875AD5AC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
