INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43D2F8E1930EEC4DBF4B998AF195586F', X'D2A09B35064C214080DE9270875AD5AC', X'D2A09B35064C214080DE9270875AD5AC', 2, NULL, NULL, 2, NULL, 0, 'Place remaining ingredients into a food processor and pulse to make the chimichurri sauce. Do not puree mixture, but leave a little texture to the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'101C86F606569144975CB213D32D9CD3', X'D2A09B35064C214080DE9270875AD5AC', X'D2A09B35064C214080DE9270875AD5AC', 3, NULL, NULL, 2, NULL, 0, 'Place chicken breasts into a resealable plastic bag and pour chimichurri mixture over top. Make sure to coat chicken well with marinade before sealing bag. Place into the refrigerator for 2 to 6 hours in a refrigerator. The longer the marinade time, the more flavor will be infused into the breast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'754528A906984A48B5CCC4D6F17D8E74', X'D2A09B35064C214080DE9270875AD5AC', X'D2A09B35064C214080DE9270875AD5AC', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'017555AE52E3CE4CA0ECD6D48B7F85D6', X'D2A09B35064C214080DE9270875AD5AC', X'D2A09B35064C214080DE9270875AD5AC', 5, NULL, NULL, 2, NULL, 0, 'Take chicken out of the bag and discard marinade. Place chicken onto to grill and cook 5 minutes per side, or until the thickest part of the meat reaches 165 F. Remove from heat and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'794016BD113CA04DBB0524B723D0B4BC', X'D2A09B35064C214080DE9270875AD5AC', X'D2A09B35064C214080DE9270875AD5AC', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A5ABBB729AD0B4593278179F0FE5253', X'6ED086A681D5C74E9A826FF3082B026D', X'6ED086A681D5C74E9A826FF3082B026D', 0, NULL, NULL, 2, NULL, 0, 'Prepare the chicken by rinsing thoroughly with cold water. Drain excess water from the inside cavity and pat dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7299AFBD30516C4F947437F3D0C285B4', X'6ED086A681D5C74E9A826FF3082B026D', X'6ED086A681D5C74E9A826FF3082B026D', 1, NULL, NULL, 2, NULL, 0, 'Place chicken in a shallow baking dish and pour some of the lime juice over the top and inside the cavity. Grab the chicken by the breast skin and place your hand between the skin and the breast creating a pocket. Using a basting brush, baste lime juice into the breast meat pocket, making sure to reserve some for later. Rub under the skin with fresh chopped cilantro.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E6C2FCDB8EED641A410A6F04938146D', X'6ED086A681D5C74E9A826FF3082B026D', X'6ED086A681D5C74E9A826FF3082B026D', 2, NULL, NULL, 2, NULL, 0, 'Place chicken in a large glass baking dish and cover with plastic wrap. Place in the refrigerator for 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96CD668FBEB511408C868687D868325E', X'6ED086A681D5C74E9A826FF3082B026D', X'6ED086A681D5C74E9A826FF3082B026D', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill at high heat. Remove chicken from the refrigerator.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B847DED71B1F4248925E17EE6EDA1B6C', X'6ED086A681D5C74E9A826FF3082B026D', X'6ED086A681D5C74E9A826FF3082B026D', 4, NULL, NULL, 2, NULL, 0, 'Coat the entire chicken with taco seasoning, stuff with fresh lime halves deep into the chicken cavity and add a little fresh chopped cilantro.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CB7169423EB0A47818CC1D696EA45E6', X'6ED086A681D5C74E9A826FF3082B026D', X'6ED086A681D5C74E9A826FF3082B026D', 5, NULL, NULL, 2, NULL, 0, 'Place the chicken securely on the rotisserie rod and truss legs and wings with string. Cook chicken for 1 1/2 to 2 hours, or until the internal temperature is between 165-175 degrees F/75-80 degrees C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C7776114287CC409D6765CB64B0E825', X'E5EEDD19C5AFF745A4F025CC8FDABD49', X'E5EEDD19C5AFF745A4F025CC8FDABD49', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
