INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F7995FC474A8548AD3C048D01E5DE32', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 0, NULL, NULL, 2, NULL, 0, 'Remove insides from the chicken and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CDF71DC63A88D408EE84CFA00522231', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the outside of the chicken evenly with the Cajun seasoning.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76109DAEAED8A044A4434BD52255E5AA', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 2, NULL, NULL, 2, NULL, 0, 'Fold the tips of the chicken wings behind the back of the chicken. Allow the chicken to stand at room temperature for about 20 minutes (if it''s a hot day, refrigerate the chicken for 60 minutes).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBBD95F0B04E464D873B1A22D93A2675', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 3, NULL, NULL, 2, NULL, 0, 'Heat the barbecue grill to medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85FAFC320E815E41AFFBE9ABD24DE090', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 4, NULL, NULL, 2, NULL, 0, 'When the grill is heated to medium (about 400 F) open a 12-ounce can of beer. Pour about half the beer out of the can (preferably into a glass, to drink it while you''re cooking). Make 3 or 4 additional holes in the top of the beer can with a can opener.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8F3ED6406F885449A8A6B233AE9929A', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 5, NULL, NULL, 2, NULL, 0, 'Set the beer can on a flat surface and place the cavity of the chicken directly over the can. With the can as the base, balance the bird on 3 points like a tripod, with the two legs and the can as the 3 points that stabilize the chicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADCF5017A0C1DB4FAE22A45637444FB1', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 6, NULL, NULL, 2, NULL, 0, 'Set the chicken on the hot grill directly over the coals or burners, stabilizing it on the grill just as it was on the flat surface. Close the lid and cook for 1 hour and 15 minutes. Check the temperature with a meat thermometer placed into the thick part of the thigh. The temperature should reach 170 F, and the juices should be clear and not pink.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEE6AD3D5ECACC40B84E058AF5300A79', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 7, NULL, NULL, 2, NULL, 0, 'If the temperature has not reached 170 F, continue cooking the chicken, checking the temperature every few minutes, until the temperature has reached 170 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD96D8B8E1546C43927CC174EA3A4CC6', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 8, NULL, NULL, 2, NULL, 0, 'Place a sturdy baking sheet or platter near the grill and carefully transfer the chicken to it. Allow the chicken to stand for 10 minutes, then carefully lift the chicken off the can of beer. Cut the chicken into serving pieces and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4449A346A907C942853FE8531868C6CB', X'4AA424D17BF38544B9E21C4689F1A9AD', X'4AA424D17BF38544B9E21C4689F1A9AD', 9, NULL, NULL, 2, NULL, 0, 'The chicken is best hot but can be enjoyed cold or at room temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC0C42E01F2F845B6612227E9FBC289', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 0, NULL, NULL, 2, NULL, 0, 'Combine all marinade ingredients in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86DA1091D612AC419CCCEF8AAB598321', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 1, NULL, NULL, 2, NULL, 0, 'Remove giblets and wing tips from chicken.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'271C4E85BBC4974BBA6E5A687CA1795F', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 2, NULL, NULL, 2, NULL, 0, 'Wash out the chicken cavity and pat dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'927A980A64FFDC4BBACDB3D393180865', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 3, NULL, NULL, 2, NULL, 0, 'Place chicken in a deep dish, coat with yogurt mixture, cover and allow the chicken to marinate in the refrigerator for 2 to 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08FCB4D8F1A8414AB8146E95A0C4A14F', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E0BEBA146EAB94CADC914F8F6A2F0B8', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 5, NULL, NULL, 2, NULL, 0, 'Remove chicken from dish and discard marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6227416E37E5947A7F5BB3B9E5BAD9B', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 6, NULL, NULL, 2, NULL, 0, 'Place chicken on rotisserie spit and cook over indirect medium-high heat for 2 hours, or until the internal temperature reaches between 165 and 170 F (75 C.)');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0EB2E278FF5FD4289E71E164F111F99', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 7, NULL, NULL, 2, NULL, 0, 'If heat is too high, reduce and cook over medium heat instead.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D615B66A20836488D64DAB869826B63', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 8, NULL, NULL, 2, NULL, 0, 'Once cooked, remove from heat and loosely tent with aluminum foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D91497227D36534A8837B49835EBD4B1', X'B82260750CC4BC479340F0E412BB333F', X'B82260750CC4BC479340F0E412BB333F', 9, NULL, NULL, 2, NULL, 0, 'Let chicken rest for 10 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E850C954EC4BC4D849ECAA4359E4392', X'E3E7B2FD23774E49B4CD8972778A712E', X'E3E7B2FD23774E49B4CD8972778A712E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9A64022F5F3EC42899A4466C183F9C0', X'E3E7B2FD23774E49B4CD8972778A712E', X'E3E7B2FD23774E49B4CD8972778A712E', 1, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together in medium bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A169760FB5910548AD86EBBDD5AD1A45', X'E3E7B2FD23774E49B4CD8972778A712E', X'E3E7B2FD23774E49B4CD8972778A712E', 2, NULL, NULL, 2, NULL, 0, 'Use immediately or store in an airtight container in a cool dry place (a cupboard will do), for up to 6 months after preparation.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E00D8C074044564AA8897739A2337FD1', X'E3E7B2FD23774E49B4CD8972778A712E', X'E3E7B2FD23774E49B4CD8972778A712E', 3, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DBC073976769541B30A87720CAB0CD9', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except the chicken and mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89647CD77D978147AFEA5F407BE30C68', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 1, NULL, NULL, 2, NULL, 0, 'Separate out 1/3 of the mixture and brush over the entire surface of the chicken inside and out. It''s best if you can get as much as possible under the skin, particularly over the breasts.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'889D772176D2F04B968DACDCF5353986', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 2, NULL, NULL, 2, NULL, 0, 'Place chicken on rotisserie spit and grill over medium-low heat on the grill with a drip pan under the chicken. After 20 minutes of cooking time brush with part of remaining of the mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C5ACFC62F97D640BD81263C3C8223DC', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 3, NULL, NULL, 2, NULL, 0, 'Repeat after another 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C55D56CF6D4455438C61DAFA4D7A4AAD', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 4, NULL, NULL, 2, NULL, 0, 'Continue grilling until chicken has reached an internal temperature of 165 F in multiple areas, particularly the thigh area. Chicken should take about 1 1/2 hours to cook.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF4AD66C645B4C4CBE283814553FF6CD', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 5, NULL, NULL, 2, NULL, 0, 'Remove from heat and let chicken rest for 10 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DAE373AF4EA2041949F3D85B7B7B9E7', X'44ECC983331EED4AA5738A4D46CBB850', X'44ECC983331EED4AA5738A4D46CBB850', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'079E3E75D87CBB48A4E9056131A3C8ED', X'1C024E795BE9A84D91964F04788F8D5D', X'1C024E795BE9A84D91964F04788F8D5D', 0, NULL, NULL, 2, NULL, 0, 'Preheat an electric skillet to 275 F. Add oil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE2F818B7AB7E428574B1C0B92D11F8', X'1C024E795BE9A84D91964F04788F8D5D', X'1C024E795BE9A84D91964F04788F8D5D', 1, NULL, NULL, 2, NULL, 0, 'Season chicken on both sides with salt and pepper. When oil is hot, add chicken to the skillet. Brown for 2 minutes on the first side. Flip.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C9B6BC95C8F3F41B875BF3E58EE5106', X'1C024E795BE9A84D91964F04788F8D5D', X'1C024E795BE9A84D91964F04788F8D5D', 2, NULL, NULL, 2, NULL, 0, 'Add the barbecue sauce and orange soda.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33B66FEDB9EA204E84842F957DD592D8', X'1C024E795BE9A84D91964F04788F8D5D', X'1C024E795BE9A84D91964F04788F8D5D', 3, NULL, NULL, 2, NULL, 0, 'Cook, basting occasionally, 35-40 minutes until chicken is cooked through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0766E53537DA7468CCF480BFB2B65B4', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67CE38414504C948B5CA0BE03E5BF367', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 1, NULL, NULL, 2, NULL, 0, 'Pat the turkey legs dry.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC19EF0F21FB754A840E4D4965D7C71C', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 2, NULL, NULL, 2, NULL, 0, 'In a large pot, combine the soda, sugar, hot sauce, red pepper flakes, and black pepper. Bring to a low boil and add the turkey legs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'868E9524D2139A4D90A6D9BBA0E83BD7', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 3, NULL, NULL, 2, NULL, 0, 'Boil over medium heat for about 30 minutes or until the turkey legs register an internal temperature of 165 F/74 C. Preheat the grill to medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489CEF17E401124BB10F9DDA51767151', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 4, NULL, NULL, 2, NULL, 0, 'Place the onion slices on the heated grill and put the turkey legs on top of the onion slices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2A4B607FC815348BB51AF3EFFF4743E', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 5, NULL, NULL, 2, NULL, 0, 'Brush with the turkey legs honey and sprinkle them with the steak seasoning.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D49FDCFC7B5646874AA1DA9CB63A6F', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 6, NULL, NULL, 2, NULL, 0, 'Grill over a medium heat for about 15 minutes watching carefully to avoid burning. Turn the turkey legs as needed. The skin should be crispy brown and the legs should reach an internal temperature of between 165 to 175 F when done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'344D268A484E3141BEBF325AA0F6AD55', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 7, NULL, NULL, 2, NULL, 0, 'Once cooked, remove the turkey legs from the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A520DFCA38E2454DB8320C5E567E54D8', X'8FFA7920AD4E964A8BAA50AE5059C64B', X'8FFA7920AD4E964A8BAA50AE5059C64B', 8, NULL, NULL, 2, NULL, 0, 'Serve with your choice of sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6D36ABC798E6341AA69B3DD4E434669', X'0CFA430F9AF05044B5E29ED778625F51', X'0CFA430F9AF05044B5E29ED778625F51', 0, NULL, NULL, 2, NULL, 0, 'Combine the yogurt, salt, black pepper, turmeric, mustard, curry powder, cardamom, lemon juice, and vinegar in a large glass or plastic bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A0BEF93E5572142823BB18A9EF0EA07', X'0CFA430F9AF05044B5E29ED778625F51', X'0CFA430F9AF05044B5E29ED778625F51', 1, NULL, NULL, 2, NULL, 0, 'Add the cubed chicken into the bowl, toss to combine, cover with plastic wrap, and place in the refrigerator for 1 to 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52B78A7D3B41C144B859508B0B87A3A1', X'0CFA430F9AF05044B5E29ED778625F51', X'0CFA430F9AF05044B5E29ED778625F51', 2, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29DC6420A49D7845AFC34113D22D24A2', X'0CFA430F9AF05044B5E29ED778625F51', X'0CFA430F9AF05044B5E29ED778625F51', 3, NULL, NULL, 2, NULL, 0, 'Remove the chicken pieces and thread onto skewers, alternating with the sliced onion, using about 5 pieces of chicken per skewer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DED426D969CD3F49844756756A123247', X'0CFA430F9AF05044B5E29ED778625F51', X'0CFA430F9AF05044B5E29ED778625F51', 4, NULL, NULL, 2, NULL, 0, 'Place the kebabs on the grill and cook for 10 to 12 minutes, turning occasionally, until cooked through and the internal temperature reaches 165 F.');
