INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B720E5895BFC2D4488882AFA97234FB6', X'EE4DAD68E504BA4EBB31484751093A9A', X'EE4DAD68E504BA4EBB31484751093A9A', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except the chicken and pour into a shallow baking dish or resealable plastic bag. Add chicken and turn to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFA600D9A26993419AD69AE48D337BED', X'EE4DAD68E504BA4EBB31484751093A9A', X'EE4DAD68E504BA4EBB31484751093A9A', 1, NULL, NULL, 2, NULL, 0, 'Cover and place in refrigerator to marinate in jerk seasoning for 30 minutes to 1 hour. Preheat grill. Remove chicken from jerk marinade and pour marinade into a saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2834C782B5766045B36A99A74A2D97C0', X'EE4DAD68E504BA4EBB31484751093A9A', X'EE4DAD68E504BA4EBB31484751093A9A', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil. Place chicken on grill cooking approximately 7 to 10 minutes per side (or until done), basting periodically with remaining cooked jerk marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1B02E854F7C3D43B952BB68F53D6174', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81C5A7FADD08F846BAC4B9A5BC69CD4B', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine 1/2 cup/120 mL soy sauce with the sake (or dry sherry), sugar, ginger, and chives. Mix well, ensuring the sugar is dissolved.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0B3111A5C1EC418D70A8B27D042413', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 2, NULL, NULL, 2, NULL, 0, 'Place chicken pieces and half of the marinade in a resealable plastic bag. Toss to coat and refrigerate for about 1 hour. Reserve the remaining marinade for grilling.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B46F6FF969037E4FBC82457D0332F4E6', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 3, NULL, NULL, 2, NULL, 0, 'Preheat the grill to medium-high heat. Remove chicken from the marinade and thread onto skewers, alternating the chicken with the scallions. Discard the marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BB8102A482AA14A964E96E0229484C5', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 4, NULL, NULL, 2, NULL, 0, 'Place the skewers on the hot grill. Brush with the reserved marinade during the first 2 to 4 minutes and grill for a total of about 6 to 8 minutes (internal temperature of 165 F), turning the skewers a few times.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAF694743048ED4C806B3085C169ED49', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 5, NULL, NULL, 2, NULL, 0, 'In a small saucepan, heat up the remaining 1/2 cup/120 mL of soy sauce to use for dipping.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A210B00305C99448D9A8918718800E4', X'6DD54044BD1E6948AAC6527FD01BBB8A', X'6DD54044BD1E6948AAC6527FD01BBB8A', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E3B0F32A530CD4C99901E54F6261D52', X'46B38453F82B1C478799762C57AC34D3', X'46B38453F82B1C478799762C57AC34D3', 0, NULL, NULL, 2, NULL, 0, '?Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D514EC3310E48C48AFA1B4C56EDADCFA', X'46B38453F82B1C478799762C57AC34D3', X'46B38453F82B1C478799762C57AC34D3', 1, NULL, NULL, 2, NULL, 0, 'In a medium sized container add the salt, sugar, and peppercorns. Add 1/2 cup of boiling or very hot water, and stir until the salt and sugar are mostly dissolved. Add another 3 cups of very cold water, maybe a few ice cubes. Place the pork chops in the brine, and let them sit at room temperature for about 45 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78FA1063430D0041AD28A99407B0309F', X'46B38453F82B1C478799762C57AC34D3', X'46B38453F82B1C478799762C57AC34D3', 2, NULL, NULL, 2, NULL, 0, 'Preheat a grill to medium high.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E7B935E773D4F4AB5B5C1790C873F1F', X'46B38453F82B1C478799762C57AC34D3', X'46B38453F82B1C478799762C57AC34D3', 3, NULL, NULL, 2, NULL, 0, 'Remove the pork chops from the brine and pat dry with paper towels. Wrap each pork chop with a piece of prosciutto, pressing so that the prosciutto seals itself.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04016A6EDA929445AB9A77F6AA7A998D', X'46B38453F82B1C478799762C57AC34D3', X'46B38453F82B1C478799762C57AC34D3', 4, NULL, NULL, 2, NULL, 0, 'Brush the pork chops with olive oil and grill for about 4 minute on each side until the just pink in the center, or cooed to an internal temperature of 140°F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9592F270D98F647B288FE49F935C6C9', X'46B38453F82B1C478799762C57AC34D3', X'46B38453F82B1C478799762C57AC34D3', 5, NULL, NULL, 2, NULL, 0, 'Let the chops sit for a minute or so before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B8C8D952BECE147A72039927F676403', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B739FB487A9DAF47AB011DDC8BE29432', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 1, NULL, NULL, 2, NULL, 0, 'Put the pork shoulder in the slow cooker, cutting the roast to fit if necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'608D88296CCAAA4196348AE3DD54FE9B', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 2, NULL, NULL, 2, NULL, 0, 'Add the water or other liquid and sprinkle the pork lightly with kosher salt, freshly ground black pepper, and cayenne pepper, if using.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA983F10D0DC2E419F3976BACC6BDFC6', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 7 to 9 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E82594191D9D2C44B54E4CB4E5942D14', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 4, NULL, NULL, 2, NULL, 0, 'Drain off the excess liquids, shred the meat with two forks, and discard any excess fat and bone.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C65CD76CDA224E906B4006D764500B', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 5, NULL, NULL, 2, NULL, 0, 'Return the meat to the crockpot and pour about 1 cup of barbecue sauce over. Cover and cook another 1 to 2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43CDA05BBC3EA8459942CFBC8D637556', X'13DF6E6708B5244EAF50F46FE5B732F2', X'13DF6E6708B5244EAF50F46FE5B732F2', 6, NULL, NULL, 2, NULL, 0, 'Serve the shredded barbecued pork with extra barbecue sauce on the side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84452F8FFC470649B26BEC13513E3B45', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D32238DFEA309D48884B47895FCBA78D', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 1, NULL, NULL, 2, NULL, 0, 'Combine all the sauce ingredients in a small saucepan until brown sugar is dissolved. Remove sauce from the heat and allow to cool for about 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE22585CC5CA04EA9AFCFA398E0F6FA', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 2, NULL, NULL, 2, NULL, 0, 'Remove membrane from back of ribs and any extra fatty pieces. Place the ribs in a large dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE148C62F7668409053038843AA5D08', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 3, NULL, NULL, 2, NULL, 0, 'Cover ribs with half of the sauce and allow to marinate in the refrigerator for 5 to 6 hours. Reserve the other half of sauce for later.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F9AC744B5D564390A3033B99E83E33', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D8692F8E4238542B19030CCD1A82EA2', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 5, NULL, NULL, 2, NULL, 0, 'Remove ribs from the dish, reserve marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'026D3D3FAF5F3D4B9AC2880D808010E2', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 6, NULL, NULL, 2, NULL, 0, 'Place the ribs on the grill and allow them to cook for 20 minutes, turning once.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA19D68DB5106C4E995E980B7CD2DCC8', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 7, NULL, NULL, 2, NULL, 0, 'Cover ribs loosely with foil and cook for an additional hour and a half.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78CD18F0096D1145BE045F9905BCE617', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 8, NULL, NULL, 2, NULL, 0, 'Once ribs reach an internal temperature of 165 F, remove from heat, place on a cutting board, and let ribs rest for 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E62A758301EE44ABFABBA8E8B9482A5', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 9, NULL, NULL, 2, NULL, 0, 'Carefully open foil packaging and discard.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83CA09214481954C983AD774D6FA2423', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 10, NULL, NULL, 2, NULL, 0, 'Heat reserved sauce, brush some over ribs, and carve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3403B74C5C4647AF4F4C66675308E0', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', X'7C8B0CC9012AF54CA43F5E43ACE26E2B', 11, NULL, NULL, 2, NULL, 0, 'Serve the remaining sauce with ribs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51F7579C17CD594D81C3DFA713DC569A', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A19874E88C58C4DB81887BF32233557', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 1, NULL, NULL, 2, NULL, 0, 'Place one sliced onion at the bottom of the slow cooker.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E4BC16A9B0F9B43A66F3D5E1C706D7B', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 2, NULL, NULL, 2, NULL, 0, 'Stud the pork roast with cloves (or put them in a small cheesecloth bag or tea strainer), if using. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CF74727113A2148ACD23611292CA6B0', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 3, NULL, NULL, 2, NULL, 0, 'Place the pork in the slow cooker on top of the sliced onion. Cover with the second sliced onion and add 2 cups of water.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'566682384F620E438D0934455C72FDC4', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook on low 9 to 12 hours, or until the pork is very tender and falling apart.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'821FD4EE3088174C991C45EE24FFD6D3', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 5, NULL, NULL, 2, NULL, 0, 'Remove the pork roast from the pot. Remove and discard the cloves and excess fat and liquids along with bones if the roast was not boneless. You may also discard the sliced onions, if desired.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85CCD315FD83E34F98B746B8F7B607AF', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 6, NULL, NULL, 2, NULL, 0, 'When the pork roast is cool enough to handle, use a fork or your fingers to pull it apart until the entire roast is shredded. If you make pulled pork often, you might want to buy meat shredding claws.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388BB60FA040ED4FB0F76C9A50ECE644', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 7, NULL, NULL, 2, NULL, 0, 'Return the pulled pork to the slow cooker. Add the finely chopped onion, barbecue sauce, and optional red pepper flakes, and optional liquid smoke. Cover the pot and continue to cook on the high setting for 1 to 2 hours, or until the onions are tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0685952968A76940ABEB2D5A36EF6EB4', X'C4D0FC8B7E06924585AE382DD6AC2C2F', X'C4D0FC8B7E06924585AE382DD6AC2C2F', 8, NULL, NULL, 2, NULL, 0, 'Serve the pulled pork on large, crusty buns with your favorite barbecue sauce. Garnish with dill pickles, thinly sliced raw onion, and pickled banana peppers. Serve with crisp, homemade slaw, either piled on the pork sandwiches or on the side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34D973F3C9B6294C8316EE1BE9202DEB', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', 0, NULL, NULL, 2, NULL, 0, 'To make rub, place peppercorns and mustard seed in a coffee grinder. Pulverize into a medium grind and place in a small bowl. Add remaining rub ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A04518C76625F54D904860A85E077A96', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', 1, NULL, NULL, 2, NULL, 0, 'Season pork shoulder all over with rub, wrap in plastic, and place in refrigerator for 12 to 15 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2B2C9DBEA17764B96C1676BD3B8C6E1', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', 2, NULL, NULL, 2, NULL, 0, 'Remove roast from the fridge and let meat stand at room temperature for 30 to 45 minutes before grilling. Season with kosher salt right before putting it onto the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C98B66551760349A13A0E4B2FFE5586', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', 3, NULL, NULL, 2, NULL, 0, 'While roast is resting, whisk ingredients for mop in a medium bowl. Set aside until ready to use.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12BF48B9FA42B64E9D90942534ABC5FA', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill and follow instructions for using the rotisserie. Secure roast on rotisserie rod and cook over direct low heat for 3 1/2 to 4 hours. The internal temperature should be at least 145 F/63 C when done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B84BEEB833882B4F84EA592C781461D1', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', X'2EE262B30B303A4F8D5F6D4B6CE99DB5', 5, NULL, NULL, 2, NULL, 0, 'After the first hour of cooking, apply mop every 20 minutes for the remainder of cook time. Remove roast from the rotisserie, cover with foil, and let stand 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9087FBEE50E8B44B4108D0A42E4612F', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 0, NULL, NULL, 2, NULL, 0, 'Gather the dry rub ingredients and ham.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7CE2B662F0CF46A4E275249C786C99', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 1, NULL, NULL, 2, NULL, 0, 'Make the Rub: The night before you smoke the ham, mix together the pepper, paprika, sugar, salt, dry mustard, and cayenne in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F5719DF6D32F44B9FDDA767D30B195D', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 2, NULL, NULL, 2, NULL, 0, 'Rub the spice mixture over the surface of the ham.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C01F2463AABF440AD2240328DE5BE9C', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 3, NULL, NULL, 2, NULL, 0, 'Wrap the ham in aluminum foil and let it sit in the refrigerator overnight to soak up all the flavors.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C25693183CBE40ACD89188A41ECE39', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 4, NULL, NULL, 2, NULL, 0, 'The next morning, remove the ham from the refrigerator and let it sit for 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D29FF6C10D43F4488DD967F3B76F42B', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 5, NULL, NULL, 2, NULL, 0, 'Gather the basting sauce ingredients and ham.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08C6B98B854B71429E0FE2E2F83F9E93', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 6, NULL, NULL, 2, NULL, 0, 'Prepare the smoker. You will be smoking at about 210 F/100 C for 5 to 6 hours, or until the ham reaches 140 F. (If you use a digital meat thermometer you can set the temperature to 140 F and you''ll be notified once the ham reaches the desired temperature.)');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C151621A2F8E84EBCD63E95E7A10B4B', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 7, NULL, NULL, 2, NULL, 0, 'Make the basting sauce: In a saucepan, mix together the chicken stock, pineapple juice, vegetable oil, dry mustard, and ground cloves. Warm over medium heat until completely mixed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B0AC3C49829D94F8D2B882DF1D78F1B', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 8, NULL, NULL, 2, NULL, 0, 'Score the ham with a sharp knife, scoring in a diamond pattern about 1/4-inch deep.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4B6B1A38DB5C447AAC5EC6320225EFE', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 9, NULL, NULL, 2, NULL, 0, 'Tent the scored ham with foil to prevent it from drying out, and place ham in the smoker.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50A3520F904AE4CA2E281D508A276A8', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 10, NULL, NULL, 2, NULL, 0, 'Baste the ham with the sauce once every hour, re-covering the ham with the foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7F842C76E513147A680994D008964F4', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 11, NULL, NULL, 2, NULL, 0, 'Gather the glaze ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E8BD2AE5BD1DE4C839A6733813D74B2', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 12, NULL, NULL, 2, NULL, 0, 'While the ham is smoking, prepare the glaze: Mix together the honey, pineapple juice, dry mustard, and a pinch of ground cloves.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D4765ABD7BE744A949F9897B2247BFB', X'03196ACDFA4D3346BF345E1092FCE942', X'03196ACDFA4D3346BF345E1092FCE942', 13, NULL, NULL, 2, NULL, 0, 'Brush the ham generously with the glaze a couple of times during the last hour of smoking, re-covering with the foil to prevent drying out.');
