INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C499857B1DC042A61DE98E459264BB', X'A14F80C733E87D419EC0770CB60E09A6', X'A14F80C733E87D419EC0770CB60E09A6', 2, NULL, NULL, 2, NULL, 0, 'Seal the container until you are ready to use the rub.');
