INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F72673CFC6F9304797454639B7EA4A94', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D74CC28B8E2F3D49B5A3D1A8EF40B8C5', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the chili powder, garlic powder, salt, pepper, onion powder, and ground mustard.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12D303A9617C674DB71B7D84D292508C', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 3, NULL, NULL, 2, NULL, 0, 'Place the pork in a roasting pan and rub all over with the spice mixture.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2C58996D323BD478559357FF1A1CD7D', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 4, NULL, NULL, 2, NULL, 0, 'Roast the pork for 1 hour. Remove from the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEF5E09C154BC6448E377B3B9AEF97BA', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 5, NULL, NULL, 2, NULL, 0, 'Reduce the oven temperature to 325 F. Pour apple juice over the roast and sprinkle the chopped garlic over the juice. Cover the roasting pan tightly with foil, return to the oven, and roast for about 2 to 2 1/2 hours, or until the pork is tender and easy to shred with a fork.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'102BDBA351B0504C8EE9E8919A968916', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, prepare the barbecue sauce. In a saucepan, combine the ketchup, cider vinegar, onion, Worcestershire sauce, brown sugar, mustard, and garlic powder. Bring to a boil, stirring constantly. Simmer, uncovered and stirring occasionally, for about 15 minutes, or until thickened.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40B8E2AEF5424546B642AE4ECB28B93E', X'8536FC7655136E4EA272FC90ACEF6A21', X'8536FC7655136E4EA272FC90ACEF6A21', 7, NULL, NULL, 2, NULL, 0, 'Remove the pork from the liquids, shred, and serve with the sauce.');
