INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D205A793FDF23D4586B0C21F0A1A6BE1', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 0, NULL, NULL, 2, NULL, 0, 'Combine marinade ingredients in a small bowl.');
