INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB85AC1780C44143A2FC52D6EAC41950', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 2, NULL, NULL, 2, NULL, 0, 'Pour marinade over chops, cover, and allow to marinate in the refrigerator for 1 to 3 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6138C8A6AD0DA443AEC9C8ACA4A9B8B9', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C293DBF27A0EC4FA21538CDEB58EEA5', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 4, NULL, NULL, 2, NULL, 0, 'Remove the chops from glass dish and discard marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FB257C15795FB45A8D56D59E924E326', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 5, NULL, NULL, 2, NULL, 0, 'Place pork chops on a lightly oiled grill rack and allow to cook for 20 to 25 minutes, turning once.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'730FDD05A55A88418EAFD2610364A931', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 6, NULL, NULL, 2, NULL, 0, 'Once internal temperature reaches 145 F/65 C, the until the pork chops are done.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C856CA17E722FC4FB3E4D5FF3FD2222F', X'F1A7D6D8A2532F4EB15B746750B9F870', X'F1A7D6D8A2532F4EB15B746750B9F870', 7, NULL, NULL, 2, NULL, 0, 'Remove from the heat, let the pork chops rest for 5 minutes and serve with your favorite sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27728B6DF5CF194EA43A37B382C16085', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4F5A88F0DD1224FBACD957B95D0447C', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 1, NULL, NULL, 2, NULL, 0, 'Prepare glaze first. Bring ketchup, brown sugar, maple syrup, and water to a simmer over medium heat. Stir often and watch for burning. Reduce heat if necessary. Add cider vinegar and salt. Stir through. Add bourbon and let the mixture simmer for 3-4 more minutes over medium-low heat. Once all ingredients are well combined and sugar has melted through, remove glaze from heat and let cool.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C524BDC0E58121418C9BD68F242CC312', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium heat with indirect cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9F4F1CAB5601F4B97FB099EEB4D7450', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 3, NULL, NULL, 2, NULL, 0, 'Cut off any straggling pieces of fat or meat from the spare ribs. Using a paper towel, grasp membrane on the back of the ribs and pull away from the bone.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88224232D5D0404C8DCB7D738CFCC7C5', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 4, NULL, NULL, 2, NULL, 0, 'Blot the rack of ribs dry with paper towels. Combine seasoning rub and apply all over ribs, front and back.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00668A9A182643409C1349AF93A8DAB1', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 5, NULL, NULL, 2, NULL, 0, 'Place ribs onto the grill and cook for 2 to 3 hours or until the internal temperature of the meat reaches at least 185 F. Adjust heat and placement of ribs on the grill as needed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60094F68FF3FE54DBEAF5AC0C0ED05A0', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 6, NULL, NULL, 2, NULL, 0, 'Warm the glaze in the microwave or stove top. There is no need to bring it back to a simmer, but warm it enough so that it doesn''t reduce the temperature of the ribs on the grill once applied.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88164E9A3B69AD4A8CFCD9C2C9A28D46', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 7, NULL, NULL, 2, NULL, 0, 'Begin brushing glaze onto ribs during the second half of grilling or when they reach an internal temperature of about 135 degrees. Repeat process several times during the remaining cook time. This will provide you with a nice caramelized candied coating on the ribs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F06BC4C715E92B4082F6A01D19C8223B', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 8, NULL, NULL, 2, NULL, 0, 'Once they have reached the appropriate temperature, remove ribs from grill and tent with foil. Let meat rest for 10 minutes before slicing. Slice between bones and serve with your favorite sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC53CB2BCDC77848A10B0B52D808E3DE', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 9, NULL, NULL, 2, NULL, 0, 'If you''d like a little extra glaze for the table, simply double the recipe and divide mixture in half. Save one-half for basting and other to serve with the cooked ribs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05F15CBB6489B24AAEC8B858DCD8623C', X'1DC0C6525CD34C4D8BF05D9946E97216', X'1DC0C6525CD34C4D8BF05D9946E97216', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA5AFDBD8BAC6146A4933246724C1CDA', X'65750E4BC076134F954FD0ACB3FEDD73', X'65750E4BC076134F954FD0ACB3FEDD73', 0, NULL, NULL, 2, NULL, 0, 'Place pork roast in the slow cooker insert; add chopped onion, salt and pepper and a generous sprinkling of garlic powder.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07E6D96D035CB64D8E98469BF0A4BC0F', X'65750E4BC076134F954FD0ACB3FEDD73', X'65750E4BC076134F954FD0ACB3FEDD73', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for about 9 to 11 hours, or until very tender and shreddable.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EAD93FC863E9B47AAA94CAB65B94C1B', X'65750E4BC076134F954FD0ACB3FEDD73', X'65750E4BC076134F954FD0ACB3FEDD73', 2, NULL, NULL, 2, NULL, 0, 'Drain all juices off and shred or chop pork; add vinegar and barbecue sauce (and liquid smoke, if using).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2500C6B9F89AF4E8ECA9C8F5174EC5E', X'65750E4BC076134F954FD0ACB3FEDD73', X'65750E4BC076134F954FD0ACB3FEDD73', 3, NULL, NULL, 2, NULL, 0, 'Continue cooking on high for about 1 hour. Serve with buns and tangy coleslaw for topping.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A829E3A413DE41A7994B555384CB15', X'65750E4BC076134F954FD0ACB3FEDD73', X'65750E4BC076134F954FD0ACB3FEDD73', 4, NULL, NULL, 2, NULL, 0, 'The recipe makes about 8 to 10 generous servings.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D786AC64C5FE0489034D588466416AE', X'D2FF183CC15E8047ADFA2F602DB76207', X'D2FF183CC15E8047ADFA2F602DB76207', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F. Line a 13 x 9 x 2-inch baking pan with aluminum foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1060BD33026F864393C00D800E240E89', X'D2FF183CC15E8047ADFA2F602DB76207', X'D2FF183CC15E8047ADFA2F602DB76207', 1, NULL, NULL, 2, NULL, 0, 'Arrange the pork chops in a single layer in the foil-lined pan and scatter the onion slices over the chops.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1243025DC32865408ABB37AEEAFFE9D9', X'D2FF183CC15E8047ADFA2F602DB76207', X'D2FF183CC15E8047ADFA2F602DB76207', 2, NULL, NULL, 2, NULL, 0, 'To make the barbecue sauce, whisk together all of the ingredients in a bowl until thoroughly combined. Pour the sauce over the pork chops.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D8202B29D6CB64A8CA9A133297A20AF', X'D2FF183CC15E8047ADFA2F602DB76207', X'D2FF183CC15E8047ADFA2F602DB76207', 3, NULL, NULL, 2, NULL, 0, 'Bake the pork chops, uncovered, for 1 hour, or until the meat is tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D57209DD3137C4DA7391DF8CB614C60', X'D2FF183CC15E8047ADFA2F602DB76207', X'D2FF183CC15E8047ADFA2F602DB76207', 4, NULL, NULL, 2, NULL, 0, 'Transfer the pork chops to a platter and spoon the sauce over them.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C31C1E00B7393438423FBE667C3CF66', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 0, NULL, NULL, 2, NULL, 0, 'In a saucepan, melt butter and add flour, stirring well to form a roux. Cook for about 1-2 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25465FC354ECD849B98543190179587F', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 1, NULL, NULL, 2, NULL, 0, 'Add milk 1/2 a cup/120 mL at a time while whisking mixture together.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A48327CE5DCB54F97CD04481C067318', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 2, NULL, NULL, 2, NULL, 0, 'Add cheese and all ingredients except, jalapenos. Whisk until thick and melted through. This process should take about 8-10 minutes total.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8D9648A4DC9734DA9FEB31AB7C21406', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 3, NULL, NULL, 2, NULL, 0, 'Once cheese sauce has reached a good thick consistency, add jalapenos and stir through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35314DA5A09E2A4A98D1E38B75A8D4DA', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 4, NULL, NULL, 2, NULL, 0, 'Keep cheese sauce on a warm setting until the hot dogs are cooked.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF0536B58EFF104FB69C002AA2A97300', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 5, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA85EB9919E6E84282C4CC449F5AA80F', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 6, NULL, NULL, 2, NULL, 0, 'Wrap each hot dog with a strip of bacon. You might need to cut bacon to fit.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44F0AFFED09D949B369F67A882D6EB9', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 7, NULL, NULL, 2, NULL, 0, 'Place hot dogs onto the grill and cook for 8 minutes, occasionally turning until the hot dog is cooked and bacon is good and crisp.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FF2B1AC8D675E40A3075E5816FB61FF', X'7E2C38517D78DC4B8027437BCEBD8C99', X'7E2C38517D78DC4B8027437BCEBD8C99', 8, NULL, NULL, 2, NULL, 0, 'Remove from heat, place into a warmed hot dog or deli bun, top with cheese sauce and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FDAE3F97E7C6742A2E6B335DD25B29B', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B145284A99DB8408F645F0B4E3364B5', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, combine rub ingredients into a paste. Coat pork shoulder with rub, cover with plastic wrap and refrigerate 1 hour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F5F226954FE6140BAE7046371813415', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 2, NULL, NULL, 2, NULL, 0, 'Preheat smoker and prepare for an 8-hour smoke. Make sure wood chips are soaked and drained before using them. Add charcoal and wood chips as necessary to keep the temperature low, or between 200 and 225 F/95 to 110 C. Smoke pork shoulder for about 8 hours or until internal temperatures reach 185 F/85 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90503A32794A2749805AE6DAFF604849', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 3, NULL, NULL, 2, NULL, 0, 'Once the pork is cooked, remove from the smoker. Wrap tightly in foil and allow to sit for an hour. While pork is resting, prepare finishing sauce. Blend together everything except the ketchup.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAEA5289D5EB644E93597FFCA4AE1317', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 4, NULL, NULL, 2, NULL, 0, 'Pour into a saucepan and add the ketchup. Simmer over low heat for 15 minutes. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF7E3EEAA6B444C9145F42CA1244A51', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 5, NULL, NULL, 2, NULL, 0, 'Unwrap pork butt and shred. Combine pork with 1/3 of the table sauce. Keep warm and serve on white bread buns with remaining sauce on the side. Top with coleslaw.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFE1BCA8483AA047B01C990DCEFBDB13', X'07B05DD648A59149B2A577EE4DA30599', X'07B05DD648A59149B2A577EE4DA30599', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C63C3EA52BF24BAB21F0EE5DA5B03A', X'CFA48014DE721C439A1C8F2B0531DCDC', X'CFA48014DE721C439A1C8F2B0531DCDC', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45925A19B3581649A8E797A1AC1D5B47', X'CFA48014DE721C439A1C8F2B0531DCDC', X'CFA48014DE721C439A1C8F2B0531DCDC', 1, NULL, NULL, 2, NULL, 0, 'In a large Dutch oven or kettle, heat olive oil over medium-high heat. Rub ribs all over with the seasoning blend then sear on all sides, about 2 minutes on each side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBA3CC9D7A4E954FA8C80EB3C9B86009', X'CFA48014DE721C439A1C8F2B0531DCDC', X'CFA48014DE721C439A1C8F2B0531DCDC', 2, NULL, NULL, 2, NULL, 0, 'Remove the ribs and set aside. Add bacon to the pot and cook for about 3 to 4 minutes, or until cooked but not crisp. Add the onions and cook, stirring, for 2 minutes. Add the ribs back to the pot and add apple juice, beef broth, and cider vinegar; bring to a simmer. cover tightly and bake for 2 hours. Add the cabbage, cover, and continue cooking, basting occasionally, for about 30 to 45 minutes longer, or until cabbage is tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DBEFE8C53F3424E9F116F86DCBC4FA6', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D9813018206E0438271CCAE1289E8C1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 1, NULL, NULL, 2, NULL, 0, 'In a small saucepan, heat the butter with the vinegar, water, brown sugar, garlic powder, onion powder, and black and cayenne peppers. Cook, stirring until butter has melted.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C70C111C31AF54DA2ACEBA5341CBE2E', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 2, NULL, NULL, 2, NULL, 0, 'Remove from the heat, let cool a bit, and then cover the sauce and refrigerate until it''s time to baste the ribs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E096BE6D24CF304F9216B211D17E90C2', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 3, NULL, NULL, 2, NULL, 0, 'Remove the silver skin from the back of the ribs. Use a kitchen towel or paper towel to grasp the edge of the skin and then pull. It should pull off in one piece.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D2456969773954A94D4E845C2196DDA', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 4, NULL, NULL, 2, NULL, 0, 'Cut the pork ribs into serving size pieces of one, two, or three ribs each. Place the spareribs on a rack in a roasting pan, meaty-side up.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'015849E1B704514C9F830CFDA1D0FF8B', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 5, NULL, NULL, 2, NULL, 0, 'Roast uncovered for about 1 1/2 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D96EACD8692BFC4582518314F312A72B', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 6, NULL, NULL, 2, NULL, 0, 'Brush with the sauce and continue roasting for about 45 minutes to 1 hour, turning and brushing with the sauce several times, until ribs are cooked through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9A3DE4246D3794FA505F009FB6B80C6', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', X'FD8E135B35D0C340A8DC82ABD8B6A9B1', 7, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E3E8A649497984CA9E12CF6E6E144DC', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73B446E7D27EBC4193C7F71DE541DD4B', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 1, NULL, NULL, 2, NULL, 0, 'Slice pork tenderloins crosswise into rounds about one inch thick. Lightly sprinkle both sides of the medallions with salt and pepper; press both sides into the flour.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56CBDC79218B8746B043EC9D87564926', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 2, NULL, NULL, 2, NULL, 0, 'Put enough olive oil in a large nonstick skillet or sauté pan to coat the bottom of the pan; heat over medium heat. Add the pork medallions and brown for about 2 minutes on each side. Remove the medallions to a plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A38A9B8A1B81E4CBF5BAF77621EC25D', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 3, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and green onions to the pan. Cook until mushrooms are just tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFF2F0BA1A1CD04E9026CEC3799C9B51', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, combine the glaze ingredients in a bowl; stir to blend thoroughly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74BFA4142E8AC14BAF9EE2C1D2346943', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 5, NULL, NULL, 2, NULL, 0, 'Put the pork tenderloin back in the skillet and pour glaze over all; stir to coat all pieces of pork.');
