INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06FA8678A5F905488F319BB8E9753452', X'F6F41165845F084181CA484390B86685', X'F6F41165845F084181CA484390B86685', 7, NULL, NULL, 2, NULL, 0, 'Serve pork with glaze. Enjoy!');
