INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AB1E5E98721C84D9DF6143D4A7B3109', X'24D2768F6FC12547B8FC5EF6CE45D824', X'24D2768F6FC12547B8FC5EF6CE45D824', 1, NULL, NULL, 2, NULL, 0, 'Arrange the ribs in the prepared pan and sprinkle all over with the kosher salt, freshly ground black pepper, and garlic powder. Let the ribs stand for about 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE8C119A918E54BA1E88D7B6B511DF9', X'24D2768F6FC12547B8FC5EF6CE45D824', X'24D2768F6FC12547B8FC5EF6CE45D824', 2, NULL, NULL, 2, NULL, 0, 'Bake for about 1 to 1 1/2 hours, or until the ribs are very tender.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'246CEA910BB3214A9CADD6C6E3C4BDFB', X'24D2768F6FC12547B8FC5EF6CE45D824', X'24D2768F6FC12547B8FC5EF6CE45D824', 3, NULL, NULL, 2, NULL, 0, 'In 1-quart saucepan, mix together remaining ingredients. Simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB215586C729B44C8A53767B0B22F86F', X'24D2768F6FC12547B8FC5EF6CE45D824', X'24D2768F6FC12547B8FC5EF6CE45D824', 4, NULL, NULL, 2, NULL, 0, 'Prepare the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A70CE7137C5F44DB5EE96BC9786C62C', X'24D2768F6FC12547B8FC5EF6CE45D824', X'24D2768F6FC12547B8FC5EF6CE45D824', 5, NULL, NULL, 2, NULL, 0, 'Remove the ribs from the baking pan and brush with sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAC3B1D532827241931F6E5E94356857', X'24D2768F6FC12547B8FC5EF6CE45D824', X'24D2768F6FC12547B8FC5EF6CE45D824', 6, NULL, NULL, 2, NULL, 0, 'Grill over direct heat, basting and turning frequently, for about 15 minutes, or until browned.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F6B5C1096A91442800E92A4B7442B4C', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5C037510432D94FA606748276160457', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 1, NULL, NULL, 2, NULL, 0, 'Fire up the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20825A203DCA9F4F9D4A4B5788680F0A', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix the softened cream cheese and shredded colby jack together. Transfer to a pastry bag fitted with a round tip. If you don''t have a pastry bag, use a zip-lock freezer bag, then use scissors to cut away one of the corners.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'433FCE15C59AEC4DA40AAF34A6F5179F', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 3, NULL, NULL, 2, NULL, 0, 'Cut off the stem end of the jalapenos. Use a small, thin knife - such as a grapefruit or steak knife - to remove the seeds and soft white membranes inside the peppers, leaving a hollow cone, open at one end. Take care not to split the peppers.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAFAFF83347A8C43B046F6FB8F8CEC91', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 4, NULL, NULL, 2, NULL, 0, 'Pipe the cheese mixture into the peppers.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74B556381BD197439022DE32B5D78204', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 5, NULL, NULL, 2, NULL, 0, 'Divide the sausage into 12 4-ounce portions. Flatten each of the portions into a thin oval, then wrap the sausage around the stuffed peppers, encasing them completely. If your jalapenos vary in size and shape you may have to make some adjustments in order to fully wrap them.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'995EA5C92436F34F990A9B48C29863C9', X'438C4578045218468A053B9CFABDB2EE', X'438C4578045218468A053B9CFABDB2EE', 6, NULL, NULL, 2, NULL, 0, 'Sear the armadillo eggs on the grill with direct heat for about 2 minutes on each side, then reduce the heat and move them to a spot with indirect heat, if possible, and close the grill. Cook for about 15 minutes, or until the cheese begins to escape, bubbling through its sausage enclosure. Remove from the grill and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDD40E1453CA4E43A1EF8ACD416A18D2', X'9E98BE256A4D044CB9F4E3D3E937A060', X'9E98BE256A4D044CB9F4E3D3E937A060', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1434E82882DE642B0E4DFE328A193F2', X'9E98BE256A4D044CB9F4E3D3E937A060', X'9E98BE256A4D044CB9F4E3D3E937A060', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients. Store in an airtight container.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FADFB97F8A44C04A8493A0A12BF55F04', X'9E98BE256A4D044CB9F4E3D3E937A060', X'9E98BE256A4D044CB9F4E3D3E937A060', 2, NULL, NULL, 2, NULL, 0, 'Apply to ribs while they are smoking in small amounts.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95CAC835D2352E4F821F2A5F59D5F053', X'81275DA840C85B49B0B1138D93E8DC2B', X'81275DA840C85B49B0B1138D93E8DC2B', 0, NULL, NULL, 2, NULL, 0, 'Cut pork tenderloin into 6 to 8 1-inch round pieces. Place pork into a resealable plastic bag. Add sprigs of thyme and rosemary. Combine remaining ingredients in a small bowl. Pour mixture over pork, seal bag, and allow to marinate in the refrigerator for 4 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'530EF4A967E86D4FB03A96AA869100E5', X'81275DA840C85B49B0B1138D93E8DC2B', X'81275DA840C85B49B0B1138D93E8DC2B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium heat. Remove pork from bag and place onto grill. Reserve marinade. Brush with marinade and cook for 7 minutes. Turn pork and cook for additional 7 minutes. When the meat reaches 140 degrees F/60 degrees C, remove from grill and serve. Discard any remaining marinade.');
