INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2F0C066D0B07A408080396C38BE4E8E', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 1, NULL, NULL, 2, NULL, 0, 'While technically optional, the first step for the best tasting grilled steak is to salt it. Put the salt in a small bowl or ramekin. If using the pepper and/or cayenne, add them to the salt and mix.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2276DA2F276CE94D8BCDA714560A5922', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 2, NULL, NULL, 2, NULL, 0, 'Liberally sprinkle the skirt steak with sea salt or kosher salt and let it sit for at least 30 minutes at room temperature or up to overnight in the fridge. In any case, bring the meat to about room temperature, or at least let it sit out to take the chill off before cooking it to help ensure even cooking.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E931681F7C5D74194CAF815D167D82B', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 3, NULL, NULL, 2, NULL, 0, 'While the meat sits, make sure the cooking grate on your charcoal or gas grill is cleaned and well-oiled, then heat the grill to high heat. You should be able to hold your hand about an inch over the grill grate for 1 second before it feels too hot. If using a charcoal grill, build the fire to one side so you''ll also have a cooler area where you can move the steak(s) if it''s seared and browned on the outside but not quite ready to take off the grill completely (if you''re using a gas grill you''ll be able to turn off a burner if need be).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63ABB32AAB993E4EB81C10A1E883292E', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 4, NULL, NULL, 2, NULL, 0, 'Set the steak(s) on the grill and cook, without moving, until that side is seared and releases from the grill of its own accord, about 3 minutes for medium-rare, 4 minutes for medium.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E10E6779BA57249BB9063EE7024A796', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 5, NULL, NULL, 2, NULL, 0, 'Turn and cook on the other side to the same effect, about 3 to 4 more minutes. You can use a meat thermometer to get an accurate temperature if you like. For rare, remove the steak(s) at 120 F to 125 F; medium-rare 125 F to 130 F; and medium 130 F to 135 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD56657AD522C1498E1E5801534377FC', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 6, NULL, NULL, 2, NULL, 0, 'Remove the steak(s) from the grill, cover loosely with foil, and let sit at least 10 minutes. This resting time allows the meat to finish cooking and the juices to redistribute evenly throughout the steak. It is extremely important to achieving a properly cooked steak when everything is said and done, so despite the temptation, don''t skip this step!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F6882671691AC42A9DB02436B229FF4', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 7, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to slice the steak against the grain and serve hot, warm, or room temperature. Skirt steak is particularly flavorful, so any cold leftovers are more than usually delicious.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5335BD32D3EB24A9880A4475302C1D4', X'2C8795A6A0EDA241A5165D272005CDF4', X'2C8795A6A0EDA241A5165D272005CDF4', 8, NULL, NULL, 2, NULL, 0, 'Perfectly grilled steak is perfectly tasty with just salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C95770E758A5284B8B19536074D790AD', X'4BDF8A6E7D659B4B80AE2597F614B4FD', X'4BDF8A6E7D659B4B80AE2597F614B4FD', 0, NULL, NULL, 2, NULL, 0, 'Heat the grill to medium.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE38A71CF250EB4FB48A4070EE389713', X'4BDF8A6E7D659B4B80AE2597F614B4FD', X'4BDF8A6E7D659B4B80AE2597F614B4FD', 1, NULL, NULL, 2, NULL, 0, 'Put all the ingredients in a large mixing bowl. If you are using bread slices, wet them and squeeze out the water before adding them to the bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36920D0158D9EE42AD08179870772DEE', X'4BDF8A6E7D659B4B80AE2597F614B4FD', X'4BDF8A6E7D659B4B80AE2597F614B4FD', 2, NULL, NULL, 2, NULL, 0, 'Briskly knead together all the ingredients for several minutes until well-blended. Set aside to rest for a few minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25A598767FC4F64F99C13D2AF5FF5C46', X'4BDF8A6E7D659B4B80AE2597F614B4FD', X'4BDF8A6E7D659B4B80AE2597F614B4FD', 3, NULL, NULL, 2, NULL, 0, 'Break off an apricot-sized piece of the meat mixture and form the desired shape with your hands. Flattened meatballs or patties cook up the best.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2D157DC51EA0B4C8E21E031CC515439', X'4BDF8A6E7D659B4B80AE2597F614B4FD', X'4BDF8A6E7D659B4B80AE2597F614B4FD', 4, NULL, NULL, 2, NULL, 0, 'Place each patty on the grill. Grill patties, turning once until browned on both sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A217FD807B3FCF4FABFEF184995D83A2', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'697EF2A1CE51D7458B7048CDF1753CB9', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 1, NULL, NULL, 2, NULL, 0, 'Trim excess fat from beef ribs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91359C71357EF747BD3923FE05533FAC', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 2, NULL, NULL, 2, NULL, 0, 'Combine salt, pepper, and cayenne.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61A0D0154F5DA8468FD0B773B07D60B1', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 3, NULL, NULL, 2, NULL, 0, 'Rub over the ribs.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4BB8087732624A9B4D836B6AE33792', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 4, NULL, NULL, 2, NULL, 0, 'Preheat grill and prepare for indirect grilling with a drip pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF8F9B6DB0D09041941CD3C8FBA00BB9', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 5, NULL, NULL, 2, NULL, 0, 'Place ribs on grill over drip pan and close the lid.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6EFE37F967D4C4CB8A1B6FA782BD579', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 6, NULL, NULL, 2, NULL, 0, 'Cook for about 3 to 3 1/2 hours over a medium low, indirect heat (try to keep the temperature under 265 F/130 C).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7501D8F131A9BA4889CC55945A793B75', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 7, NULL, NULL, 2, NULL, 0, 'To prepare sauce combine onion, honey, ketchup, chili peppers, garlic, and mustard in a saucepan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32C79E6D6D641140B1B219A397A90193', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 8, NULL, NULL, 2, NULL, 0, 'Heat over a medium-low temperature, stirring until even and warmed through.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'959EC9F4EDA1AD468A75DFD088ACE0A9', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 9, NULL, NULL, 2, NULL, 0, 'When the ribs are nearly finished cooking, begin brushing the sauce on them until you get a thick coating.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7D8B75EFDF94B43BDC77FF75AE587D7', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 10, NULL, NULL, 2, NULL, 0, 'Once cooked through, remove from heat and let stand for 5 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E903BACBD63EE248AA6D6F93EDCF723A', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 11, NULL, NULL, 2, NULL, 0, 'Cut into individual ribs and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72033CF54DA2C048BB126AF2E3C52FA2', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 12, NULL, NULL, 2, NULL, 0, 'Make a little extra sauce to serve on the side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1DAE89DAE65842850184C5F22CE367', X'68039E350C2B5C4182F21C95CCCA1DC5', X'68039E350C2B5C4182F21C95CCCA1DC5', 13, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF855FCE2A50043B96E3DFDBA14608B', X'8A66B709EBD7E6418396A68096057080', X'8A66B709EBD7E6418396A68096057080', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk together paprika, salt, garlic powder, onion powder, black pepper, dried parsley, cayenne pepper, cumin, coriander, hot chili powder, oregano, and if desired, the brown sugar until thoroughly mixed.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'435075C6EB8A8542B64ED603FBFDA0ED', X'8A66B709EBD7E6418396A68096057080', X'8A66B709EBD7E6418396A68096057080', 1, NULL, NULL, 2, NULL, 0, 'Pat the brisket dry on all sides using paper towels. With a spoon, sprinkle the rub liberally onto the meat. Simultaneously press it in and rub it with your fingertips until it adheres to the entire surface. Turn the meat and repeat on all sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78F1181E0285944888B1E045E45723E9', X'8A66B709EBD7E6418396A68096057080', X'8A66B709EBD7E6418396A68096057080', 2, NULL, NULL, 2, NULL, 0, 'Let the brisket sit for up to 24 hours (in the refrigerator) or cook immediately according to your recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65B7AE0486827641A69436961DEDC77B', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18C5116F58F5CB4EA5FB5710C74AFB10', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 1, NULL, NULL, 2, NULL, 0, 'Prepare grill for indirect grilling by placing a large dripping pan underneath the grate, directly below where the roast will be placed. Make sure you know the weight of your prime rib to help you figure the cooking time and ingredient amounts. Plan on grilling over indirect heat for 12 to 14 minutes per pound. If you are using a smaller roast, adjust the ingredients and cooking times accordingly.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E188752FF5FB464784E9B7DC388790DF', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 2, NULL, NULL, 2, NULL, 0, 'Aim for a grill temperature of about 375 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0153D9A13EF80340BE2F66822F06F854', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 3, NULL, NULL, 2, NULL, 0, 'Periodically add water to the pan to keep the drippings from burning away, especially if you want to make gravy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3887776F643A847A98F19488D86B30F', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 4, NULL, NULL, 2, NULL, 0, 'With a sharp paring knife, make a series of 1/2-inch deep holes spaced every 2 inches all over the roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52258848D35670449AB5BE5416F5570A', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 5, NULL, NULL, 2, NULL, 0, 'Insert the garlic slivers in every other hole and a leaf from the rosemary in the rest.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84C65E692E9C2A469B440492A448DD74', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 6, NULL, NULL, 2, NULL, 0, 'Combine black pepper, dried rosemary, salt, and paprika, and rub the mixture over the surface of the meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD2B51E886B4594D9E936DEF864B8576', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 7, NULL, NULL, 2, NULL, 0, 'When the grill is ready, place the roast on the well-oiled grate directly over the dripping pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03ADCEE6DE8EA3429B530B7FA986FFEC', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 8, NULL, NULL, 2, NULL, 0, 'Cook for about 12 to 14 minutes per pound. An 18-pound roast will take about 3 1/2 to 4 hours. It is very important to use an accurate meat thermometer to check for doneness. You do not want to overcook this roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E7B6FBFEF0D2E439EC3C4EA94145958', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 9, NULL, NULL, 2, NULL, 0, 'When the meat reaches an internal temperature of 115 F to 125 F, remove it from the grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'207AB0465AD84E48B46FBF0173C8B179', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 10, NULL, NULL, 2, NULL, 0, 'Place the roast on a platter and cover loosely with foil. The meat will continue cooking, and the internal temperature will continue to rise. Tent loosely with aluminum foil and let rest for at least 15 minutes but no more than 30 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4D3035D44907845A8F89E6B4C43FC98', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 11, NULL, NULL, 2, NULL, 0, 'Once the roast has rested, slice it.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C104BA8908AE74D832845ACDE466025', X'98B454FC650BC149B58ED5A5346E7BFC', X'98B454FC650BC149B58ED5A5346E7BFC', 12, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sides and enjoy.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEB304B552DCD146B30F160D045ABFDD', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8506D17F6290924CBA36D1D004D3ED25', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 1, NULL, NULL, 2, NULL, 0, 'Place prime rib roast onto a large cutting board. Combine seasoning ingredients and spread over roast. Make sure it is well coated.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0F1BC727BE9F41BE3096BA5E110453', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 2, NULL, NULL, 2, NULL, 0, 'Prepare grill for rotisserie cooking following the instructions in your grill manual.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63726560C9E08344BC5DBD077E11E815', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 3, NULL, NULL, 2, NULL, 0, 'Place the rib roast bone-side down. If you are using a boneless rib roast just place it on its side. Lift the roast from the ends and rotate it to find its approximate center.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA0B8EBA763F2D409E6CC217A2ADF900', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 4, NULL, NULL, 2, NULL, 0, 'Using a long, thin blade knife, cut through the center end to end.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5DDECE175BA24ABF3FE26DAD94C193', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 5, NULL, NULL, 2, NULL, 0, 'Run the rotisserie skewer through the hole and loosely secure with the rotisserie forks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFA625262DB7BB4BB822E00DD5FDF226', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 6, NULL, NULL, 2, NULL, 0, 'Place roast on grill and adjust to center; then tighten the forks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9F1A06518F89D4581847A01D4EA2091', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 7, NULL, NULL, 2, NULL, 0, 'Remove from roast grill in order to preheat grill to a high temperature.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E27CF508345C94F89B2F7CDE6509EEF', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 8, NULL, NULL, 2, NULL, 0, 'Place an aluminum drip pan under the space where the roast will sit. Add about 1 cup of water to the drip pan, if you intend to make gravy with the rendered juices.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B80B9CE11B507B4AA6818CE913A20FFF', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 9, NULL, NULL, 2, NULL, 0, 'Using heat-resistant gloves, return roast to grill once it has pre-heated.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C19948FE78E2364C83028F441E50D39F', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 10, NULL, NULL, 2, NULL, 0, 'Leave the grill on high for 20 minutes, then reduce the temperature to medium-low. Continue cooking until the internal temperature reaches at least 120 F (140 F for medium done), about 3 to 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3B04F275CB1EF4C91D9E97CE582C437', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 11, NULL, NULL, 2, NULL, 0, 'Remove from grill and rotisserie rod, cover with aluminum foil, and allow to rest for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7630B0437CA6F54C9CA28C6F756BD238', X'2ED857616F34084EA06001BF2D674D6A', X'2ED857616F34084EA06001BF2D674D6A', 12, NULL, NULL, 2, NULL, 0, 'Carve and place onto a platter.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E544CD7B70BE0A47B7AFA2009F32AAC8', X'38114890030D47418F29B51C151EBF43', X'38114890030D47418F29B51C151EBF43', 0, NULL, NULL, 2, NULL, 0, 'Divide ground beef into three equal parts. Spread each part of the ground beef out on a piece of plastic wrap, spreading into a thin, large square. The meat should be about a third of an inch thick. Spread about one-third of the macaroni and cheese. This might be too much so adjust as necessary.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36803C532D97814F9A03B05CC7C4E3AD', X'38114890030D47418F29B51C151EBF43', X'38114890030D47418F29B51C151EBF43', 1, NULL, NULL, 2, NULL, 0, 'Using the plastic wrap, roll the whole packed into a large, hot dog shape, making sure to keep the macaroni inside. Seal the edges of the meat to make a stuffed burger dog. Continue with remaining ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9077F6F25A297F4892B27F9CB7137640', X'38114890030D47418F29B51C151EBF43', X'38114890030D47418F29B51C151EBF43', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill to a medium-high heat. Gently place burger dogs over direct heat and grill for about 15 minutes, rolling to cook evenly. Remove from grill, place on hoagie buns and top with melted cheese sauce and jalapenos. Omit jalapenos if preferred.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11A134A685BDFD4EAA8E358E84A0FEE8', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A1C12E00E8B7F4CB81972C23F61DF59', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 1, NULL, NULL, 2, NULL, 0, 'Place the steak or steaks into a resealable plastic bag and pour the Italian dressing over the steaks, making sure meat is well coated. Release the air from the bag and seal. Place the bag into the refrigerator for 4 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8A739CE93464B42B4EA9FD7AABD1A6D', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 2, NULL, NULL, 2, NULL, 0, 'Remove the bag containing steaks at least 30 minutes before grilling. Let it stand at room temperature during this time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DE904A0D6443548B562B9898F3F01CB', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 3, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D326B1E05FC7A4F9758085C8581AAD3', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 4, NULL, NULL, 2, NULL, 0, 'Remove the steaks from the bag and discard the marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C382E78C3ECA5343B5453090CA8A321D', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 5, NULL, NULL, 2, NULL, 0, 'Season the steaks with salt, lemon pepper, and black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D5671232F6E4945B7BA71DC3C5BBD4D', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 6, NULL, NULL, 2, NULL, 0, 'Place the steaks onto the grill and cook for 6 to 7 minutes per side, depending on thickness and desired doneness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9584CB4ACBE01842BA259B4CAC384AF2', X'31875D0A5AB60B46BD6FB835CAC9645A', X'31875D0A5AB60B46BD6FB835CAC9645A', 7, NULL, NULL, 2, NULL, 0, 'Once the steaks have reached the preferred doneness, remove them from the heat onto a warmed plate and let the meat rest for 5 to 7 minutes before serving. This allows the juices to be evenly distributed. They will run out if you cut the meat too soon. Serve with your choice of sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2293714DBA4B444DA4C7B6F18C972F8D', X'647801BE82E8D94CA272EEAEF2E59A4A', X'647801BE82E8D94CA272EEAEF2E59A4A', 0, NULL, NULL, 2, NULL, 0, 'Remove prime rib roast from the refrigerator and let stand at room temperature for 1 hour. Meanwhile combine garlic, salt, pepper, oregano, and rosemary together and rub over the surface of the roast. Place prime rib fat side up in a smoker-safe roasting pan.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BE041607942794489B78D0E4CF71128', X'647801BE82E8D94CA272EEAEF2E59A4A', X'647801BE82E8D94CA272EEAEF2E59A4A', 1, NULL, NULL, 2, NULL, 0, 'Prepare smoker for a 4- to 6-hour smoke at a temperature of about 225 F/110 C. When the smoker is up to temperature, put the roasting pan with the prime rib on the top rack and add several water-soaked chunks of wood. A mixture of apple and mesquite work well for this recipe.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0808185EBD429D44952FCEE61425A5E0', X'647801BE82E8D94CA272EEAEF2E59A4A', X'647801BE82E8D94CA272EEAEF2E59A4A', 2, NULL, NULL, 2, NULL, 0, 'About halfway through smoking, add chopped vegetables to the roasting pan with the prime rib and throw some more wood chunks on the fire.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC96E51DF0B994489FD6D77A2A457CA1', X'647801BE82E8D94CA272EEAEF2E59A4A', X'647801BE82E8D94CA272EEAEF2E59A4A', 3, NULL, NULL, 2, NULL, 0, 'When done, remove the roasting pan from the smoker. Place prime rib roast on a carving board to rest for about 10 minutes. Place vegetables on a serving dish and keep warm.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89C3C3D35822BE4995E9461E2DFBBB77', X'647801BE82E8D94CA272EEAEF2E59A4A', X'647801BE82E8D94CA272EEAEF2E59A4A', 4, NULL, NULL, 2, NULL, 0, 'Place the roasting pan on the stove over medium heat. Add red wine and water to deglaze the pan. Stir well and loosen any pieces of meat and vegetables. Add Worcestershire sauce, salt, and pepper. Heat for about 2 to 3 minutes. Carve prime rib roast and serve with vegetables and au jus.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3521536C8AC5C445B54C5DD8635CD8D0', X'41B942DCE5DDC642A62F28B17A12BB2C', X'41B942DCE5DDC642A62F28B17A12BB2C', 0, NULL, NULL, 2, NULL, 0, 'Combine coriander seeds, peppercorns and mustard seeds in a spice grinder. Grind coarsely.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2B6317F5C34D34B8118838AE07A1DC6', X'41B942DCE5DDC642A62F28B17A12BB2C', X'41B942DCE5DDC642A62F28B17A12BB2C', 1, NULL, NULL, 2, NULL, 0, 'Add in remaining ingredients and mix well.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F795C39A3B4B4C915A851E804C0CD2', X'C380142CA247584F94015774BB507466', X'C380142CA247584F94015774BB507466', 0, NULL, NULL, 2, NULL, 0, 'Preheat smoker and add wood chips of your choice. Clean and dry the brisket. Combine rub ingredients and apply onto brisket.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53679ED2AA39564D82E73E30E7B3C108', X'C380142CA247584F94015774BB507466', X'C380142CA247584F94015774BB507466', 1, NULL, NULL, 2, NULL, 0, 'Place brisket in the smoker for 8 to 10 hours. Keep the smoker temperature at about 225 F/110 C. To increase tenderness, take the brisket out of the smoker, wrap in aluminum foil and place in the oven at 180 to 200 F/80 to 95 C for the last two hours. Typically after 8 to 10 hours a piece of meat will have absorbed as much smoke as it can and additional smoking may impart a bitter flavor.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7A50C5444C8074F8CECD7528AD9D98E', X'C380142CA247584F94015774BB507466', X'C380142CA247584F94015774BB507466', 2, NULL, NULL, 2, NULL, 0, 'Take brisket off of heat and allow to sit for 20 minutes before slicing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'960525FF5D1B984CB9D202FC83F9033A', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 0, NULL, NULL, 2, NULL, 0, 'Put the pot roast in the slow cooker with 1/2 cup water.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62598C05766EF042BC6905C327184DBE', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 1, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 10 to 12 hours, until very tender, or HIGH for about 5 to 6 hours).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E92913C1CF5EDE4084AFD4FA0C2A98F5', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 2, NULL, NULL, 2, NULL, 0, 'Drain and discard the liquids.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37ECCDC1CA4B9D4794F202A8020843D4', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 3, NULL, NULL, 2, NULL, 0, 'Remove meat; shred with forks. Discard fat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C316954C1A43364BA1FD995A14EAC774', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 4, NULL, NULL, 2, NULL, 0, 'Put the shredded beef back in the slow cooker and stir in ketchup, cola, mustard, smoke flavoring, and Tabasco.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D924AABFC3FC54EAC4301EFED0CDFDE', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on HIGH for 1 hour longer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1DD475ED3FFD24F9CEBAB675875977E', X'432ACBA0220E794581C39DDF8B798BB0', X'432ACBA0220E794581C39DDF8B798BB0', 6, NULL, NULL, 2, NULL, 0, 'Serve on toasted or warmed split buns.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1FFC7ADEBB9E045829722473E5395F5', X'330D09426FA3C44C90B2B21D61E3C24C', X'330D09426FA3C44C90B2B21D61E3C24C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06D07CA0C79B67418CDA334CFAAC4F3A', X'330D09426FA3C44C90B2B21D61E3C24C', X'330D09426FA3C44C90B2B21D61E3C24C', 1, NULL, NULL, 2, NULL, 0, 'Trim the skirt steak of fat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E51A66D36191AE4582B6032ABDC62D39', X'330D09426FA3C44C90B2B21D61E3C24C', X'330D09426FA3C44C90B2B21D61E3C24C', 2, NULL, NULL, 2, NULL, 0, 'Mix marinade together then combine it with skirt steak in a resealable plastic bag or flat baking dish.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC975072AC5DDB4297017C118E4A0E99', X'330D09426FA3C44C90B2B21D61E3C24C', X'330D09426FA3C44C90B2B21D61E3C24C', 3, NULL, NULL, 2, NULL, 0, 'Make sure the skirt steak is well coated then let it sit overnight.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC35F75296ED4A42912A5990F7C0E797', X'330D09426FA3C44C90B2B21D61E3C24C', X'330D09426FA3C44C90B2B21D61E3C24C', 4, NULL, NULL, 2, NULL, 0, 'Prepare smoker to 200 to 220 F/95 to 105 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EF3BF086772A84DA365F4BE9A00E9B3', X'330D09426FA3C44C90B2B21D61E3C24C', X'330D09426FA3C44C90B2B21D61E3C24C', 5, NULL, NULL, 2, NULL, 0, 'Remove skirt steak from marinade and let sit for 30 minutes at room temperature.');
