INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD1D7A14A59F704AAD929BEC4AF7DC37', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 1, NULL, NULL, 2, NULL, 0, 'Place the brisket onto a large cutting board.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F3D84D3A66C8D41AE95098680F03EFE', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 2, NULL, NULL, 2, NULL, 0, 'Combine the brown sugar, Worcestershire sauce, minced garlic, mild chili powder, ground black pepper, yellow mustard, salt, paprika, and cumin in a small bowl.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D89CBA9AA0A9649B0FA89ACB1B17A9E', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 3, NULL, NULL, 2, NULL, 0, 'Rub the mixture over the surface of the brisket, making sure to get every square inch of the meat. If you are adding liquid smoke, sprinkle it over the meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBDFA92FC9BF994387F94EB1A78EC432', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 4, NULL, NULL, 2, NULL, 0, 'Wrap the seasoned brisket tightly with plastic wrap and refrigerate for 8 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E134C8C01851F54AB6AF864B395667F6', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 5, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 300 F / 150 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64F7449394466E41A5F268C9902A3EB7', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 6, NULL, NULL, 2, NULL, 0, 'Gently remove the brisket from plastic wrap and rewrap it in aluminum foil. Do not wrap the brisket too tightly—make sure to give it some room for steam.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B57D3DAF466BF42BA85F5FE2C71C48F', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 7, NULL, NULL, 2, NULL, 0, 'Place the brisket in a roasting pan on a roasting rack and poke a couple of holes in the foil on the top. This will help to release some steam as it cooks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F13520CB2720EB428B71762695280C84', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 8, NULL, NULL, 2, NULL, 0, 'Cook the brisket for 4 1/2 to 5 hours. It is done when the internal temperature reaches 185 F / 85 C.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CA154C4F04A8641AE91CB2B6CA4E397', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 9, NULL, NULL, 2, NULL, 0, 'Using heat-resistant gloves, remove the foil and let the meat rest for 5 to 10 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25D08AF8C1B451408E89211DA175BCB3', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 10, NULL, NULL, 2, NULL, 0, 'Slice the brisket across the grain. Serve it with your favorite side sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B089D5471710FD4AA03ABD3839F58051', X'76EEAE49E6EDB547875B34A8294026D2', X'76EEAE49E6EDB547875B34A8294026D2', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1633BDEEF4E8444E82674C4909F193AD', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FD9CBAE4E18AD498C3E516745AB1500', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30B4DF2EE65ECE47A3D992940A4CA595', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 2, NULL, NULL, 2, NULL, 0, 'Place a large double thickness of heavy-duty foil in a 9x13x2-inch pan—enough foil to cover and seal foil around the chuck roast.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF8EA6979AD474CB89D29DAB8D391D1', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 3, NULL, NULL, 2, NULL, 0, 'Place chuck roast on the foil in pan. Mix the cream of mushroom soup, onion soup mix and barbecue sauce (directions below) together. Pour over meat. Seal foil tightly, leaving about 1` clearance between meat and foil.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38CA3357E011E544BB3B048FA71D2815', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 4, NULL, NULL, 2, NULL, 0, 'Bake the chuck roast in the preheated oven for 2 1/2 to 3 hours, or until meat is very tender. Let chuck roast sit for 10 minutes before slicing.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B823B67ADA0BB4CBE7E130A055553D6', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 5, NULL, NULL, 2, NULL, 0, 'Serve with the pan drippings over noodles or serve in sandwiches.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD8EC452EEBABD458B17373305D78BC1', X'B858FC1F3F7EF942835F9E6A0A2E8858', X'B858FC1F3F7EF942835F9E6A0A2E8858', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D22589CE15DE4478B962C30D96AED2D', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 0, NULL, NULL, 2, NULL, 0, 'To prepare the basting sauce, combine all sauce ingredients in a saucepan (tomato sauce, barbecue sauce, cola, honey, brown sugar, apple cider vinegar, white pepper, and hot sauce.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D69CDB8D1706469C0D20672B5F0E26', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 1, NULL, NULL, 2, NULL, 0, 'Let mixture come to a boil and then reduce and allow to simmer for 5 minutes. If the sauce is too thick, add 2 to 3 tablespoons water. Remove the sauce from the heat and set it aside.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E47D956870007549A43CD4CC10233940', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 2, NULL, NULL, 2, NULL, 0, 'If you are using frozen ribs, they should be thawed. Remove the membrane from the underside of the ribs. Trim off any loose fat or meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46E10C3D818E75408C22DA079B674505', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 3, NULL, NULL, 2, NULL, 0, 'Place the ribs in a large pot with cold water. Bring the water to a boil. Parboil the ribs for 5 minutes and remove them onto a baking sheet. You might need to do the ribs in batches since they can be so large.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9468F23DBB3C164CBCEBE836CA77ACCE', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 4, NULL, NULL, 2, NULL, 0, 'Mix the brown sugar, salt, paprika, and white pepper in a small bowl. This is the rib rub.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D66D96246DE9448BD729635C1D9DC7', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 5, NULL, NULL, 2, NULL, 0, 'After ribs are parboiled, apply the rib rub to each side of the ribs. Don''t worry if some of the rub falls off. The right amount is the amount that sticks.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA68A2FC104E8F49B991BB793F15D2A2', X'AB821EFD44F11A4ABF07B01944C60D65', X'AB821EFD44F11A4ABF07B01944C60D65', 6, NULL, NULL, 2, NULL, 0, 'Preheat the grill. Place the beef ribs on the grill and cook over medium-low heat. Rewarm the basting sauce. Once the ribs have reached 165 F, begin basting. Turn and baste the ribs with the basting sauce every 10 minutes, until finished cooking, about 1 1/2 hours. Remove the ribs from the heat and serve immediately.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A0045930AFFC44E8D62788E2231534F', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85079EBE092CE8439D9B8BC7E3BDCF1E', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 1, NULL, NULL, 2, NULL, 0, 'In a blender combine garlic, parsley, cilantro and basil. Blend until fine.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36DFD1BCD6E2F6469814F760EBB97C9C', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 2, NULL, NULL, 2, NULL, 0, 'Add olive oil, rice wine vinegar, Tabasco, salt, and pepper. Blend until smooth.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29CE4943B325874E8AD5BDEDE9AAE4B9', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 3, NULL, NULL, 2, NULL, 0, 'Pour half the mixture into a resealable bag and add tri-tip.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B67317F40C68B43B05345038A07C6A8', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 4, NULL, NULL, 2, NULL, 0, 'Turn the tri-tip to coat and refrigerate for about 4 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65DA5CC0ECA3754B82EF8F9AA8DB83A6', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 5, NULL, NULL, 2, NULL, 0, 'Place the remaining half of the sauce in a container and refrigerate until you need it.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A2FEBAF32F09041A1E28242265912F0', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 6, NULL, NULL, 2, NULL, 0, 'Preheat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'958B1E7772F1414DBE58C7DA84428B66', X'4C5B119F6E27764AAC23713A6682E321', X'4C5B119F6E27764AAC23713A6682E321', 7, NULL, NULL, 2, NULL, 0, 'Remove tri-tip from bag and place on hot grill.');
