INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71D0C5541700134E948C1D99BF0A82D0', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 2, NULL, NULL, 2, NULL, 0, 'Score the chuck roast with a sharp knife making 6 or 7 large cuts all over one side of the meat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79D207B1C162C74CBC15643D39601388', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 3, NULL, NULL, 2, NULL, 0, 'Add the marinade to a plastic bag and then add the steak. Seal the bag and massage the steak inside the marinade. Place it on a plate to catch any drips or leaks and refrigerate for at least 6 hours, ideally overnight. You can even marinate up to 24 hours. Since the meat is so thick it will not break down easily.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18D3E709C9905346BAA5A98F5534AB5B', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 4, NULL, NULL, 2, NULL, 0, 'Prepare the grill. Charcoal works best for flavor and low and slow cooking but you can use a gas grill as well. Heat your bricks and then lay them on each side of the grill. Place a disposable pan in the center where there aren’t hot bricks to catch the drippings.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A440BC1BC00B634D9F441B3444B14713', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 5, NULL, NULL, 2, NULL, 0, 'Sear one side of the meat over the coals and then place it on the center of the grill, over the grill pan. If you’re using a gas grill, place it under a burner that is not on, with the other burners around it turned on to medium-high heat. You can also throw in a few wood smoke chips on top of the charcoal for a nice smokey flavor. Close the cover to the grill and cook for about 5 minutes on the first side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49BE7F15E8E8B44DA3933AA9A1013EFF', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 6, NULL, NULL, 2, NULL, 0, 'Flip and cook for an additional 20 minutes or until the internal temperature has reached 145 F which is the right temperature for “medium` (the best temperature for serving this cut of meat).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04F25EE4CB57A342AFC5AD38AEEE0453', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 7, NULL, NULL, 2, NULL, 0, 'Remove the steak from the grill and allow to rest for 2 to 3 minutes. Slice against the grain (do the best you can, chuck steaks grains can change across the meat due to the configuration of the muscle).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64C3D9DCF35AAB41AAF2FE365C8EF43C', X'D3618980EDDD50458B4D4C1A7A3CF36B', X'D3618980EDDD50458B4D4C1A7A3CF36B', 8, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sauce like chimichurri. Enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35327F6CAE0CB04C82823FB3E54CF144', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 0, NULL, NULL, 2, NULL, 0, 'In large shallow glass dish, combine wine, thyme, oil, garlic, salt and pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0461432394EF40A04A5C50119680EF', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 1, NULL, NULL, 2, NULL, 0, 'Add steak and turn to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FF19F8941A7CE46A58B7D034472C3D9', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 2, NULL, NULL, 2, NULL, 0, 'Cover with plastic wrap and marinate in refrigerator for 8 to 24 hours, turning steak occasionally to coat with marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA7D1D84C26EF044AA33E658864F16E0', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 3, NULL, NULL, 2, NULL, 0, 'Prepare and heat grill.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33386F8FEDA20643A9C1E53D08B5D4D9', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 4, NULL, NULL, 2, NULL, 0, 'Place steak on greased grill over hot coals.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F74E66F57B2BFE4F9C3526A912F9D9DE', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 5, NULL, NULL, 2, NULL, 0, 'Brush with marinade to coat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'952ABECCC3410641B9ACA44480091E80', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 6, NULL, NULL, 2, NULL, 0, 'Cover grill and cook steak for 7 to 8 minutes per side or until medium, or 145 F on a meat thermometer.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B36588DE64094599303F906BB39D91', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 7, NULL, NULL, 2, NULL, 0, 'Remove steaks from grill, cover, and let stand for 5 minutes before carving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2BA9A97A20C844E97D92CB0ABAE0404', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 8, NULL, NULL, 2, NULL, 0, 'Discard remaining marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA525835D79078478E55D5C30E2BDACE', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 9, NULL, NULL, 2, NULL, 0, 'Cut steak in half and refrigerate one of the halves until chilled; wrap and refrigerate for up to 2 days.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DE3E18A1811C34B98499D6378B9F62C', X'40128C4AC9E55349B25C1F0F12EEDC36', X'40128C4AC9E55349B25C1F0F12EEDC36', 10, NULL, NULL, 2, NULL, 0, 'Cut remaining steak into serving size pieces. You can also thinly slice remaining half across the grain on an angle and serve.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B89955980EDA14B85310626214AB195', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E634954ADAFA4ABA15699C0DEB2205', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 1, NULL, NULL, 2, NULL, 0, 'Place the steak or steaks into a resealable plastic bag and pour the Italian dressing over the steaks, making sure meat is well coated. Release the air from the bag and seal. Place the bag into the refrigerator for 4 to 12 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3EFD965D2DAFE4C9A79A1DBD251D3F6', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 2, NULL, NULL, 2, NULL, 0, 'Remove the bag containing steaks at least 30 minutes before grilling. Let it stand at room temperature during this time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11AFD81FFFB73C4EAA0DC3D6E8A89A1C', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 3, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70D0A20E7459A24080FA255F67137BFD', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 4, NULL, NULL, 2, NULL, 0, 'Remove the steaks from the bag and discard the marinade.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86CF3994B5676948B2B1BC36266AF364', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 5, NULL, NULL, 2, NULL, 0, 'Season the steaks with salt, lemon pepper, and black pepper.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBE5D5545A66B041AFAA848BFDEE2127', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 6, NULL, NULL, 2, NULL, 0, 'Place the steaks onto the grill and cook for 6 to 7 minutes per side, depending on thickness and desired doneness.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC331568BFC45140AB03EB211C72E8BB', X'88E9977D371CFB40AA0FA49BB10E409E', X'88E9977D371CFB40AA0FA49BB10E409E', 7, NULL, NULL, 2, NULL, 0, 'Once the steaks have reached the preferred doneness, remove them from the heat onto a warmed plate and let the meat rest for 5 to 7 minutes before serving. This allows the juices to be evenly distributed. They will run out if you cut the meat too soon. Serve with your choice of sides and enjoy!');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF49AC1A02FCC34D811EC2A1B7F6DD43', X'95AE4B8CC14B6A4FACBB25140E90A32B', X'95AE4B8CC14B6A4FACBB25140E90A32B', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 F.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0AC93510416E44390145A1238DD0CF8', X'95AE4B8CC14B6A4FACBB25140E90A32B', X'95AE4B8CC14B6A4FACBB25140E90A32B', 1, NULL, NULL, 2, NULL, 0, 'Preheat an ovenproof skillet, preferably cast-iron with raised grill ridges, over medium-high heat. Add olive oil and let heat for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47DC7399F8071445B7B7E668786E0BA4', X'95AE4B8CC14B6A4FACBB25140E90A32B', X'95AE4B8CC14B6A4FACBB25140E90A32B', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle steaks with salt and pepper on both sides.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39E09675B9A99B4CB0D4E40BFE9BDA1A', X'95AE4B8CC14B6A4FACBB25140E90A32B', X'95AE4B8CC14B6A4FACBB25140E90A32B', 3, NULL, NULL, 2, NULL, 0, 'Place in skillet and sear for 3 to 4 minutes on the first side until the steaks release easily from the pan and have a dark brown crust. Turn and sear for 3 to 4 minutes on the second side.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58BBE09037E9A34B94365331032DCC6D', X'95AE4B8CC14B6A4FACBB25140E90A32B', X'95AE4B8CC14B6A4FACBB25140E90A32B', 4, NULL, NULL, 2, NULL, 0, 'Place a pat of butter on top of each steak, and place the pan in the oven.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52F8A13B71F4474A8290E04D5E6B8904', X'95AE4B8CC14B6A4FACBB25140E90A32B', X'95AE4B8CC14B6A4FACBB25140E90A32B', 5, NULL, NULL, 2, NULL, 0, 'Roast for 8 to 13 minutes until the desired doneness, which should be at least 140 F for rare. Remove pan from oven, cover with foil, and let steaks stand for 10 minutes before serving.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'555983596AFD4F49826D6D54AEDF7423', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB17A38C57812446BA774AE4CADC6116', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 1, NULL, NULL, 2, NULL, 0, 'Heat a gas or charcoal grill to medium-high heat (you should be able to hold your hand about 1 inch above the cooking grate for 3 to 4 seconds before pulling it away from the intensity of the heat).');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57A898F9088F3444AD43319EC43DD700', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 2, NULL, NULL, 2, NULL, 0, 'While the grill heats, trim off and discard any wilted greens at the top of the spring onions. Cut off and discard the root end of the onions as well. Be careful to cut right at the bottom of the onion and the top of the roots so the bulb stays together and you can cook and serve the onions intact.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC138853686E014BA7881398AA8656F3', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 3, NULL, NULL, 2, NULL, 0, 'Brush the spring onions with olive oil or vegetable oil. Be liberal with the application of the oil — it prevents the onions from sticking to the hot grill and adds tons of flavor as it mixes with the juices from the onions.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E361356443C1C34AA1005C39704BAB8E', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 4, NULL, NULL, 2, NULL, 0, 'Put the spring onions on the preheated grill and cook them, turning once, until grill marks appear on both sides and the spring onions feel fork tender all the way through, about 15 minutes total cooking time.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B9F41D73573B4F9A927CB000056460', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle the grilled spring onions with salt and freshly ground black pepper to taste.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35F5D6F9B70CF34DB56ED9299FDFDC94', X'DF18A563B7BAF549B5B2EC478AC83C23', X'DF18A563B7BAF549B5B2EC478AC83C23', 6, NULL, NULL, 2, NULL, 0, 'Spritz the onions with fresh lemon juice or serve the lemon wedges on the side so everyone can dress their own plate.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25EBBB24966C5A49A558C44C88C12BCF', X'9830C539939A8C4F99EE890699C91834', X'9830C539939A8C4F99EE890699C91834', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91595A603138324FBDC8C2876D7E79BD', X'9830C539939A8C4F99EE890699C91834', X'9830C539939A8C4F99EE890699C91834', 1, NULL, NULL, 2, NULL, 0, 'Place the flap steaks into a resealable plastic bag.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'493E234B8BE48B45A0B38418952FCF82', X'9830C539939A8C4F99EE890699C91834', X'9830C539939A8C4F99EE890699C91834', 2, NULL, NULL, 2, NULL, 0, 'Combine marinade ingredients together and pour over the steaks, making sure all surfaces are well coated. Carefully let air out of the bag, seal, and place into refrigerator for 2 to 8 hours.');
INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D222228F7A6DE4C8D5117B9A5EAD045', X'9830C539939A8C4F99EE890699C91834', X'9830C539939A8C4F99EE890699C91834', 3, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat.');
