INSERT INTO `VWR INTERNAL AmericanRecipes3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD01E128757564990C50B306CFB6505', X'6F39F242B085DF45A2921DE9AFCAB4D9', X'6F39F242B085DF45A2921DE9AFCAB4D9', 0, NULL, NULL, 2, NULL, 0, 'In a medium skillet, toast cumin seeds for 2 to 3 minutes over medium heat. Make sure to move the pan around so that the seeds do not burn. Once the seeds have a deep brown (not charred) color and spice smells fragrant, remove from heat.');
